css:
@-moz-document domain(twitch.tv)
{
  #chat_line_list .line, .chat-messages .chat-line
  {
    background-color: #C8C8C8 !important;
    border-bottom: 1px solid #B4B4B4 !important;
  }
  #chat_line_list .line:nth-child(2n), .chat-messages .chat-line:nth-child(2n)
  {
    background-color: #D9D9D9 !important;
  }
  #chat_line_list .highlight, .chat-messages .chat-line.highlight
  {
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.8) inset !important;
    border-bottom: 1px solid rgba(180, 0, 0, 0.8) !important;
    background-color: #F97979 !important;
  }
  .chat-messages .chat-line.whisper
  {
    background-color: #E8D6FF !important;
  }
  .chat-messages .chat-line:not(.action) .message, .chat-messages .chat-line.highlight .from, .ember-chat .chat-header, #chat_text_input
  {
    color: #000 !important;
  }
  .chat_line a, .ember-chat .chat-line a
  {
    color: #005596 !important;
  }
  .chat-messages .chat-line .svg-whisper-arrow
  {
    fill: #32323E !important;
  }  
  
  .ember-chat .chat-header, .ember-chat .chat-interface
  {
    background: #F2F2F2!important;
  }
  #chat_text_input
  {
    border: 1px solid #BFBFBF !important;
    background-color: #FFF !important;
    
  }
  #chat_text_input:focus
  {
    border: 1px solid #000 !important;
    
  }
  .ember-chat .chat-interface .textarea-contain .emoticon-selector-toggle svg path
  {
    fill: #CCC !important;
  }
  .ember-chat .chat-interface .textarea-contain .emoticon-selector-toggle:hover svg path
  {
    fill: #7F7F7F !important;
  }
  .chat-buttons-container .button.glyph-only
  {
    background-color: transparent !important;
    border: none !important;
    box-shadow: none !important;
  }
}
