css:
@-moz-document domain("my.pocketsmith.com") {
  
  /* Adjust the below 140px values to suit desired calendar day height */ 
  body.calendars .fc .fc-body .fc-row {
    min-height:140px;
    height:140px!important;
  }
  
  /* the below minimises the calendar toolbar, while retaining controls */ 
  
  body.calendars div.fixed.toolbar {
    top:4.4rem;
    padding:0.3rem 1.6rem;
    z-index:10;
    width:70%;
    height:3.6rem;
  }
  body.calendars > .breadcrumb {
    height: 4rem;
  }
  
  body.calendars > .page-header.calendars {
    top:7rem;
    padding:0;
  }
  body.calendars > .page-header.calendars th {
    line-height:5rem;
  }
  
  body.calendars > .page-header.calendars ~ .content {
    margin-top:3rem
  }

}
