css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("omskinform.ru"), domain("eda.omskinform.ru") {
*
{
  font-family: arial !important;
}

.fon
{
  background-image: none !important;
}

.head_menu_div
{
  width: 700px !important;
}

TABLE [width="800"]
{
  width: 1000px !important;
}

TABLE [width="564"]
{
  width: 764px !important;
}

TD [width="164"]
{
  width: 350px !important;
}

.tn_time
{
  font-size: 12px;
  font-family: arial;
}

.n_date
{
  font-size: 14px;
  font-weight: bold;
  width: 200px;
}

.td_print
{
  color: black;
  font-size: 14px;
  width: 300px !important;
  padding-right: 50px;
}

.comments_cnt
{
  margin-top: 5px;
  color: orange !important;
  font-size: 18px !important;
  width: 200px !important;
  background: none;
  padding-left: 0px;
}

.comments_cnt:before
{
  content: "Комментарии:  ";
}

.n_img
{
  width: 100px !important;
  height: 100px !important;
}

.print
{
  font-size: 12px;
  margin-left: 50px;
}

DIV[id^='slide_text_']
{
  width: 300px !important;
}

.slide_text_holder
{
  width: 350px !important;
}

.cmm_message,
.cmm_menu_fon,
#comments,
.cmm_title,
.cmm_div,
.n_line
{
  width: 1000px !important;
}

.cmm_message_menu,
.cmm_message_text
{
  width: 930px !important;
}

.tags_list,
.n_cap_lnk,
.n_text
{
  width: 850px !important;
}

.tags_list *
{
  border-radius: 5px;
  padding: 2px;
  margin: 2px !important;
}


DIV[style="display: block; float: left; width: 721;"]
{
  width: 950px !important;
}

.n_fon,
.n_news
{
  width: 1000px !important;
}

.cmm_message_text_ans
{
  width: 865px !important;
}

DIV[style="display: block; float: left; margin-top: 10px; margin-bottom: 20px;"]
{
  margin-left: 150px !important;
}

a[target="_blank"],
td[width^="41%"] *,
td[width^="40%"] *,
td[width^="39%"] *,
/* td[valign="middle"], */
div[style="padding-top: 12px; position: relative; max-width: 3200px; margin: 0px auto;"],
td[style="width: 34%; text-align: center; vertical-align: top;"],
td[style="width: 33%; text-align: center; vertical-align: top;"],
div[style="max-width: 3200px; margin: 0px auto;"],
div[class^='blockOf'],
div[id^='yandex'],
div[id^='smi_'],
div[id^='LentaInform'],
img[style^=" width:"],
img[width^="59"],
img[width^="60"],
img[width^="79"],
img[width^="80"],
.print,
.under_top,
.foot_links_table,
td[width="530"],
col[width="389"],
td[width="389"],
table[width="369"],
table[style="margin-bottom: 50;"],
.pb_top_content_container,
#yandex_ad,
iframe,
.tags_cap,
.weather_emp_fon,
.topblocks,
.relnews_block,
.lenta-wrapper,
.go_to_comments,
#tags_plate,
.tags_social,
.right_col_top,
#foot_table,
.foot_sl,
TABLE [width="100%"][style="margin-bottom: 15;"],
.nmg_div,
.d_fon,
.th5_fon,
.th6_fon,
.th7_fon,
.th8_fon,
.th9_fon,
.th10_fon,
.bp_table,
.bp_table2,
.left_col_top,
.gray_foot,
.bplace_,
.foot_banners_div,
.left_banner,
.right_banner
{
  display: none !important;
}
}
