css:
@-moz-document url-prefix("https://startingpoints.kanbanize.com/ctrl_board") {
.task {
    width: 75%;
}
.task .task_tab {
    font-size: 11px;
}
.task .body {
    font-size: 12px;
}
.task .title {
    max-height: none;
    overflow: auto;
}
.task .assignee {
    text-align: left;
    font-weight: normal;
    font-size: 13px;
}
.board td.swimlane_inner {
    font-size: 1.5em;
    padding: 8px;
}
.column_name.inline {
    font-size: 13px;
}
.column_limit.inline {
    font-size: 13px;
}
tbody td div.limits {
    font-size: 12px;
}
.column_limit.inline span {
    font-weight: bold;
}
.board_card_list {
    font-size: 12px;
}
.queue_sectionname {
    text-align: center;
    border-top: 1px solid black;
    font-size: 1.25em;
}
}
