css:
@-moz-document regexp("https?://(www\\.)?torrentsm((d\\.(com|eu|me))|(oldova\\.(com|org|net))).*") {
textarea.markItUpEditor {
    border-color: #000000;
    color: #fafafa;
}
body, #user_box td {
    height: 100%;
    color: #999;
    background: url(http://i.imgur.com/w5mUfto.png);
    font: 12px/18px Helvetica, Arial, sans-serif;
    margin: 0;
}

#torrents > table > tbody > tr  {
    background-color:transparent !important;
}

table {
    padding: 25px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -ms-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
    background: rgba(255, 255, 255, 0.08);
}
input[type="text"],
input[type="password"],
input[type="search"],
textarea,
select {
    width: 100%;
    border: 1px solid #666;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -ms-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
    padding: 7px 9px;
    color: #858585;
    background: rgba(255, 255, 255, 0.08);
    font-size: 11px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
input[type="text"]:focus,
input[type="password"]:focus,
input[type="search"]:focus,
textarea:focus,
select:focus {
    border-color: #69aaba;
    outline: 1px solid #69aaba;
}
input[type="text"][disabled],
input[type="password"][disabled],
input[type="search"][disabled],
textarea[disabled],
select[disabled] {
    opacity: 0.6;
}
.mCenter,
#menutable a,
#tbl_menu > tbody > tr > td:nth-child(2) {
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    -ms-border-radius: 2px;
    -o-border-radius: 2px;
    border-radius: 2px;
    color: #ccc;
    background: rgba(0, 0, 0, 0) center center no-repeat;
    font-size: 10px;
    font-weight: 700;
    text-transform: uppercase;
    -webkit-transition: background-color 500ms;
    -moz-transition: background-color 500ms;
    -o-transition: background-color 500ms;
    transition: background-color 500ms;
    margin: 0 8px 0 0;
    padding: 5px 10px;
}
#menutable img {
    width: 0;
}
td,
#user_box {
    color: #ccc;
    vertical-align: auto;
    zoom: 1;
    font-size: 12px;
    line-height: 16px;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    -ms-border-radius: 2px;
    -o-border-radius: 2px;
    border-radius: 2px;
    border: 1px solid rgba(0, 0, 0, 0.28);
    background: rgba(0, 0, 0, 0.23);
    font-family: monospace;
    padding-bottom: 0;
    margin-top: 2px;
    margin-bottom: 2px;
    padding-top: 0;
    padding-left: 3px;
}
table,
table a,
#user_box a {
    color: #ccc;
}
tr td font {
    color: #8bd9ab;
}
tbody tr {
    background-color: rgba(255, 255, 255, 0.08);
}
span.lnk,
span.lnk.post_like,
span.lnk.post_unlike,
span.likes_number,
span.lnk.raportareLnk {
    color: #d1c2d1;
}
a {
    font-weight: bold;
    color: #ccc;
    text-decoration: none;
}
a:hover {
    text-shadow: 1px 0px 0px #000;
    text-decoration: none;
}
div#top_menu>table>tbody>tr>td:first-child>a {
    display: block;
    background: url("http://funkyimg.com/i/2vobS.png") no-repeat scroll 0 0 transparent;
    width: 280px;
    height: 76px;
}
#tmd_logo {
    display: none;
}
}
