@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.zippcast.com"), domain("support.zippcast.com"), domain("forum.zippcast.com") {
  
  body {
    word-wrap:break-word !important;
  }
  
  /* Potential notice - based on one instance, so may not be very effective */
    body > :nth-child(2)[style*='padding-bottom'] {
      padding-bottom:0 !important;
    }
  /* end potential notice */
  
  /* header */
    .toplinks {
      font-size:12px !important;
      font-family:Arial !important;
    }
  
    .toplinks a {
      display:inline-block !important;
      padding-left:3px !important;
      padding-right:3px !important;
    }
  
    .toplinks .dropdown-menu {
      width:435px !important;
    }
  
    .toplinks .dropdown-menu > div {
      width:auto !important;
    }
  
    .toplinks .dropdown-menu > div > div:first-child {
      padding-top:0 !important;
    }
  
    body > .bgwhite + .bgwhite > tbody > tr > td,
    body > table > tbody > tr > .bgwhite > .bgwhite + .bgwhite > tbody > tr > td {
      padding-left:0 !important;
      padding-right:0 !important;
    }

    #logo_color {
      padding-top:1px !important;
      padding-bottom:1px !important;
      height:52px !important;
    }

    /* .bgwhite is used to distinguish from a notice (such as latency issues due to high volumes of traffic) which confusingly/inappropriately includes elements matching #masthead-search-bar-container, #masthead-search-bar and/or .masthead-bar */
    .bgwhite #masthead-search-bar-container {
      margin-left:10px !important;
      font-family:Arial !important;
    }

    .bgwhite #masthead-search-bar-container span {
      display:inline-block !important;
      height:48px !important;
    }

    .bgwhite #masthead-search-bar {
      height:48px !important;
      width:inherit !important;
      line-height:48px !important;
    }

    #masthead-nav {
      padding:0 !important;
      font-size:16px !important;
    }

    #masthead-nav > span {
      display:inline-block !important;
      float:left !important;
    }

    #masthead-nav a {
      display:inline-block !important;
      height:51px !important;
      width:100px !important;
      text-align:center !important;
      padding:0 !important;
    }

    #masthead-search input {
      height:35px !important;
      margin:0 5px !important;
      padding-left:8px !important;
    }

    #masthead-search #search-btn {
      top:14px !important;
      right:14px !important;
      height:18px !important;
      width:20px !important;
      padding:0 !important;
    }
  
    #upload_menu, #upload_menu:visited {
      font-size:14px !important;
      height:48px !important;
      line-height:48px !important;
      padding:0 !important;
    }

    #upload_menu > #slinkdiv {
      height:51px !important;
      padding:0 16px !important;
    }

    /* search suggestions */
      body > ul.ui-autocomplete {
        font-size:14px !important;
      }
  
      body > ul.ui-autocomplete > li {
        padding-left:8px; /* overriden by aesthetics */
      }
  
      body > .ui-helper-hidden-accessible {
        /* accessibility-friendly visibility hiding */
        position:absolute;
        top:-9999px !important;
        left:-9999px !important;
      }
    /* end search suggestions */
  /* end header */
  
  a[href^='/user/'] {
    word-wrap:break-word !important;
  }
  
  body > br:first-of-type {
    display:none !important;
  }
  
  #pmaintbl > tbody > tr:nth-child(2) > td {
    display:none !important;
  }
  
  .cborder, .br2, td[class=''] > .br1.smart_break ~ .br1 > tbody > tr:first-child {
    cursor:inherit !important;
  }
  
  .icon-reorder {
    cursor:move !important;
  }
  
  .mh1 {
    font-size:18px !important;
  }
  
  .right_col #mod-mdvid .mh1,
  .right_col #mod-vidnow .mh1 {
    font-size:17px !important;
  }
  
  #bgshp2 {
    padding-bottom:3px !important;
  }
  
  .thumb, .thumbf, .thumbp,
  img[src*='files_thumbnail'] {
    padding:0 !important;
  }
  
  .thumb:hover, .thumbf:hover, .thumbp:hover,
  img[src*='files_thumbnail']:hover {
    padding:0 !important;
  }
  
  #mmenulisthp2 {
    padding:5px 10px 0 10px !important;
  }
  
  .left_col #mod-vidnow #mmenulisthp2 > div {
    padding-bottom:0 !important;
    height:auto !important;
  }
  
  .left_col #mod-vidnow #mmenulisthp2 > div:nth-child(2) > div {
    height:85px !important;
  }
  
  .left_col #mod-vidnow #mmenulisthp2 > div:nth-child(2) > div:last-child {
    height:75px !important;
  }
  
  .qlistadding2 ~ div {
    padding-top:4px !important;
  }
  
  #mod-mpvid a[href^='http://www.zippcast.com/categories/'] {
    font-size:14px !important;
  }
  
  .left_col #mod-mpvid #mmenulisthp2 > div:not(#floating_box_base),
  .left_col #mod-vidnow #mmenulisthp2 > div:not(#floating_box_base),
  .left_col #mod-featvid #recipient > div,
  .left_col #mod-mdvid #recipient > div {
    margin:0 0 10px 0 !important;
    width:50% !important;
  }
  
  #mod-mpvid #mmenulisthp2 > div:not(:last-child) > div:last-child,
  #mod-featvid #recipient > div > div:last-child {
    margin-left:10px !important;
  }
  
  #mod-mpvid #mmenulisthp2 > div > div:nth-child(2) {
    margin-top:3px !important;
  }
  
  .user_mods.right_col > .no_move > table.featvid_info {
    padding:2px 5px 5px 5px !important;
  }
  
  /* spacing from header on login page */
    body > .width950 > tbody > tr > .pr15[width='55%'] {
      padding-top:12px !important;
      padding-right:0 !important;
    }

    body > .width950 > tbody > tr > .pr15 + [width='45%'] {
      padding-top:5px !important;
    }
  /* end spacing from header on login page */
  
  /* login and password reset forms */
    .user_mods.right_col > .no_move > table:nth-of-type(3),
    body > .width950 td[class=''][width='50%'][valign='top'] table:first-child:nth-last-child(2),
    body > .width950 td[width='45%'][valign='top'] table[class='']:first-child,
    .float_right > table[class='']:first-child {
      padding:7px 12px 0 12px !important;
      font-size:16px !important;
    }

    /* forum login form */
      .float_right > .br1:nth-child(2) {
        padding:0 10px !important;
      }

      .float_right > .br1:nth-child(2) .panel-body {
        padding:0 5px !important;
      }

      .float_right > .br1:nth-child(2) .form-horizontal .form-group {
        margin-left:0 !important;
        margin-right:0 !important;
      }

      .float_right > .br1:nth-child(2) .form-horizontal label:not(:first-child),
      .float_right > .br1:nth-child(2) .form-horizontal .col-sm-2 {
        padding:0 !important;
      }

      .float_right > .br1:nth-child(2) .form-horizontal input[type='checkbox'] {
        margin-top:2px !important;
      }
    /* end forum login form */
  
    .user_mods.right_col > .no_move > .br1 > tbody > tr > td,
    body > .width950 td[class=''][width='50%'][valign='top'] table:nth-child(2):last-child,
    body > .width950 td[width='45%'][valign='top'] table.br1:nth-child(2),
    .float_right > .br1:nth-child(2) > tbody > tr > td {
      padding:0 0 10px 0 !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] form table:nth-child(2):last-child {
      padding-top:5px !important;
      padding-right:30px !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] > .br1 > tbody > tr > td,
    body > .width950 td[width='45%'][valign='top'] > .br1 > tbody > tr > td {
      padding-bottom:0 !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] table:first-child:nth-last-child(2),
    body > .width950 td[class=''][width='50%'][valign='top'] table:nth-child(2):last-child,
    body > .width950 td[width='45%'][valign='top'] table[class='']:first-child,
    body > .width950 td[width='45%'][valign='top'] table.br1:nth-child(2) {
      width:390px !important;
    }

    .user_mods.right_col > .no_move > .br1 .pt10 {
      padding-top:5px !important;
    }

    body > .width950 [class=''] > .br1 .pt10,
    body > .width950 [width='45%'] > .br1 .pt10 {
      padding-top:0 !important;
    }

    .user_mods.right_col #login_tbl {
      width:100% !important;
      padding:0 12px !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] table:nth-child(2):last-child tr > td[colspan='3'],
    body > .width950 td[width='45%'][valign='top'] table.br1:nth-child(2) tr > td[colspan='3'] {
      display:none !important;
    }

    #login_tbl > tbody > tr > td:first-child {
      width:100px !important;
    }

    #login_tbl > tbody > tr:nth-child(3) > td:last-child {
      padding-top:0 !important;
      padding-bottom:4px !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] input.ff {
      font-size:14px !important;
      height:34px !important;
      padding:6px 12px !important;
      vertical-align:middle !important;
      width:100% !important;
      font-family:inherit !important;
    }

    #btn-sub-xs[class^='sub'] > button.fb {
      font-size:12px !important;
      padding:3px 6px 1px 6px !important;
    }

    #login_tbl .fb,
    form[name='home_login'] .fb,
    #rec_pass .fb,
    body > .width950 td[class=''][width='50%'][valign='top'] table:nth-child(2):last-child .fb {
      font-size:13px !important;
      padding:6px 12px !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] table:nth-child(2):last-child tr:nth-child(4) > td,
    body > .width950 td[width='45%'][valign='top'] table.br1:nth-child(2) tr:nth-child(4) > td {
      padding-top:6px !important;
    }
  
    /* recover username or password */
      .float_right .br1 .panel-body + table {
        width:100% !important;
      }

      #recoveruser, #recoverpass {
        padding-left:5px !important;
        padding-right:5px !important;
      }

      #rec_pass * {
        width:100% !important;
      }
  
      #rec_pass td {
        display:block !important;
      }
  
      #rec_pass td:nth-child(2):last-child {
        text-align:center !important;
        padding-top:5px !important;
      }
  
      #rec_pass .fb {
        width:auto !important;
      }
  
      #recpass, #recpass2 {
        display:none !important;
      }
    /* end recover username or password */
  /* end login and password reset forms */
  
  .hpYellowBlock .hpWNentry > div {
    margin-bottom:0 !important;
  }
  
  #floating_box_base {
    height:0 !important;
  }
  
  .user_mods.right_col > #floating_box_base:last-child {
    height:auto !important;
  }
  
  .user_mods.right_col > #floating_box_base:last-child > h2 {
    font-size:16px !important;
    margin-bottom:5px !important;
  }
  
  .user_mods.right_col #mod-mdvid > br:last-child,
  .user_mods.right_col #mod-featvid > br:last-child {
    display:none !important;
  }
  
  .right_col #mod-mdvid,
  .right_col #mod-featvid,
  .right_col #mod-vidnow,
  .right_col #mod-mpvid,
  .right_col #mod-subvid {
    margin-bottom:14px !important;
  }
  
  .right_col #mod-vidnow #mmenulisthp2 > div,
  .right_col #mod-mpvid #mmenulisthp2 > div {
    width:275px !important;
    height:auto !important;
  }
  
  .right_col #mod-mpvid #mmenulisthp2 > div {
    padding:0 !important;
    margin-bottom:10px !important;
  }
  
  .right_col #mod-vidnow #mmenulisthp2 > div:nth-child(2) > div {
    width:265px !important;
    height:85px !important;
  }
  
  .right_col #mod-vidnow #mmenulisthp2 > div:nth-child(2) > div > div:nth-child(2),
  .right_col #mod-mpvid #mmenulisthp2 > div > div:nth-child(3) {
    width:130px !important;
  }
  
  .right_col #mod-vidnow #mmenulisthp2 > div:first-child img.thumb {
    width:265px !important;
  }
  
  .right_col #mod-vidnow > br,
  .right_col #mod-mpvid > br,
  .right_col #mod-subvid > br {
    display:none !important;
  }
  
  .right_col #mod-mdvid #recipient > div,
  .right_col #mod-featvid #recipient > div {
    width:100% !important;
  }
  
  .right_col #mod-mdvid #recipient > div > div:last-child,
  .right_col #mod-featvid #recipient > div > div:last-child {
    width:110px !important;
  }
  
  /* video listing */
    .qlistadding {
      margin-bottom:4px !important;
      width:100% !important;
    }
  
    #listview .dottb td[width='175'] > .qlistadding {
      width:auto !important;
    }
  /* end video listing */
  
  .width960 > tbody > tr > td.grey {
    padding-left:0 !important;
    padding-right:0 !important;
  }
  
  #media_container {
    height:auto !important;
  }
  
  /* video title and options (lights and expand icons are characters) */
    #media_container > table {
      margin-bottom:5px !important;
    }
  
    /* button positioning when the video title spans multiple lines */
    #media_container .vtitle {
      vertical-align:middle !important;
    }
  
    #media_container .vtitle,
    #lightSwitcher {
      font-size:20px !important;
    }
  
    #media_container .vtitle:first-child {
      padding-top:10px !important;
    }
  
    #media_container .vtitle:not(:first-child) {
      width:auto !important;
      height:36px !important;
      text-align:center !important;
      padding:0 !important;
      margin:0 -2px !important;
    }
  
    #media_container .vtitle #command {
      height:36px !important;
      width:100% !important;
      position:relative !important;
      left:2px !important;
    }
  
    #media_container #player_size {
      width:43px !important;
    }
  
    #media_container #lightSwitcher,
    #media_container #player_size {
      position:static !important;
      display:inline-block !important;
      text-align:center !important;
      padding:6px 0px !important;
      height:100% !important;
    }
  
    #media_container #player_size {
      height:36px !important;
    }
  
    #media_container #lightSwitcher {
      width:41px !important;
    }
  
    #media_container #lightSwitcher.turnedON {
      position:relative !important;
    }
  /* end video title and options */
  
  #the_lights {
    position:fixed !important;
  }
  
  .grey td[align='left'] > br:first-of-type,
  .grey td[align='right'] > br:first-of-type {
    display:none !important;
  }
  
  .br2 {
    margin-top:10px !important;
    font-size:15px !important;
    padding:5px 0 0 0 !important;
  }
  
  .br2 .br2b, .br2 .br2l {
    cursor:pointer !important;
    font-size:15px !important;
    padding-bottom:3px !important;
    display:inline-block !important;
    margin:0 21px !important;
    padding:2px 0 3px 0 !important;
  }
  
  .br2 .br2b {
    float:left !important;
    margin-left:20px !important;
  }
  
  .br2 .br2l:nth-child(5) {
    float:right !important;
    margin-right:20px !important;
    margin-left:0 !important;
  }
  
  .br2 ~ .br2 {
    margin-top:0 !important;
    font-size:12px !important;
  }
  
  .br2 table table td[width='75'] {
    width:0 !important;
    padding:0 !important;
  }
  
  /* view count */
  .grey .br2 h3 {
    font-size:16px !important;
  }
  
  .pl2[id^='cleftarr'] {
    padding-left:10px !important;
    padding-right:7px !important;
  }
  
  .pl2[id^='cdownarr'] {
    padding-left:7px !important;
    padding-right:6px !important;
  }
  
  a {
    font-size:inherit !important;
  }
  
  [id^='scfradd'] {
    font-size:15px !important;
  }
  
  .br2:only-of-type > tbody > tr:nth-child(2) > td.nopad {
    font-size:12px !important;
  }
  
  .br2:only-of-type > tbody > tr:nth-child(2) > td.nopad div[id^='divfr'] {
    padding-top:10px !important;
    padding-bottom:10px !important;
  }
  
  [id^='divfr'] [align='center'] .qlistadding2 {
    padding-left:5px !important;
    padding-right:5px !important;
  }
  
  [id^='divfr'] > br {
    display:none !important;
  }
  
  #theresp {
    display:none !important;
  }
  
  /* show video responses by default */
    /*#cdownarr5 {
      display:block !important;
    }

    #cleftarr5 {
      display:none !important;
    }

    [id^='divfr'] {
      display:block !important;
    }*/
  /* end show video repsonses by default */
  
  /* video comments */
    #comments h3 {
      font-size:12px !important;
    }
  
    .nopad > #comments > div[id^='stbody'] {
      padding-left:5px !important;
      padding-right:5px !important;
    }
  
    .nopad > #comments > div.stbody:not(:first-child) {
      padding-top:5px !important;
    }
  
    .sttext {
      padding-bottom:0 !important;
      min-height:62px !important;
    }
  
    .sttext > div:first-of-type {
      width:100% !important;
    }
      
    .commentcontainer > .stcommentbody:last-child {
      margin-bottom:8px !important;
    }
  
    .stcommentbody > .stcommenttext > div:first-of-type {
      width:100% !important;
    }
  /* end video comments */
  
  .grey > div:nth-child(2)[style] {
    width:310px !important;
  }
  
  .grey > div:nth-child(2)[style] td[class=''] {
    padding:0 !important;
  }
  
  .grey > div:nth-child(2) td[class=''] .vtitle {
    display:none !important;
  }
  
  .br1.smart_break {
    padding:10px !important;
    margin-top:8px !important;
  }
  
  .br1.smart_break table.dottc {
    padding:0 0 10px 0 !important;
  }
  
  .br1.smart_break table.dottc + table {
    padding-left:5px !important;
    padding-right:5px !important;
  }
  
  .user_img, .user_img:hover,
  .user_imgf, .user_imgf:hover,
  img.sm-gen-border, img.sm-gen-border:hover,
  img.sper-sm-gen-border, img.sper-sm-gen-border:hover {
    height:auto !important;
    width:auto !important;
  }
  
  .br1.smart_break .dottc td.pl5 {
    width:100% !important;
    padding-left:8px !important;
  }
  
  .br1.smart_break .dottc td.pl5 tr:first-child {
    font-size:14px !important;
  }
  
  .br1.smart_break .dottc td.pl5 tr:nth-child(2) td:before {
    content:'Uploaded' !important;
  }
  
  .br1.smart_break .dottc td.pl5 tr:nth-child(3) span {
    white-space-collapsing:trim-inner !important;
  }
  
  .br1.smart_break .dottc td.pl5 + td {
    display:none !important;
  }
  
  #fdetails .pl5 {
    width:70px !important;
  }
  
  td[class=""] > .br1.smart_break ~ .br1 > tbody > tr:first-child > td:first-child {
    padding:10px 0 10px 4px !important;
  }
  
  td[class=""] > .br1.smart_break ~ .br1 > tbody > tr:first-child > td:last-child {
    padding:10px 10px 10px 0 !important;
  }
  
  #morefromdiv > div[style^='clear:both'],
  #relalldiv > div[style^='clear:both'] {
    overflow:hidden !important;
    padding-left:10px !important;
  }
  
  #morefromdiv,
  #relalldiv {
    padding-top:5px !important;
  }
  
  #relalldiv {
    padding-bottom:5px !important;
  }
  
  .br1.smart_break ~ .br1 .qlistadding {
    width:auto !important;
  }
  
  #morefromdiv > div > div[style^='width:135px'],
  #relalldiv > div > div[style^='width:135px'] {
    -webkit-hyphens:auto !important;
    -ms-hyphens:auto !important;
    -moz-hyphens:auto !important;
    hyphens:auto !important;
    padding-left:3px !important;
    width:145px !important;
  }
  
  #morefromdiv div[style^='width:135px'] p,
  #relalldiv div[style^='width:135px'] p {
    margin:3px 0 0 0 !important;
  }
  
  #morefromdiv > br {
    display:none !important;
  }
  
  /* prevent a suggested video having more than five stars */
  .rate-bg,
  .rate-bg2 {
    max-width:100% !important;
  }
  
  /* fix chevron-arrow-thing for related videos */
    .br1.smart_break ~ table.br1:last-of-type .pl2[style='display: none;'] {
      display:block !important;
    }

    .br1.smart_break ~ table.br1:last-of-type .pl2[style='display: block;'] {
      display:none !important;
    }
  /* end fix chevron-arrow-thing */
  
  /* footer */
    #footContent {
      padding:0 !important;
    }

    #footTopDiv {
      padding:8px 0 !important;
    }
  
    #footTopDiv td:first-child {
      width:100% !important;
    }
  
    #footTopDiv td:first-child form {
      margin-right:294px !important;
    }
  
    #footTopDiv td[width='33%'] {
      display:none !important;
    }
  
    #footBotDiv {
      padding-bottom:8px !important;
      line-height:18px !important;
    }
  
    .footColumnLeft {
      padding-left:0 !important;
    }
  
    #footBotDiv .column {
      min-width:25% !important;
    }
  
    .footLabel {
      font-size:12px !important;
    }
