@-moz-document regexp('https?://(www|support)\\.zippcast\\.com/(?!user/).*') {
  body {
    background-color:#eeeeee !important;
  }
}

@-moz-document domain("www.zippcast.com"), domain("support.zippcast.com"), domain("forum.zippcast.com") {
  
  /* header */
    .toplinks {
      background-color:#ffffff !important;
    }
  
    .bgwhite {
        background-color:transparent !important;
    }
  
    #logo_color {
      border-radius:4px !important;
      border:1px solid #aaaaaa !important;
      background-color:#ffffff !important;
    }
  
    /* .bgwhite is used to distinguish from a notice (such as latency issues due to high volumes of traffic) which confusingly/inappropriately includes elements matching #masthead-search-bar-container, #masthead-search-bar and/or .masthead-bar */
    .bgwhite .masthead-bar {
      box-shadow:0 4px 0 #888888, 0 1px 0 #dddddd inset !important;
      border:1px solid #888888 !important;
      background: rgb(196,196,196) !important; /* Old browsers */
      background: -moz-linear-gradient(top, rgba(196,196,196,1) 0%, rgba(170,170,170,1) 100%) !important; /* FF3.6+ */
      background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(196,196,196,1)), color-stop(100%,rgba(170,170,170,1))) !important; /* Chrome,Safari4+ */
      background: -webkit-linear-gradient(top, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* Chrome10+,Safari5.1+ */
      background: -o-linear-gradient(top, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* Opera 11.10+ */
      background: -ms-linear-gradient(top, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* IE10+ */
      background: linear-gradient(to bottom, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* W3C */
    }
  
    .bgwhite #masthead-search-bar {
      border-radius:4px !important;
    }
  
    #masthead-nav > span {
      border-top:none !important;
      border-left:none !important;
      border-bottom:none !important;
      border-right:1px solid rgba(0, 0, 0, 0.2) !important;
    }
  
    #masthead-nav a {
      color:#494949 !important;
      text-shadow:0 1px rgba(255, 255, 255, 0.4) !important;
      font-weight:bold !important;
    }
  
    #masthead-nav a:hover, #masthead-nav a:focus {
      text-decoration:none !important;
      background-color:rgba(0, 0, 0, 0.05) !important;
    }
  
    #masthead-nav > span:first-child > a {
      border-radius:4px 0 0 4px !important;
    }
  
    #masthead-search input {
      border:1px solid #c5c5c5 !important;
      box-shadow:0 1px 1px rgba(0, 0, 0, 0.5) inset !important;
      border-radius:3px 3px 4px 4px !important;
      transition:box-shadow 0.2s, border 0.3s !important;
      -webkit-transition:box-shadow 0.2s, border 0.3s !important;
    }
  
    #masthead-search input:focus {
      box-shadow: 0 1px 4px rgba(0, 0, 0, 0.8) inset !important;
      transition:box-shadow 0.2s, border 0.3s !important;
      -webkit-transition:box-shadow 0.2s, border 0.3s !important;
      border-radius:3px !important;
    }
  
    #upload_menu, #upload_menu:visited,
    input.fb,
    button.fb:not(#search-btn) {
      border:1px solid #eac00a !important;
      text-shadow:0 1px rgba(255, 255, 255, 0.6) !important;
      background: rgb(255,234,76) !important; /* Old browsers */
      background: -moz-linear-gradient(top, rgba(255,234,76,1) 0%, rgba(255,234,76,1) 50%, rgba(255,218,96,1) 51%, rgba(255,255,96,1) 100%) !important; /* FF3.6+ */
      background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255,234,76,1)), color-stop(50%,rgba(255,234,76,1)), color-stop(51%,rgba(255,218,96,1)), color-stop(100%,rgba(255,255,96,1))) !important; /* Chrome,Safari4+ */
      background: -webkit-linear-gradient(top, rgba(255,234,76,1) 0%,rgba(255,234,76,1) 50%,rgba(255,218,96,1) 51%,rgba(255,255,96,1) 100%) !important; /* Chrome10+,Safari5.1+ */
      background: -o-linear-gradient(top, rgba(255,234,76,1) 0%,rgba(255,234,76,1) 50%,rgba(255,218,96,1) 51%,rgba(255,255,96,1) 100%) !important; /* Opera 11.10+ */
      background: -ms-linear-gradient(top, rgba(255,234,76,1) 0%,rgba(255,234,76,1) 50%,rgba(255,218,96,1) 51%,rgba(255,255,96,1) 100%) !important; /* IE10+ */
      background: linear-gradient(to bottom, rgba(255,234,76,1) 0%,rgba(255,234,76,1) 50%,rgba(255,218,96,1) 51%,rgba(255,255,96,1) 100%) !important; /* W3C */
    }
  
    input.fb,
    button.fb:not(#search-btn) {
      /* must specify the shadow that comes in on hover (with 0 alpha) so that the transition operates as intended */
      box-shadow:0 0 0 1000px rgba(255, 255, 255, 0.15) inset, 0 1px 0 rgba(255, 255, 255, 1) inset !important;
      -webkit-transition:box-shadow 0.3s !important;
      transition:box-shadow 0.3s !important;
    }
  
    input.fb:hover,
    button.fb:hover:not(#search-btn) {
      /* workaround to the fact a transparent background-colour cannot be added on top of a gradient */
      box-shadow:0 0 0 1000px rgba(255, 255, 255, 0) inset, 0 1px 0 rgba(255, 255, 255, 1) inset !important;
      -webkit-transition:box-shadow 0.1s !important;
      transition:box-shadow 0.1s !important;
    }
  
    #upload_menu, #upload_menu:visited {
      border-radius:4px !important;
      box-shadow:0 4px 0 #e7be2b, 0 1px 0 #ffffff inset !important;
    }
  
    #upload_menu > #slinkdiv {
      border-radius:4px !important;
      background-color:rgba(255, 255, 255, 0.15) !important;
      -webkit-transition:background-color 0.3s !important;
      transition:background-color 0.3s !important;
    }
  
    #upload_menu > #slinkdiv:hover {
      background-color:rgba(255, 255, 255, 0) !important;
      -webkit-transition:background-color 0.1s !important;
      transition:background-color 0.1s !important;
    }

    /* search suggestions */
      body > ul.ui-autocomplete {
        position:absolute !important;
        border:none !important;
        padding-left:5px !important;
        box-shadow: 2px 3px 3px rgba(0,0,0,0.4) !important;
        border-radius:0 0 4px 4px !important;
        background:none !important;
        width:319px !important;
        top:75px !important;
        cursor:default !important;
      }
  
      body > ul.ui-autocomplete > li {
        position:relative !important;
        box-shadow:-2px 4px 3px rgba(0,0,0,0.4) !important;
        background-color:#ffffff !important;
        cursor:pointer !important;
        padding-left:4px !important; /* override padding-left from layout */
      }
  
      body > ul.ui-autocomplete > li:last-child {
        border-radius:0 0 3px 3px !important;
      }
  
      body > ul.ui-autocomplete > li:hover {
        background-color:#ccccff !important;
      }
  
      body > ul.ui-autocomplete .hl_results {
        font-weight:normal !important;
        color:#444444 !important;
      }
    /* end search suggestions */
  /* end header */
  
  .cborder, .br2, td[class=''] > .br1.smart_break ~ .br1 > tbody > tr:first-child {
    background:#d0dff3 !important;
    box-shadow:0 1px 0 rgba(255, 255, 255, 0.4) inset !important;
    text-shadow:0 1px rgba(255, 255, 255, 0.6) !important;
    border-bottom:none !important;
    border-radius:5px 5px 0 0 !important;
  }
  
  #mmh1hp2 {
    color:#455056 !important;
  }
  
  .thumb, .thumbf, .thumbp,
  img[src*='files_thumbnail'] {
    border:none !important;
    box-shadow:1px 1px 2px #777777 !important;
  }
  
  .thumb:hover, .thumbf:hover, .thumbp:hover,
  img[src*='files_thumbnail']:hover {
    border:none !important;
    outline:1px solid #fa961f !important;
    box-shadow:1px 1px 5px #555555 !important;
    opacity:0.9 !important;
  }
  
  .mod_content {
    background:transparent !important;
  }
  
  #mmenulisthp2 {
    background-color:#ffffff !important;
    border-radius: 0 0 3px 3px !important;
  }
  
  .user_mods.right_col > .no_move:first-child > table:first-of-type div[align='center'] {
    border-radius:3px 3px 0 0 !important;
  }
  
  .user_mods.right_col > .no_move > table.featvid_info {
    background-color:#ffffff !important;
    border:1px solid #c5d0d6 !important;
    border-top:none !important;
    border-radius:0 0 3px 3px !important;
  }
  
  
  /* login and password reset forms */
    .user_mods.right_col > .no_move > table:nth-of-type(3),
    body > .width950 td[class=''][width='50%'][valign='top'] table:first-child:nth-last-child(2),
    body > .width950 td[width='45%'][valign='top'] table[class='']:first-child,
    .float_right > table[class='']:first-child {
      border-radius:5px 5px 0 0 !important;
      border:1px solid #65a8ff !important;
      border-bottom:none !important;
      background-color:#f0f6ff !important;
      color:#0f4489 !important;
      font-weight:bold !important;
    }

    .user_mods.right_col > .no_move > .br1,
    body > .width950 td[class=''][width='50%'][valign='top'] table:nth-child(2):last-child,
    body > .width950 td[width='45%'][valign='top'] table.br1:nth-child(2),
    .float_right > .br1:nth-child(2) {
      border-radius:0 0 5px 5px !important;
      border:1px solid #65a8ff !important;
      border-top:none !important;
      background-color:#f0f6ff !important;
    }

    .user_mods.right_col > .no_move > .br1 td[align='right'],
    body > .width950 td[class=''][width='50%'][valign='top'] table:nth-child(2):last-child .types,
    body > .width950 td[width='45%'][valign='top'] table.br1:nth-child(2) .types {
      font-weight:normal !important;
    }

    #login_tbl .form-control {
      border-radius:3px !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] input.ff {
      border:1px solid #e2e2e4 !important;
      border-radius:3px !important;
      color:#333333 !important;
      -webkit-transition:border-color 0.15s ease-in-out 0s !important;
      transition:border-color 0.15s ease-in-out 0s !important;
    }

    body > .width950 td[class=''][width='50%'][valign='top'] input.ff:focus {
      border-color:#517397 !important;
    }

    #login_tbl .fb {
      border-radius:3px !important;
    }
  
    /* recover username or password */
    #recoveruser, #recoverpass {
      border:none !important;
    }
  /* end login and password reset forms */
  
  .user_mods.right_col > #floating_box_base:last-child > h2 {
    color:#000000 !important;
    text-shadow:0 1px #ffffff !important;
  }
  
  .grey {
    background:none !important;
  }
  
  /* video title and options (lights and expand icons are characters) */
    #media_container .vtitle {
      text-shadow:0 1px 0 rgba(255, 255, 255, 0.8) !important;
    }
  
    #media_container .vtitle #command {
      background-color:#ffffff !important;
      border:1px solid #aaaaaa !important;
    }
  
    #media_container .vtitle #command,
    #media_container .vtitle #command i {
      border-radius:4px 0 0 4px !important;
    }
  
    #media_container #player_size {
      background-color:#ffffff !important;
      border:1px solid #aaaaaa !important;
    }
  
    #media_container .vtitle i:hover {
      box-shadow:0 0 1000px rgba(100, 100, 100, 0.2) inset !important;
    }
  
    #media_container .vtitle:last-child,
    #media_container .vtitle:last-child i {
      border-radius:0 4px 4px 0 !important;
    }
  
    #media_container #lightSwitcher.turnedOFF:not(.turnedON) {
      color:#455372 !important;
    }
  
    #media_container #lightSwitcher.turnedON:hover {
      background-color:rgba(255, 255, 255, 0.05) !important;
    }
  /* end video title and options */
  
  .br2 {
    border-radius:5px 5px 0 0 !important;
  }
  
  .br2 .br2b, .br2 .br2l {
    font-weight:bold !important;
    color:#455056 !important;
    border-top:none !important;
    border-right:none !important;
    border-left:none !important;
    border-bottom:2px solid rgba(0, 0, 0, 0) !important;
    -webkit-transition:border-bottom 0.2s, color 0.2s !important;
    transition:border-bottom 0.2s, color 0.2s !important;
  }
  
  .br2 .br2b i, .br2 .br2l i {
    color:#455056 !important;
  }
  
  .br2 .br2b:hover, .br2 .br2l:hover {
    color:#4560e6 !important;
    border-bottom:2px solid #4560e6 !important;
    -webkit-transition:border-bottom 0.2s, color 0.2s !important;
    transition:border-bottom 0.2s, color 0.2s !important;
  }
  
  .br2 ~ .br2 {
    border-radius:0 0 3px 3px !important;
    border-bottom:1px solid #c5d0d6 !important;
    background-color:#ffffff !important;
    text-shadow:none !important;
  }
  
  .br2:only-of-type {
    border-radius:5px 5px 3px 3px !important;
    border-bottom:1px solid #c5d0d6 !important;
  }
  
  .br2:only-of-type > tbody tr:first-child td {
    background:none !important;
  }
  
  [id^='scfradd'] {
    font-weight:bold !important;
    color:#455056 !important;
  }
  
  a[onclick*='cleftarr']:hover {
    text-decoration:none !important;
  }
  
  .br2 .pl5 > a {
    text-decoration:none !important;
  }
  
  .br2:only-of-type > tbody > tr:nth-child(2) > td.nopad {
    background-color:#ffffff !important;
    border-top:1px solid #c5d0d6 !important;
    border-radius:0 0 3px 3px !important;
  }
  
  /* video comments */
    .br2 ~ table:last-child tr:first-child > td[colspan='2'] {
      border-bottom:2px solid #9aabbc !important;
    }
  
    .br2 ~ table:last-child tr:nth-child(3) td.nopad {
      background-color:#c2ccd6 !important;
    }
  
    #comments h3 {
      color:#000000 !important;
    }
  
    .sttime, .stcommenttime {
      color:rgba(0, 0, 0, 0.6) !important;
    }
  
    .stbody {
      border-bottom:1px dashed #eeeeee !important;
    }
  
    .stcommentbody {
      background-color:#bbbbd5 !important;
      border-bottom:1px solid #cccce0 !important;
    }
  
    .commentcontainer > .stcommentbody:first-child {
      border-top:2px solid #9d9dc3 !important;
    }
  /* end video comments */
  
  .br1.smart_break {
    border:1px solid #65a8ff !important;
    border-radius:3px !important;
    background-color:#f0f6ff !important;
  }
  
  .br1.smart_break table.dottc {
    border-bottom:1px solid rgb(190, 190, 240) !important;
  }
  
  .br1.smart_break td.dottc {
    border:none !important;
  }
  
  /* avatars */
    .user_img, .user_img:hover,
    .user_imgf, .user_imgf:hover,
    img.sm-gen-border, img.sm-gen-border:hover,
    img.sper-sm-gen-border, img.sper-sm-gen-border:hover {
      background-color:#ffffff !important;
      border:none !important;
      border-radius:3px !important;
      box-shadow:0 0 2px rgba(0, 0, 0, 0.2) !important;
    }

    .user_img,
    .user_imgf,
    .big_face,
    .small_face,
    img.sm-gen-border,
    img.sper-sm-gen-border {
      -webkit-transition:box-shadow 0.2s !important;
      transition:box-shadow 0.2s !important;
    }

    .user_img:hover,
    .user_imgf:hover,
    .big_face:hover,
    .small_face:hover,
    img.sm-gen-border:hover,
    img.sper-sm-gen-border:hover {
      box-shadow:0 0 5px rgba(0, 0, 0, 0.4) !important;
      -webkit-transition:box-shadow 0.4s !important;
      transition:box-shadow 0.4s !important;
    }

    /* avatar containers in video comment replies */
    .stcommentimg {
      border:none !important;
    }
  /* end avatars */
  
  .br1.smart_break .dottc td.pl5 tr:nth-child(3) span {
    font-style:normal !important;
    font-weight:normal !important;
  }
  
  td[class=''] > .br1.smart_break ~ .br1 {
    background-color:#ffffff !important;
    border-radius:5px 5px 3px 3px !important;
  }
  
  td[class=""] > .br1.smart_break ~ .br1 > tbody > tr:first-child > td {
    border-bottom:1px solid #c5d0d6 !important;
  }
  
  /* footer */
    #footContent {
      background-color:#ffffff !important;
      border:1px solid #888888 !important;
      border-radius:4px !important;
    }
  
    #footTopDiv {
      box-shadow:0 1px 0 #dddddd inset !important;
      border-bottom:inherit !important;
      border-radius:4px 4px 0 0 !important;
      background: rgb(196,196,196) !important; /* Old browsers */
      background: -moz-linear-gradient(top, rgba(196,196,196,1) 0%, rgba(170,170,170,1) 100%) !important; /* FF3.6+ */
      background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(196,196,196,1)), color-stop(100%,rgba(170,170,170,1))) !important; /* Chrome,Safari4+ */
      background: -webkit-linear-gradient(top, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* Chrome10+,Safari5.1+ */
      background: -o-linear-gradient(top, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* Opera 11.10+ */
      background: -ms-linear-gradient(top, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* IE10+ */
      background: linear-gradient(to bottom, rgba(196,196,196,1) 0%,rgba(170,170,170,1) 100%) !important; /* W3C */
    }
  
  /* end footer */
  
  body > .br2[width='950'] {
    background-color:transparent !important;
    border-radius:0 !important;
  }
  
  /* make link to current page appear disabled - can't actually be disabled without @-moz-document rules for each case */
  body > .br2 a[href*='/channels/'] .act,
  body > .br2 a[href*='/videos/'] .act,
  body > [width='950'] > tbody > tr:first-child > td:only-child[class=''] #show_filters a .act {
    color:#000000 !important;
  }
  
  .width950b {
    border:none !important;
  }
  
  .width950b > tbody > tr > td.pl0 .br1[width='80%'] {
    border:none !important;
  }
  
  .width950b > tbody > tr > td.pl0 .br1[width='80%'] section,
  .width950b > tbody > tr > td.pl0 .br1[width='80%'] > form,
  [width='10%'] + .nopad section {
    border:none !important;
    border-radius:0 !important;
    box-shadow:0 1px 2px rgba(0, 0, 0, 0.2) !important;
    background-color:#ffffff !important;
  }
  
  .width950 .lm[width='20%'] + .nopad_bg {
    background:none !important;
  }
  
  .width950 .lm[width='20%'] + .nopad_bg > table {
    background-color:#ffffff !important;
    box-shadow:0 1px 2px rgba(0, 0, 0, 0.2) !important;
  }
  
  /* left side menu items on editing and exploring pages */
    .menu_side ul li a:hover {
      background-color:#f5f5f5 !important;
    }

    .menu_side ul li:last-child a {
      border-bottom:1px solid #efeff0 !important;
    }
  
    .menu_side > li > a.active {
      border-bottom:none !important;
    }
  /* end left side menu */
  
  .zc-button {
    text-shadow:0 1px rgba(255, 255, 255, 0.6);
  }
  
  body > [width='950'] > tbody > tr:first-child > td:only-child[class=''] {
    background: rgb(185,208,219) !important; /* Old browsers */
    background: -moz-linear-gradient(top, rgba(185,208,219,1) 0%, rgba(172,198,212,1) 50%, rgba(159,189,207,1) 51%, rgba(144,170,194,1) 100%) !important; /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(185,208,219,1)), color-stop(50%,rgba(172,198,212,1)), color-stop(51%,rgba(159,189,207,1)), color-stop(100%,rgba(144,170,194,1))) !important; /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, rgba(185,208,219,1) 0%,rgba(172,198,212,1) 50%,rgba(159,189,207,1) 51%,rgba(144,170,194,1) 100%) !important; /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, rgba(185,208,219,1) 0%,rgba(172,198,212,1) 50%,rgba(159,189,207,1) 51%,rgba(144,170,194,1) 100%) !important; /* Opera 11.10+ */
    background: -ms-linear-gradient(top, rgba(185,208,219,1) 0%,rgba(172,198,212,1) 50%,rgba(159,189,207,1) 51%,rgba(144,170,194,1) 100%) !important; /* IE10+ */
    background: linear-gradient(to bottom, rgba(185,208,219,1) 0%,rgba(172,198,212,1) 50%,rgba(159,189,207,1) 51%,rgba(144,170,194,1) 100%) !important; /* W3C */
    color:#000000 !important;
  }
  
  body > [width='950'] > tbody > tr:first-child > td:only-child[class=''] .btn-default {
    background-color:#dddddd !important;
    color:#000000 !important;
  }

  body > table[width='950'][border='0'][style='border: 1px solid #E2E2E2'] {
    border:none !important;
    box-shadow:0 1px 2px rgba(0, 0, 0, 0.2) !important;
  }

  h2 {
    color:#455372 !important;
    text-shadow:0 1px rgba(255, 255, 255, 0.6);
  }
  
  /* news pages */
    h1 {
      text-shadow:0 1px rgba(255, 255, 255, 0.6);
    }
  
    .article {
      border-radius:5px !important;
    }
  
    #cat_left_col {
      border:1px solid rgba(221, 221, 221, 0.75) !important;
      box-shadow:1px 1px 2px 0 rgba(221, 221, 221, 0.75) !important;
    }
  
    #cat_left_col .br2 {
      background-color:transparent !important;
      border:none !important;
      box-shadow:none !important;
      text-shadow:none !important;
