@-moz-document domain("facebook.com") {

/*== Background-Mod (with Site-Manager) ==*/

body           {background-color: rgba(255,255,255,0.0) !important;}

html#facebook  {background: url('http://fs5.directupload.net/images/151001/if43gf8y.jpg') fixed; background-size:100% auto;}





/*== Transparenz für Meldungen ==*/

body>#blueBar       {background: none !important;}

.jewel, .jewelBox   {border: none !important; }

.jewelBox           {background-color: rgba(255,255,255,0.9) !important;}

#headNavOut         {background: none !important; border: none !important;}

#pageNav a          {color: #111 !important;}

#pageNav a:hover    {color: #333 !important; background: none !important;}

#navAccount         {border: none !important;}

#navAccount ul      {background-color: rgba(255,255,255,0.9) !important;  border: none !important;}

#contentCol,#left_column,#right_column                  {background-color: rgba(255,255,255,0.4) !important;  border: none !important;}

.inputtext,.add_comment_text,.uiMorePager, .box_header  {background-color: rgba(255,255,255,0.5) !important;}

.ufi_section, .photo_table, .photo_container            {background: none !important; border: none !important;}

#sidebar_ads, #pagelet_adbox, #ego                      {display: none !important;}

.uiBoxWhite {background-color: rgba(255,255,255,0.5);}

.uiInputLabel .uiInputLabelLabel {color: gray !important;}

.escapeHatchMinimal, ._4lh .timelineReportContainer {background-color: rgba(0,0,0,0);}

._50f8, a.uiLinkSubtle {color: rgba(80,80,80,0.8);}





/*== Privacy-PopUp-Icon-Color ==*/

div#userNavigationLabel, div#privacyFlyoutLabel, div._5ahz {-webkit-filter: invert(50%);}

._5ahz {background: none;}



  

/*== Chat-Bar Transparency ==*/

  .fbChatSidebar.fixed_always._5pr2							{color : rgb(0,0,255); background-color: rgba(255,255,255, 0.5);}

  div._5rzs, div#leftCol									{background : rgba(255,255,255, 0.4);}

  div._5qqe, div#pagelet_canvas_nav_content					{background : #333 ; opacity : 0.1;}  

  div._5rzs, div._4-u2.mbm._5v6e, div#u_0_0, .cardRightCol	{background : rgba(255,255,255, 0.4);}





/*== Icon-Mod ==*/

#fbRequestsJewel .jewelButton                       {background:url("https://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Friends-Gray.png") no-repeat center center!important}

#fbMessagesJewel .jewelButton, ._1z4y a.jewelButton {background:url("https://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Mail_Icon-gray.png") no-repeat center center!important}

#fbNotificationsJewel .jewelButton                  {background:url("https://i795.photobucket.com/albums/yy232/DaedalusIcarusHelios/Earth-gray.png") no-repeat center center!important}



  

/*== Schwarz-Mod für blauen Hauptbalken ==*/

#blueBarHolder #blueBar, #blueBarNAXAnchor        {background: #222 !important;}

div#blueBarNAXAnchor._4f7n._xxp.fixed_elem::after {background: #333 !important;}

    

    

/*== Chat-Mod ==*/

.fbNubFlyoutTitlebar						{background: #222 !important; border: 1px solid #222 !important; border-radius: 1px !important;}

/*== Transparency ==*/

._1nc6 ._5w1r, ._1nc6 ._3okg:before			{background-color: rgba(100,165,255,0.6); background-image: none;}

._1nc7 ._5w1r, ._1nc7 ._3okg:before 		{background-color: rgba(255,255,255,0.7); background-image: none;}

._5w1r										{text-shadow: rgba(255, 255, 255,0.2) 0 1px 0;}

._3okg:after, ._3okg:before					{display: none;}

._5ys_:after								{background: none;}

._5w-6, ._510g								{background-color: rgba(0,0,0,0); color: gray;}

.fbNubFlyoutBody, ._50mz .fbNubFlyoutInner	{background-color: rgba(255,255,255,0.5) !important;}





/*====== Button-Mod ======*/

._51sy, ._9c6, ._58gm ._51xa									{border-color: black; background-image: none; background: rgba(255,255,255, 0.5); color: #525252; text-shadow: none;}



/*== Temporary Picture-View Bugfix ==*/

.ego_page .UIStandardFrame_Content {background-color: rgba(255,255,255,0.7);}



/*====== TL-Transparency-Mod ======*/

._4-u8, ._55d0, ._5vgz, ._5otg, ._4nq7, ._5142, ._50nb,

.UFIAddCommentInput, ._1kny, ._1f8g, ._2i-0						{background: rgba(255,255,255, 0.3) !important;}

#pagelet_composer, ._55d0, ._5t_y, ._5v9k, .UFIRow, ._5h60		{background: rgba(255,255,255, 0.0) !important;}

._5t_y ._1dsp._42jz, ._6dh ._1dsp, ._1dsp, ._1eds {background-color: rgba(0,0,0,0);}



}



@-moz-document domain("facebook.com") {

/*== Login-Screen Mods ==*/

.loggedout_menubar_container          { background:none  !important; }

.fbIndex #globalContainer #dropmenu_container,

.fbIndex #globalContainer #content,

.fbIndex #globalContainer #pageFooter { display:   none  !important; }



/*== SchriftFarbe ==*/

label.uiInputLabelLabel, .login_form_label_field, .login_form_label_field a

{ color: white !important; }



/*== LoginFeld-Größen und Farben ==*/

.fbIndex .loggedout_menubar { width: auto !important; }



.fbIndex .loggedout_menubar_container {

  position:   fixed !important;

  width:      435px !important;

  height:     82px  !important;

  min-width:  0     !important;

  top:        45%   !important;

  left:       5%    !important; }



.loggedout_menubar {

  background:              rgba(0,0,0,0.6) !important;

  padding:                 0 10px 14px 10px;

	-webkit-border-radius: 6px;

	border-radius:         1px;

    box-shadow:            20px 20px 40px  rgba(0, 0, 0, 0.5);

    border:                1px solid rgba(0,0,0,0.3);}





/*== POP-UP TRANSPARENCY TEST + minor bugfixes and improvements ==*/

._5p3y .fcg, ._5k4f, ._538r {color: rgba(50,50,50,0.7);}

.fbTimelineSection, ._513x, ._2w3>._30f {background: none;}

.fbTimelineCapsule .timelineUnitContainer,._70k, ._3cz {background-color: rgba(255,255,255,0.5);}

.litestandClassicWelcomeBox.fbxWelcomeBox a, .litestandClassicWelcomeBox.fbxWelcomeBoxSmall a {color: black;}



