css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(funnyjunk.com) {
html,
body {
    background-color: #002b36 !important;
    border: none !important;
    font-family: Open Sans, sans-serif !important;
}
#contentRight,
.main,
.similarPosts {
    border: none !important;
}
#Userbar,
#content,
#wrapper {
    background-color: #002b36 !important;
    border-color: #002b36 !important;
}
#comTypeHeader,
#footer,
#fj-chatbar,
#authorContentsLinksContainer,
#contentFlags,
#channels_bar,
#fj-chatbar-hide,
#sectionsNav,
#frontpageComTitle,
#frontHighestComments,
#barAva {
    display: none !important;
}
.title,
.paginationSlider,
.paginationSliderNext,
.aCenter,
.userTagsList,
.m-inner,
.block,
.ui-widget-overlay,
.cb-container {
    display: none !important;
}
.uploadBttn {
    border-radius: 0px !important;
    padding: 5px 6px !important;
    margin-left: 10px !important;
    margin-right: 10px !important;
    margin-top: 10px !important;
    background: #073642 none repeat scroll 0% 0%;
    border: none !important;
}
.search-inner input {
    border-radius: 0px !important;
    border: none !important;
    background-color: #073642 !important;
    width: 115% !important;
}
.btn,
.btn-primary {
    display: none !important;
}
#siteContent {
    background-color: #002b36 !important;
}
#sectionsNav {
    display: none !important;
}
#barAva {
    border-radius: 0px !important;
}
.f {
    background-color: #002b36 !important;
}
#contentRight,
.f_New,
.inner {
    background-color: #002b36 !important;
}
#alertContainer {
    background-color: #002b36 !important;
}
#alertContainer img {
    display: none !important;
}
#contentDesc,
.innerContentDescription {
    background-color: #002b36 !important;
}
#powerTip {
    background-color: #002b36 !important;
}
#topUserbar {
    background-color: #002b36 !important;
    border: none !important;
    padding-bottom: 7px;
}
#cNext_floating_js,
#cNext_floating,
.cNext_floating,
#cPrev_floating,
#cPrev_floating_js,
.cPrev_floating {
    background-color: #073642;
    font: 18px/1.8 "Open Sans", sans-serif;
    color: #FFF;
    border-radius: 0px;
    text-shadow: none;
    border: none;
}
#cNext_floating::before,
#cNext_floating_js::before,
.cNext_floating::before {
    display: none;
}
#cPrev_floating::before,
#cPrev_floating_js::before,
.cPrev_floating::before {
    display: none;
}
div.com,
img.av {
    border-radius: 0px;
}
#cRandom_floating {
    left: 5px;
    margin-left: 5px;
    width: 60px;
    background-color: #073642;
    font: 14px/1.8 "Open Sans", sans-serif;
    color: #FFF;
    border-radius: 0px;
    text-shadow: none;
    border: none;
    height: 25px;
    padding: 4px;
}
.styledBtn,
.magentaBtn,
.mt3,
#bottomFavAdd,
#AddSub {
    padding: 4px 5px 3px 4px;
    color: #DDD !important;
    background: #073642 none repeat scroll 0% 0%;
    border: none;
    border-radius: 0px;
    font-weight: inherit;
    margin-right: 10px;
}
.topRightUsername {
    font-weight: inherit;
}
#showCommentsOptions {
    border: none;
    padding: 2px 3px 1px;
    color: #FFF !important;
}
.selected {
    border: 1px solid #FFF;
}
.showCommentsButton {
    vertical-align: top;
    display: inline-block;
    padding: 2px 4px 1px;
    background-color: #073642;
    color: #FFF !important;
    text-decoration: none !important;
    border-radius: 0px;
    font-weight: inherit;
    cursor: pointer;
    border: none;
}
.showCommentsButton:hover {
    border: none;
    text-decoration: underline;
}
.latest-users-list {
    padding: 5px 10px;
    border: none;
    border-radius: 0px;
    margin-bottom: 10px;
    background-color: #073642
}
ul.profileContent li {
    background: #073642 none repeat scroll 0% 0%;
}
}
