css:
@-moz-document domain("stellar.mit.edu") {

body {background: white}
#hmlogo {visibility:hidden;}
#pageslice {border: 10px solid white}
div {background: white !important}

u1, h1, h2 {display:none}
#coursemodulewrapper{display:none}
#gatewaynav ul {background: none}

#pageslice a.bigLink, #pageslice a.bigLink:visited, #gatewaynav ul:after {background:none}

#gatewaynav li a, #gatewaynav li a:visited, #gatewaynav li {
    border: none;
    color: white;
}

/*fixes classes list*/
p.courseD, ul.atlist, #classnav {
    background:none;
    border:none;
}

/*fixes "Go to class"*/
#classnav li a, #classnav li:first-child {
    border:none;
    font-weight: 600;
}

/*fixes calendar*/
table.calendarTable, table.calendarTable th,
table.calendarTable td {
background:none;
border:none
}

}
