css:
@-moz-document domain("codepen.io") {
input[type='name'],
input[type='text'],
input[type='email'],
input[type='password'],
input[type='search'],
input[type='url'],
input[type='tel'],
input[type='number'],
textarea,
.CodeMirror-search-field {
  background: transparent !important;
  color: #cccccc !important;
  border: 2px solid gainsboro !important;
}
input[type='name']:active, input[type='name']:focus,
input[type='text']:active,
input[type='text']:focus,
input[type='email']:active,
input[type='email']:focus,
input[type='password']:active,
input[type='password']:focus,
input[type='search']:active,
input[type='search']:focus,
input[type='url']:active,
input[type='url']:focus,
input[type='tel']:active,
input[type='tel']:focus,
input[type='number']:active,
input[type='number']:focus,
textarea:active,
textarea:focus,
.CodeMirror-search-field:active,
.CodeMirror-search-field:focus {
  background: transparent !important;
  color: #cccccc !important;
  border: 2px solid #999999 !important;
}

.CodeMirror-dialog {
  background: #1A1C1D !important;
  color: #cccccc !important;
}
.CodeMirror-dialog input {
  background: #1d1f20 !important;
  color: #cccccc !important;
}

.view-meta > * {
  background: transparent;
}

.user-dropdown,
.priority-nav__dropdown {
  background-color: #28282D;
  color: #777777;
}

.activity-dropdown {
  background: #28282D;
  color: #999999;
}
.activity-name, .activity-thing {
  color: #aaa;
}
.activity-list {
  background: #28282D;
  color: #888888;
}
.activity-list a {
  color: #999999;
}
.activity-sentence.wrap-text {
  padding-right: 90px;
}

.pen-templates-dropdown {
  background-color: #28282D;
  color: #999999;
}
.pen-settings-halfs-wrap h4, .pen-settings-halfs-wrap label {
  color: #cccccc;
}
.pen-settings-halfs-wrap svg.icon-help {
  fill: #aaa;
}
.pen-settings-modal, .pen-details-when-dropdown {
  background: #444;
  color: #cccccc;
}
.pen-settings-modal input[type='name'],
.pen-settings-modal input[type='text'],
.pen-settings-modal input[type='email'],
.pen-settings-modal input[type='password'],
.pen-settings-modal input[type='search'],
.pen-settings-modal input[type='url'],
.pen-settings-modal input[type='number'],
.pen-settings-modal textarea {
  background: #444 !important;
  color: #cccccc;
}

.link-list a {
  color: #aaa;
}
.link-list a:hover, .link-list a:focus, .link-list a.active {
  background: #222222;
  color: #cccccc;
}
.link-list a:hover .view-meta, .link-list a:focus .view-meta, .link-list a.active .view-meta {
  background: #222222;
  color: #aaa;
}
.link-list a:hover svg, .link-list a:focus svg, .link-list a.active svg {
  fill: #aaa;
}
.link-list a svg {
  fill: #cccccc;
}
.link-list .user-dropdown-quicklink-row a:hover {
  background: none;
}

.single-collection pre, .single-post pre {
  background: #222222;
  color: #aaa;
}
.single-collection .collection-title a {
  color: #aaa;
}
.single-collection .collection-title a:hover, .single-collection .collection-title a:focus {
  color: #47CF73;
}
.single-collection .collection-content a {
  color: #76DAFF;
}
.single-post {
  color: #888888;
}
.single-post .post-title {
  text-overflow: ellipsis;
  overflow: hidden;
}
.single-post .post-title a {
  color: #aaa;
}
.single-post .post-title a:hover, .single-post .post-title a:focus {
  color: #47CF73;
}
.single-post .post-content a {
  color: #76DAFF;
}
.single-post-body p > code, .single-post-body li > code, .single-post-body dd > code, .single-post-body td > code {
  background: #444;
}
.single-title-and-excerpt, .iframe-wrap {
  background: black;
}

.job {
  background: #222222;
}
.job-company {
  color: #cccccc;
}
.job > a {
  background: #222222;
}
.job-title {
  color: #888888;
}
.job-featured, .job-featured > a {
  background: #262626;
}
.job-featured-text {
  background: #444;
}
.job:hover .job-title {
  color: #aaa;
}

.recent-activity-widget a {
  color: #aaa;
}
.recent-activity-widget a:hover, .recent-activity-widget a:focus {
  color: #76DAFF;
}

.settings-modal-right, .settings-modal-left {
  background-color: #3d3d3d;
  color: #aaa;
}
.settings-modal-right {
  border-left: 1px solid #777777;
}
.settings-modal-left {
  border-right: 1px solid #777777;
}

.pen-settings-modal-half .settings-modal-left {
  border-right: none;
}

.settings-row {
  color: #aaa;
}
.settings-row-5 a:hover, .settings-row-5 a:focus {
  color: gainsboro;
}

.save-and-close-wrap {
  border-top: 2px solid #888888;
  margin-top: 0;
  padding-top: 20px;
  padding-bottom: 20px;
  background-color: #3d3d3d;
}

.page-wrap, .job-list {
  background: #222222;
  color: #cccccc;
}
.page-wrap article, .job-list article {
  color: #cccccc;
}
.page-wrap article strong,
.page-wrap article em, .job-list article strong,
.job-list article em {
  color: gainsboro;
}
.page-wrap article blockquote, .job-list article blockquote {
  background: #272727;
  border-left-color: #444;
}
.page-wrap article code, .page-wrap article .table-of-contents, .job-list article code, .job-list article .table-of-contents {
  background: #272727;
}

.home-article h1 a {
  color: gainsboro;
}
.home-article .block-time {
  color: #777777;
}

.blog-post, .post-body {
  background: #222222;
  color: #cccccc;
}

.post-body p code, .post-body li code, .post-body dd code, .post-body td code {
  background: #272727;
}
.post-body hr:before {
  background: #ccc;
  background: -webkit-linear-gradient(left, transparent, rgba(230, 230, 230, 0.3), transparent);
}
.post-body .cp_embed_iframe {
  background: #fff;
}

.main-page-header {
  color: #cccccc;
}

.podcast-episode {
  background-color: black;
  border-color: black;
}
.podcast-grid ul {
  list-style: none;
}
.podcast-player {
  background: none;
}

.widget_wpsc_shopping_cart {
  background: #444;
  color: gainsboro;
}

figure {
  background: #28282D;
  border-top-color: #45454d;
  border-bottom-color: #45454d;
}

.code-type-selector span, .theme-dropdown label {
  color: #cccccc;
}

.code-type-selector span:hover, .code-type-selector span.active {
  color: gainsboro;
}
.code-type-selector span:hover {
  text-decoration: underline;
}

.explore-tabs a, .explore-tabs span {
  color: #cccccc;
}
.explore-tabs a.active, .explore-tabs a:hover, .explore-tabs a:focus {
  color: #fff !important;
}

.share-buttons .button,
.share-actions .button,
.send-to-phone h5,
.mobile-sms-left {
  color: gainsboro;
}

.share-buttons .button svg, .share-actions .button svg {
  fill: gainsboro;
}
.share-buttons .button:hover, .share-buttons .button:focus, .share-actions .button:hover, .share-actions .button:focus {
  background-color: gainsboro;
  color: #444;
}
.share-buttons .button:hover svg, .share-buttons .button:focus svg, .share-actions .button:hover svg, .share-actions .button:focus svg {
  fill: #444;
}
.share-static a {
  color: #cccccc;
}
.share-static a:hover, .share-static a:focus {
  background-color: gainsboro;
  color: #444;
}

.action-button:hover, .action-button:active {
  background-color: #444;
}

.assets-area {
  background-color: #444;
  color: #aaa;
}
.assets-area header h1, .assets-area .no-files {
  color: #cccccc;
}
.assets-all-files li:nth-child(odd) {
  background-color: #555555;
}
.assets-all-files li a {
  color: #cccccc;
}
.assets-all-files li:hover a, .assets-all-files li:active a {
  color: gainsboro;
  background-color: #5f5f5f;
}
.assets-area-close-button:hover svg {
  fill: gainsboro;
}

.editor-layout-buttons {
  border-color: gainsboro;
}
.editor-layout-buttons > a.active, .editor-layout-buttons > a:hover {
  background-color: #cccccc;
}
.editor-layout-buttons > a.active svg, .editor-layout-buttons > a:hover svg {
  fill: #444;
}
.editor-layout-buttons > a svg {
  fill: #cccccc;
}

.theme-selector {
  background-color: #555555;
}
.theme-action-buttons button {
  background-color: #888888;
}

.public-private-wrap {
  background-color: #444;
}

#meta-tag-insert {
  color: gainsboro;
}

.drawer-close-button {
  cursor: pointer;
}

.mini-title {
  color: #aaa;
}

h1 a,
h2 a,
h3 a,
h4 a,
h5 a,
h6 a {
  color: gainsboro;
}
h1 a:hover, h1 a:focus,
h2 a:hover,
h2 a:focus,
h3 a:hover,
h3 a:focus,
h4 a:hover,
h4 a:focus,
h5 a:hover,
h5 a:focus,
h6 a:hover,
h6 a:focus {
  color: #cccccc;
}

.info-table td,
.wpsc_checkout_table td,
.info-table th,
.wpsc_checkout_table th {
  border-color: #555555;
}

.comment-button:hover, .comment-button:focus {
  color: #fff;
}

.checkbox-row #pen-details-template {
  transform: translateY(2px);
}

.view-switcher,
.sharing-panel,
.export-panel {
  background-color: #444;
  color: #ccc;
}

hr, .sep {
  border-color: #ccc !important;
}

.send-to-phone h5 svg {
  fill: #aaa;
}

.inline-q {
  background: transparent;
}

.file-delete svg {
  fill: #ddd;
}

.embed-builder {
  background-color: #444;
  color: #ccc;
}

.embed-builder h1 {
  color: #ddd;
}

.copy-paste-title {
  color: #999;
}

.embed-options {
  background: -webkit-gradient(linear, left top, left bottom, from(#555), to(#444));
  background: -webkit-linear-gradient(#555, #444);
  background: linear-gradient(#555, #444);
}

#disqus_thread {
  background: #fff;
  padding: 20px;
}

.profile-username-area .badge {
  color: gainsboro !important;
  padding: 3px 5px;
  border: 1px solid gainsboro;
  background: none;
}
}
