css:
@-moz-document domain("juqster.com") {
/**
	* This is an ugly hack combining 2 other styles.
	* I wrote NONE of the code in here. I only smashed 
	* them together until I ended up with something that I, personally, liked.
	*
	* The Two Projects I Used were:
   * EGSA's Pretty Jukebox, By: Deku - https://userstyles.org/styles/119288/egsa-pretty-jukebox
	* JUQSTER JUQQSY, By: Mullins - https://userstyles.org/styles/119224/juqster-juqqsy
   */
/*version*/

.navbar-brand:hover::after {
   content: 'MrKeith\'s Juqster v0.001';
}
/*nav resize*/

.navbar-brand:hover::after {
   font-size: 11px;
   position: relative;
   top: -5px;
   left: 5px;
   background: rgba(0, 0, 20, .2);
   padding: 2px 5px;
   border-radius: 2px;
}
.navbar {
   min-height: 25px;
   margin-bottom: 5px;
   border: 0;
   opacity: .3;
   transition: .15s;
   z-index: 1000;
}
.navbar:hover,
.navbar:focus,
.navbar:active {
   opacity: 1;
}
.navbar-default {
   background: linear-gradient(#3498db, #2181C0);
   border-color: transparent;
   border-radius: 0;
   border-bottom: 1px solid #A2CFF0;
}
@media (min-width: 1200px) {
   .container {
      width: 100%;
   }
}
@media (min-width: 768px) {
   .navbar>.container .navbar-brand, .navbar>.container-fluid .navbar-brand {
      margin-left: 0;
   }
}
.navbar-brand {
   height: auto;
   padding: 2px 0 0 0;
   font-size: 15px;
   line-height: normal;
}
.navbar-default .navbar-brand {
   padding-bottom: 0;
   margin-top: 0;
}
.navbar-brand img {
   height: 32px;
   width: 75px;
}
.navbar-default .navbar-right > li > a {
   font-size: 16px;
   padding-top: 9px;
}
@media (min-width: 768px) {
   .navbar-nav>li>a {
      padding-top: 0;
      padding-bottom: 0;
   }
}
.navbar-default .navbar-right > li img.userProfile {
   max-height: 25px;
   margin-top: -6px;
}
.share-button.navbar-right.nav.navbar-btn.sharer-0 {
   height: 20px;
}
.share-button.navbar-right.nav.navbar-btn.sharer-0 label {
   font-size: 16px;
   padding: 0 10px;
   border-radius: 0;
   transition: .15s;
}
body .navbar-default .share-button label span {
   font-size: 16px !important;
}
/*fix page height*/

@media (min-width: 1900px) {
   .Override.container {
      height: 95%;
   }
}
@media (min-width: 1200px) and (max-width: 1899px) {
   .Override.container {
      height: 93%;
   }
}
/*bigger chatbox*/

body hubinfo .hub-info {
   border: 0;
   background: #676A6F;
}
@media (min-height: 900px) {
   .hub-info {
      min-height: 0;
   }
}
.hub-info .hub-title {
   font-size: 20px;
   font-weight: 600;
   padding: 5px 10px;
   background: rgba(0, 0, 20, .7);
   color: #fff;
   border-radius: 0 10px 10px 0;
   line-height: normal;
   display: block;
}
.hub-info .hub-tags {
   padding-left: 10px;
}
.hub-info .hub-tags .badge {
   font-size: 11px;
   background: #406B9E;
   padding: 5px 10px;
   border-radius: 0;
}
.hub-info .hub-description {
   margin-top: 5px;
   background: rgba(0, 0, 20, .7);
}
.hub-info .col-xlg-9 {
   width: auto;
   display: block;
   max-height: 55px;
   overflow-y: auto;
   color: #DBECF9;
   padding: 5px 10px;
   font-size: 12px;
}
.hub-info .col-xlg-9 a {
   color: #A4D1F7;
   transition: .15s;
}
.hub-info .col-xlg-9 a:hover {
   color: #71B1F9;
   text-decoration: none;
}
/*chat*/

body chat {
   font-family: "Segoe UI", "Tahoma", "Trebuchet MS", Arial;
   font-size: 13px;
   line-height: 14px;
}
.chat li {
   margin-bottom: 2px;
   margin-left: 7px;
   margin-right: 7px;
   border-bottom: 0;
   background: linear-gradient(rgba(230, 230, 255, .09), rgba(230, 230, 255, .06));
   padding: 5px;
}
body chat .comments-container .chat .relative-time,
body chat .comments-container .chat .song-label {
   margin-top: -3px;
   font-size: 10px;
   margin-right: 0px;
}
.chat li .song-label,
.chat li .relative-time {
   line-height: 10px;
}
.chat li:hover .song-label,
.chat li:active .song-label {
   max-height: 15px;
   overflow: visible;
}
.chat li .chat-img {
   margin-right: 5px;
}
.chat li .chat-img img {
   height: 32px;
   width: 32px;
}
.chat li .chat-body {
   margin-left: 37px;
}
.chat li .chat-body .header {
   margin-bottom: 2px;
}
.chat li .chat-body .header .primary-font {
   font-size: 13px;
   color: #A4D1F7;
   font-weight: 600;
}
/*image border*/

.img-circle {
   border-radius: 0px;
}
/*************/
/* DEKU MODS */
/*************/
/* Remove useless scrollbar */

#jukebox {
   overflow: hidden;
}
/* Fixes hidden scrollbar */

@media (min-width: 768px) {
   #jukebox #jukeboxContainer .player-and-playlist-container {
      padding-right: 6px;
   }
   @media (-webkit-min-device-pixel-ratio: 0) {
      #jukebox #jukeboxContainer .player-and-playlist-container:hover {
         padding-right: 0;
      }
   }
}
/* Give the search bar some space */

#jukebox #jukeboxContainer .search-container {
   height: 10%;
   margin-bottom: 5px;
}
/* Video player */

player {
   display: flex;
   flex-flow: row nowrap;
   height: 12vw;
   padding: 10px;
   margin-bottom: 20px;
}
/* Video */

.embed-responsive {
   padding: 0 !important;
   margin: 0 !important;
   height: 100%;
   width: 14vw;
}
/* Song info*/

.currently-playing-song {
   margin: 0;
   width: 70%;
   max-width: 100%;
}
/* Hidden video thumbnail */

.currently-playing-song .song-img-container {
   display: none;
}
/* Details */

.currently-playing-song > div.col-xlg-10 {
   width: 100%;
}
.currently-playing-song > div.col-xlg-10 > div {
   margin-bottom: 5px;
}
.currently-playing-song > div.col-xlg-10 > div:nth-child(1) {
   display: flex;
   flex-flow: column nowrap;
}
/* Song title */

.currently-playing-song > div.col-xlg-10 > div:nth-child(1) > div {
   width: 100%;
}
/* Save (heart) and Skip buttons */

.currently-playing-song > div.col-xlg-10 .badge {
   width: 2vw;
   height: 2vw;
}
.currently-playing-song > div.col-xlg-10 .badge i {
   font-size: .9em;
   line-height: 1;
}
.currently-playing-song > div.col-xlg-10 .remove-song {
   visibility: visible;
   opacity: 1;
}
/* Volume control */

.currently-playing-song .volume,
.currently-playing-song:hover .volume,
.currently-playing-song:active .volume {
   right: auto;
}
.currently-playing-song #slider,
.currently-playing-song:hover #slider,
.currently-playing-song:active #slider {
   right: 20px;
}
/* Same color as the timer */

.added-by {
   color: #7e7c7c !important;
}
/*participants*/

participants {
   font-size: 13px;
}
.participants-container {
   padding-bottom: 40px;
}
.participants-container img {
   width: 32px;
   height: 32px;
   transition: .15s;
   float: none;
   margin-left: 0;
}
.participants-container .participant-picture-container {
   width: auto;
   padding-left: 0;
}
.participants-container .col-md-6.col-xs-7 {
   width: auto;
   padding-left: 10px;
}
.participants-container .row:hover img {
   width: 96px;
   height: 96px;
}
.participants-container .row {
   background: rgba(200, 200, 255, .06);
   padding: 0;
   cursor: pointer;
   margin-top: 2px;
   margin-left: 5px;
}
/*player*/

body jukebox .player-and-playlist-container playlist .playlistContainer .item-odd {
   background: none !important;
}
playlist .row.item {
   border-bottom: 1px dotted rgba(220, 220, 255, .2);
   padding-bottom: 5px;
   margin-top: 5px;
}
.currently-playing-song .song-img-container>div {
   border-radius: 0;
}
playlist .row.item .left-panel {
   width: 13%;
}
playlist .playlistContainer .row.item {
   display: block;
}
playlist .right-panel {
   width: 85%;
   padding: 0;
   float: right;
   margin-right: 10px;
}
playlist .right-panel .row {
   margin: 0;
}
playlist .right-panel .row .col-md-12 {
   padding: 0;
}
playlist .right-panel .row:nth-child(1) {
   display: table;
   padding-top: 5px;
   width: 50%;
}
playlist .right-panel .row:nth-child(2) {
   position: absolute;
   right: 0;
   top: 5px;
}
playlist .right-panel .row:nth-child(3) {
   display: table;
}
playlist .col-md-3.col-xs-4 {
   width: auto;
   display: inline-block;
   padding: 5px 5px 0 0;
}
playlist .item a.link-to-song {
   margin-left: 2px;
}
playlist .item .song-duration {
   font-size: 13px;
}
playlist .col-md-9.col-xs-8 {
   width: auto;
   display: inline-block;
   padding: 0;
}
playlist .row.item .song-name {
   margin-bottom: 0;
   font-size: 15px;
   word-wrap: break-word;
}
playlist .row .col-md-12 p {
   font-size: 13px;
   line-height: normal;
}
playlist .item span {
   margin-right: 5px;
   padding: 6px 7px;
   border-radius: 0 !important;
}
.song-duration {
   font-family: "Segoe UI", "Tahoma", "Trebuchet MS", Arial;
   font-size: 18px;
}
@media (min-width: 767px) {
   body #jukebox {
      border-right: 1px dotted #204559 !important;
      border-left: 1px dotted #204559 !important;
   }
}
.playlistContainer {
   border-top: 0;
}
.currently-playing-song {
   background: #1A191C;
   padding: 5px 10px;
}
/*volume always visible*/

.currently-playing-song:hover #slider,
.currently-playing-song:active #slider,
.currently-playing-song #slider,
.currently-playing-song #slider {
   right: 20px;
}
.currently-playing-song:hover .volume,
.currently-playing-song:active .volume,
.currently-playing-song .volume,
.currently-playing-song .volume {
   right: 110px;
}
/*options*/
/*participants login notification*/

@-webkit-keyframes login {
   from {
      background: #2282C2;
   }
   to {
      background: rgba(200, 200, 255, .06);
   }
}
@keyframes login {
   from {
      background: linear-gradient(to right, #2282C2 50%, rgba(200, 200, 255, .06) 50%);
      background-size: 200% 100%;
      background-position: left bottom;
   }
   to {
      background: linear-gradient(to right, #2282C2 50%, rgba(200, 200, 255, .06) 50%);
      background-size: 200% 100%;
      background-position: right bottom;
   }
}
body .participants-container .row {
   -webkit-animation-name: login;
   -webkit-animation-duration: 66s;
   animation-name: login;
   animation-duration: 66s;
}
}
