css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  body {
    background: url("http://i.imgur.com/Ra0UaLd.jpg") !important;
    background-size: cover;
  }
  
  .header {
    background: rgba(13, 17, 29, 0.65);
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.4);
  }
  
  .userbar .username {
    text-shadow: 1px 1px 0px #0D111D, 1px -1px 0px #0D111D, -1px 1px 0px #0D111D, -1px -1px 0px #0D111D;
  }
  
  .userbar button.icon {
    border: 1px solid #0D111D;
    background: rgba(13, 17, 29, 0.65);
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
    border-radius: 3px;
  }
  
  .userbar button.icon:hover {
    background: rgba(13, 17, 29, 0.8);
  }
  
  .maintabbarbottom {
    border: 1px solid #5C1116;
    background: #87241F;
  }
  
  .button.subtle-notifying {
    color: #5C1116 !important;
    text-shadow: none !important;
    border: 1px solid #5C1116 !important;
  }
  
  .tabbar a.button.notifying {
    background: #FFCFBC;
    border-color: #8F3031;
    color: #7C201F;
    text-shadow: none;
  }
  
  .tabbar a.button.notifying:hover {
    background: #F6BDA4;
    border-color: #8F3031;
    color: #7C201F;
    text-shadow: none;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border: 1px solid #5C1116;
    background: #87241F;
    text-shadow: 0px -1px 0px #5C1116;
    color: #EEE !important;
    width: 125px;
  }
  
  a.button i, a.button span {
    color: #EEE;
  }
  
  .tabbar a.button {
    background: #A84740;
    border: 1px solid #5C1116;
    margin-left: 5px;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    width: 85px;
    text-shadow: 0px -1px 0px #5C1116;
    color: #EEE !important;
  }
  
  .tabbar a.button:hover {
    background: #C1554D;
    text-shadow: 0px -1px 0px #5C1116;
    color: #EEE;
    border: 1px solid #5C1116;
  }
  
  .tabbar .closebutton {
    color: #DDD;
  }
  
  .tabbar .closebutton:hover {
    color: #5C1116;
  }
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
  }
  
  .tabbar li:first-child a.button {
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
  }
  
  .ps-room.ps-room-light {
    background: transparent;
    border-color: #0D111D;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(13, 17, 29, 0.65) url("http://i.imgur.com/c75P9wF.png?1") bottom right no-repeat;
    color: #EDF6FF;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .roomlist a.ilink {
    border: 1px solid #0D111D;
    background: transparent linear-gradient(to bottom, #2D3B48, #1D2A33) repeat scroll 0% 0%;
    color: #FFF;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.3) inset;
    border-radius: 3px;
    text-shadow: 0px -1px 0px #0F1923;
  }
  
  .roomlist a.ilink:hover {
    border: 1px solid #0D111D;
    background: transparent linear-gradient(to bottom, #21303F, #15222C) repeat scroll 0% 0%;
    color: #FFF;
  }
  
  .roomcounters button {
    border: 1px solid #0D111D;
    color: #FFF;
    background: rgba(13, 17, 29, 0.5);
    text-shadow: 0px -1px 0px #0F1923;
    box-shadow: none;
  }
  
  .roomcounters button:hover {
    background: rgba(13, 17, 29, 0.75);
    border: 1px solid #0D111D;
    text-shadow: 0px -1px 0px #0F1923;
    color: #FFF;
  }
  
  .roomlist h2 {
    text-shadow: 0px -1px 0px #0F1923;
    color: #FFF;
  }
  
  .menugroup {
    border: 1px solid #0D111D;
    background: rgba(13, 17, 29, 0.5);
    text-shadow: 0px -1px 0px #0F1923;
    color: #FFF;
  }
  
  .userdetails strong a[href="//pokemonshowdown.com/users/creaturephil"] {
    background: #000 !important;
    padding-right: 40px;
  }
  
  .menugroup .button {
    border: 1px solid #0D111D !important;
    background: transparent linear-gradient(to bottom, #2D3B48, #1D2A33) repeat scroll 0% 0% !important;
    color: #FFF;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.3) inset;
    border-radius: 3px;
  }
  
  .menugroup .button:hover {
    border: 1px solid #0D111D !important;
    background: transparent linear-gradient(to bottom, #21303F, #15222C) repeat scroll 0% 0% !important;
    color: #FFF;
  }
  
  .select {
    border: 1px solid #0D111D;
    background: transparent linear-gradient(to bottom, #2D3B48, #1D2A33) repeat scroll 0% 0%;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.3) inset;
    color: #FFF;
    text-shadow: 0px -1px 0px #0F1923;
    border-radius: 3px;
  }
  
  .select:hover {
    border: 1px solid #0D111D;
    background: transparent linear-gradient(to bottom, #21303F, #15222C) repeat scroll 0% 0%;
    color: #FFF;
  }
  
  .select::after {
    color: #FFF;
  }
  
  .select:disabled {
    border: 1px solid #0D111D;
    color: #FFF !important;
    background: rgba(13, 17, 29, 0.5);
  }
  
  .userlist {
    background: rgba(13, 17, 29, 0.65);
    border-color: #0D111D;
    color: #FFF;
    text-shadow: 0px -1px 0px #000;
  }
  
  .userlist li {
    border-bottom-color: #0D111D;
    background: transparent;
  }
  
  .userlist li em.group {
    color: #0D111D;
  }
  
  .userlist li button {
    background: rgba(255, 255, 255, 0.7);
  }
  
  .userlist-minimized .userlist-count:hover, .userlist-maximized .userlist-count:hover {
    background: rgba(13, 17, 29, 0.75);
  }
  
  .infobox {
    border-color: #0D111D;
  }
  
  .infobox a.ilink {
    color: #384857 !important;
  }
  
  .infobox a.ilink:hover {
    color: #25323B !important;
  }
  
  .chat-log {
    background: rgba(245, 245, 245, 0.8) url("http://i.imgur.com/koWRDto.png?2") bottom right no-repeat;
  }
  
  .chat-log a.ilink {
    color: #501215;
  }
  
  .chat-log a.ilink:hover {
    color: #430915;
  }
  
  .chat-log-add {
    border-top-color: #0D111D;
    background: rgba(13, 17, 29, 0.65);
  }
  
  .chat-log-add .chatbox label, .battle-log-add .chatbox label {
    text-shadow: 0px 0px 3px #0D111D;
  }
  
  .textbox, .textbox:hover {
    background: #1C233A;
    box-shadow: 0px 0px 3px #0D111D;
    border-color: #0D111D;
    color: #E8F2FB;
  }
  
  .textbox:focus {
    box-shadow: none;
    border-color: #0D111D;
    background: #1C233A;
  }
  
  .textbox::-moz-selection {
    background: #550C15 !important;
    color: #FFF !important;
  }
  
  .ps-popup {
    background: #D9C8B6 url("http://i.imgur.com/Y7BNTCw.png?2") bottom right no-repeat;
    color: #000;
    border: 1px solid #7C3C21;
  }
  
  .popupmenu h3 {
    color: #7C3C21;
  }
  
  .popupmenu button.sel {
    border: 1px solid #7C3C21;
  }
  
  .popupmenu button:hover, .popupmenu button.sel:hover {
    border: 1px solid #7C3C21;
    background: rgba(255, 255, 255, 0.25);
  }
  
  .credits h2, .credits ul {
    color: #0D111D;
  }
  
  a.subtle, .credits ul li p {
    color: #303C4A;
  }
  
  a.subtle:hover {
    color: #242E39;
  }
  
  .pm-window h3.pm-notifying, .pm-window h3.pm-notifying:hover {
    background: #A84740;
    border: 1px solid #5C1116;
    text-shadow: 0px -1px 0px #5C1116;
    color: #EEE;
  }
  
  .pm-window h3 {
    background: rgba(13, 17, 29, 0.65);
    border-color: #0D111D;
    color: #E4EBF5 !important;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .pm-window h3:hover {
    color: #DFC5A4 !important;
  }
  
  .pm-window.focused h3, .pm-window.focused h3:hover {
    background: rgba(13, 17, 29, 0.8);
    border-color: #0D111D;
    color: #E4EBF5 !important;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .pm-window h3 small {
    color: #B4BBC3;
  }
  
  .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #DDD;
  }
  
  .pm-window h3 .closebutton:hover {
    color: #FFB6B6;
  }
  
  .pm-window h3 .minimizebutton:hover {
    color: #FFEA91;
  }
  
  .chat.mine {
    background: transparent;
  }
  
  .pm-log, .pm-window.focused .pm-log {
    background: url("http://i.imgur.com/KpRUrnS.png");
    border-color: #0D111D;
    color: #E4EBF5;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .pm-log a {
    color: #FFF !important;
  }
   
  .pm-log .chat small {
    color: #B4BBC3;
  }
  
  .pm-log-add {
    background: rgba(13, 17, 29, 0.65);
    border-color: #0D111D;
  }
  
  .pm-window.focused .pm-log-add {
    background: rgba(13, 17, 29, 0.8);
    border-color: #0D111D;
  }
  
  .chat code, .chat .spoiler:hover code, .chat .spoiler:active code, .chat .spoiler-shown code {
    text-shadow: none !important;
  }
  
  .tournament-title {
    background: rgba(13, 17, 29, 0.65);
    color: #EDF6FF;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .tournament-status, .tournament-toggle {
    background: rgba(13, 17, 29, 0.65);
    color: #EDF6FF;
    text-shadow: 0px -1px 0px #0D111D;
    border-color: #0D111D;
  }
  
  .tournament-tools {
    background: rgba(13, 17, 29, 0.65);
    border-color: #0D111D;
  }
  
  .tournament-box {
    border-color: #0D111D;
  }
  
  font[color="#088cc7"] {
    color: #953B3D !important;
  }
  
  .ps-room.ps-room-opaque {
    background: rgba(13, 17, 29, 0.65);
    color: #EDF6FF;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .ps-room .battle-controls {
    background: transparent;
  }
  
  .battle-log {
    background: transparent;
    border: none;
    color: #EDF6FF;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .battle-log-add {
    background: transparent;
    border: none;
  }
  
  .battle-log .chat em {
    color: #EDF6FF;
    text-shadow: 0px -1px 0px #0D111D;
  }
  
  .battle-log .message-throttle-notice, .battle-log .message-error {
    color: #B9FEFB;
    background: transparent;
    border: none;
    text-shadow: none;
  }
  
  .turn {
    border-width: 1px;
    font-weight: normal;
    text-shadow: none;
  }
  
  .statbar {
    position:absolute; 
    display:none; 
    width:200px;     
    height:23px;   
    background:url(http://i.imgur.com/x76Yv1K.png);    
    min-height:0px; 
    padding: 3px 0px 0px 32px;
  }

  .statbar strong {
    position:absolute; 
    left:1px; 
    top:-22px; 
    font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif;
    font-size:17px; 
    font-weight:normal; 
    display:block; 
    text-align:Center; 
    color:#FFF; 
    text-shadow:#000 1px 1px 0,#000 1px -1px 0,#000 -1px 1px 0,#000 -1px -1px 0;
  }
  
  .statbar strong small {
    font-size:17px; 
    font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif;
    color:#acc5d4;
    font-weight:normal;
  }

  .statbar .hpbar {
    position:relative; 
    border:1px solid #131313; 
    background:url(http://i.imgur.com/ByYA9T2.png); 
    height:6px; 
    margin:0 0 0 0; 
    width:151px; 
    border-radius:0px; 
    padding:0;
    top:3px;
  }

  .hptextborder {
    display:none !important;
  }

  .statbar .hpbar .hp {
    height:3px; 
    border-top:1px solid #007734; 
    background:#00bb51; 
    border-bottom:2px solid #007734; 
    border-radius:0px;
  }

  .statbar .hpbar .hp-yellow {
    background-color:#f5d538; 
    border-color:#be9f0a;
  }

  .statbar .hpbar .hp-red {
    background-color:#ee4928; 
    border-color:#a3260d;
  }

  .statbar .hpbar .prevhp {
    background:url(http://i.imgur.com/ByYA9T2.png);  
    height:6px; 
    border-radius:0px;
  }
  .statbar .hpbar .prevhp-yellow {
    background:url(http://i.imgur.com/ByYA9T2.png); 
  }

  .statbar .hpbar .prevhp-red {
    background-color:#facec5;
  }

  .hptext{
    margin-top:2px;
  }

  .statbar .hpbar .hptext {
    position:absolute; 
    color:#FFFFFF; 
    text-shadow:1px 0 black,-1px 0 black,0 1px black,0 -1px black; 
    font-size:9px; 
    width:32px; 
    height:12px; 
    top:-1px; 
    text-align:center;
  }

  .rstatbar .hpbar .hptext {
    width:42px; 
    height:16px; 
    background:url(http://i.imgur.com/LPDWEbx.png); 
    right:-47px; 
    top:-7px; 
    font-size:11px; 
    color:#49c39f; 
    border-radius:0 4px 4px 0;
  }

  .lstatbar .hpbar .hptext {
    width:40px; 
    height:16px; 
    background:url(http://i.imgur.com/LPDWEbx.png); 
    left:155px; 
    top:-7px; 
    font-size:11px; 
    color:#49c39f; 
    border-radius:4px 0 0 4px; 
    padding-left:2px;
  }

  .statbar .status {
    margin-top:15px; 
    min-height:10px; 
    font-size:7pt;
  }

  .statbar span.frz {
    position:absolute; 
    left:-107px; 
    top:-5.5px;  
    font-size:0px;     
    background:url(http://i.imgur.com/L7UTW2y.png); 
    padding:10px 36px; 
    border:0; 
    border-radius:3px;
  } 

  .statbar span.frz:hover {    
    transition:All 1.6116s ease;   
    transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);    
  }    

  .statbar span.slp {
    position:absolute; 
    left:-107px; 
    top:-5.5px; 
    font-size:0px;     
    background:url(http://i.imgur.com/D3VqIVw.png); 
    padding:10px 36px; 
    border:0; 
    border-radius:3px;
  }

  .statbar span.slp:hover {    
    transition:All 1.6116s ease;    
    transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);    
  }    

  .statbar span.par {
    position:absolute; 
    left:-107px; 
    top:-5.5px;  
    font-size:0px;     
    background:url(http://i.imgur.com/4XrzuuB.png); 
    padding:10px 36px; 
    border:0; 
    border-radius:3px;
  } 

  .statbar span.par:hover {   
    transition:All 1.6116s ease;    
    transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);    
  }

  .statbar span.brn {
    position:absolute; 
    left:-107px; 
    top:-5.5px; 
    font-size:0px; 
    background:url(http://i.imgur.com/4F0lebL.png); 
    padding:10px 36px; 
    border:0; 
    border-radius:3px;
  } 

  .statbar span.brn:hover {
    transition:All 1.6116s ease;    
    transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px); 
  }    

  .statbar span.psn {
    position:absolute; 
    left:-107px; 
    top:-5.5px; 
    font-size:0px;   
    background:url(http://i.imgur.com/4wrWKR0.png); 
    padding:10px 36px; 
    border:0; 
    border-radius:3px;
  }    

  .statbar span.psn:hover {   
    transition:All 1.6116s ease;    
    transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);
  }

  .statbar span.bad {
    background:#e74c3c; 
    color:#ecf0f1; 
    border-color:#c0392b;
  }

  .statbar span.good {
    background:#27ae60; 
    color:#ecf0f1; 
    border-color:#067A37;
  }
  .statbar span.neutral {
    background:#7f8c8d; 
    color:#ecf0f1; 
    border:solid #2c3e50;
  }

  .pfx-panel {
    background:#333;
  }
  
  #tooltipwrapper {
    position: absolute;
    top: 400px;
    left: 100px;
    text-align: left;
    color: #311A44;
  }

  #tooltipwrapper .tooltipinner {
    position: relative;
  }

  #tooltipwrapper .tooltip {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 300px;
    border: 1px solid #2B378B;
    background-color: #92E6FE !important;
    border-radius: 2px;
    z-index: 9999;
  }

  #tooltipwrapper .tooltip h2 {
    padding: 2px 4px;
    margin: 0;
    border-bottom: 1px solid #2B378B;
    font-size: 10pt;
  }

  #tooltipwrapper .tooltip h2 small {
    font-weight: normal;
  }

  #tooltipwrapper .tooltip p {
    padding: 2px 4px;
    margin: 0;
    font-size: 8pt;
  }

  #tooltipwrapper .tooltip p.section {
    border-top: 1px solid #2B378B;
  }
  
  .battle-log .chat strong {
    color: #FC7885;
  }
  
  em[style="color:#445566;display:block;"] {
    color: #FC7885 !important;
  }
  
  .battle-log .rated strong {
    background: #561018;
    border: 1px solid #470B15;
    text-shadow: 0px -1px 0px #470B15;
  }
  
  .highlighted {
    background: #FFCFBC;
  }
  
  .challenge {
    background: #FFCFBC;
    border-color: #8F3031;
    color: #7C201F;
    text-shadow: none;
  }
  
  .challenge .buttonbar button, .challenge .select {
    background: #F3B89E;
    border: 1px solid #8F3031;
    color: #7C201F;
    text-shadow: none;
  }
  
  .challenge .select::after {
    color: #7C201F;
  }
  
  .challenge .select:disabled, .challenge .select:disabled:hover {
    background: #F3B89E;
    border: 1px solid #8F3031;
    color: #7C201F !important;
    text-shadow: none;
  }
  
  .challenge .buttonbar button:hover, .challenge .select:hover {
    background: #D7987D;
    border: 1px solid #8F3031;
    color: #4A0D0B;
    text-shadow: none;
  }
  
  .battle-log h2 {
    background: #92E6FE;
    border-color: #2B378B;
    color: #2B378B;
    text-shadow: none;
    text-align: right;
  }
  
  .rightbar, .leftbar {
    background: rgba(13, 17, 29, 0.65);
    border-color: #0D111D;
  }
  
  .battle {
    border-color: #0D111D;
  }
  
  .battle-controls .whatdo {
    color: #ECF4FF;
  }
  
  .switchmenu button {
    border: 1px solid #0D111D;
    background: transparent linear-gradient(to bottom, #2D3B48, #1D2A33) repeat scroll 0% 0%;
    color: #FFF;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.3) inset;
    border-radius: 3px;
    text-shadow: 0px -1px 0px #0F1923;
  }
  
  .switchmenu button:hover {
    border: 1px solid #0D111D;
    background: transparent linear-gradient(to bottom, #21303F, #15222C) repeat scroll 0% 0%;
    color: #FFF;
  }
  
  .switchmenu button .hpbar {
    border-color: #0D111D;
  }
  
  .switchmenu button.disabled, .switchmenu button:disabled {
    background: #1D2A33 !important;
    color: #EDF6FF !important;
    border: 1px solid #0D111D !important;
  }
  
  .switchselect button, .moveselect button {
    color: #0D111D;
  }
  
  .movemenu button.type-Electric {
		background: transparent linear-gradient(to bottom, #2D3B48, #1D2A33) repeat scroll 0% 0%;
		border-color: #FFFB95;
		color: #FFFB95;
		text-shadow: 0px 0px 2px #000;
		box-shadow: 0px 0px 2px #000;
	}
	
	.movemenu button.type-Electric:hover {
		background: transparent linear-gradient(to bottom, #21303F, #15222C) repeat scroll 0% 0%;
		border-color: #F9F348;
		color: #F9F348;
		text-shadow: 0px 0px 2px #000;
		box-shadow: 0px 0px 2px #000;
