css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("uonetplus-opiekun.vulcan.net.pl") {
  html {
    background: rgba(240,240,240,1);
  }
  body::after {
    background: none;
  }
  body::before {
    background: none;
  }
  body > header > nav ul:not(:last-of-type) * {
    display: none;
  }
  body, body > header::after, body > header::before, body > header > nav, body > header > a {
    max-width: 100rem !important;
    width: auto !important;
    min-width: 0 !important;
  }
  body > header * {
    width: 100%;
  }
  body > footer * {
    max-width: 100rem;
    width: auto;
  }
  body > ul {
    max-width: 88rem;
    width: 100%;
  }
  body > nav > ul {
    border-top-width: 1px;
  }
  label + input[type="text"], label + input[type="password"], label + select, label + textarea {
    max-width: 20rem;
    float: none;
  }
  label {
    width: auto;
    margin-right: 1rem;
  }
  fieldset select {
    width: auto !important;
    /*float: right;*/
  }
  fieldset label {
    width: auto !important;
  }
  table td div {
    padding-left: 3px !important;
    padding-right: 3px !important;
  }
  table tbody td span {
    font-size: inherit !important;
  }
  table th:nth-of-type(1):nth-last-of-type(7), table td:nth-of-type(1):nth-last-of-type(7), table th:nth-of-type(1):nth-last-of-type(8), table td:nth-of-type(1):nth-last-of-type(8), table th:nth-of-type(1):nth-last-of-type(9), table td:nth-of-type(1):nth-last-of-type(9) {
    width: 5%;
  }
  table thead th:not(:last-of-type), table tbody th:not(:last-of-type), table thead td:not(:last-of-type), table tbody td:not(:last-of-type)  {
    /*border: 2px solid black !important;*/
  }
  table thead th:last-of-type, table tbody th:last-of-type, table thead td:last-of-type, table tbody td:last-of-type {
    border-right: 1px solid #595965 !important;
  }
  table thead th, table tbody th, table thead td, table tbody td {
    border-style: solid !important;
    border-width: 1px !important;
    border-color: #595965 !important;
    border-right: none !important;
    border-bottom: none !important;
  }
  table {
    border: none;
  }
  table {
    border-bottom: 1px solid !important;
  }
  body > nav > ul > li a[open] {
    border-left: none;
  }
  a.button:hover, a.button-prev:hover, a.button-next:hover, a.button-down:hover, a.button-up:hover, a.button-delete:hover {
    text-decoration: none;
  }
}
