iframe[src*="sabavision"],
div.col-md-4.hidden-sm.hidden-ms.hidden-xs.r_padd,
.img-responsive[src*="adv"] {
	display: none!important;
}

div.col-md-2.pdl_news.hidden-sm.hidden-ms.hidden-xs {
	width: 0!important;
}

div.col-md-4.hidden-sm.hidden-ms.hidden-xs.r_padd {
	width: 0!important;
}

div.col-md-10.col-sm-12 {
	width: 100%!important;
}

.linear_news a.title5:hover,
.linear_news a.title9:hover,
.linear_news a.titlepic:hover {
	-webkit-box-shadow: 2px 2px 4px 3px rgba(119, 112, 119, 0.76);
	-moz-box-shadow: 2px 2px 4px 3px rgba(119, 112, 119, 0.76);
	box-shadow: 2px 2px 4px 3px rgba(119, 112, 119, 0.76);
	font-size: 0.88em;
	transition: all 0.1s ease-in-out;
	-webkit-transition: all 0.1s ease-in-out;
	-moz-transition: all 0.1s ease-in-out;
}

.linear_news a.title5:active,
.linear_news a.title9:active,
.linear_news a.titlepic:active {
	-webkit-box-shadow: 2px 2px 4px 3px rgba(119, 112, 119, 0.76) inset;
	-moz-box-shadow: 2px 2px 4px 3px rgba(119, 112, 119, 0.76) inset;
	box-shadow: 2px 2px 4px 3px rgba(119, 112, 119, 0.76) inset;
	font-size: 0.86em;
}
}
