css:
@-moz-document domain("se7ensins.com") {
.messageHeading {
    font-size: 13px;
    color: #FFF;
    font-weight: bold;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #e07900 0%,#e07900 100%);
    padding: 5px 10px;
    overflow: hidden;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    margin-bottom: 0px;
}
.message .messageInfo {
    background-color: rgb(19, 19, 19);
    padding: 2px;
    margin-left: 185px;
    border: 1px solid rgb(26, 26, 26);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    zoom: 1;
}

.button {
    font-style: oblique;
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 0px 5px;
    border: 1px solid rgb(224, 121, 0);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    text-align: center;
    outline: none;
    line-height: 26px;
    cursor: pointer;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -webkit-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    -moz-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    -khtml-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#E07900 100%);
    height: 28px;
}

.extraFooter {
    font-size: 12px;
    color: rgb(224, 121, 0);
    background-color: rgb(34, 34, 34);
    padding: 15px;
    border-top: 6px solid rgb(224, 121, 0);
    -webkit-border-top-left-radius: 0px;
    -moz-border-radius-topleft: 0px;
    -khtml-border-top-left-radius: 0px;
    border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    -moz-border-radius-topright: 0px;
    -khtml-border-top-right-radius: 0px;
    border-top-right-radius: 0px;
    -webkit-border-bottom-right-radius: 10px;
    -moz-border-radius-bottomright: 10px;
    -khtml-border-bottom-right-radius: 10px;
    border-bottom-right-radius: 10px;
    -webkit-border-bottom-left-radius: 10px;
    -moz-border-radius-bottomleft: 10px;
    -khtml-border-bottom-left-radius: 10px;
    border-bottom-left-radius: 10px;
    display: block;
    overflow: hidden;
}

.extraFooter h3 {
    font-size: 16px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(224, 121, 0);
    padding-bottom: 5px;
    margin: 0 auto 7px;
    font-weight: 700;
}

a:link, a:visited {
    color: rgb(224, 121, 0);
    text-decoration: none;
}

.footer a, .footer a:visited {
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(224, 121, 0);
    padding: 11px 5px;
    display: block;
}

.messageUserInfo {
    background-color: rgb(224, 121, 0);
    padding: 1px;
    border: 0px solid rgb(224, 121, 0);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    float: left;
    width: 160px;
}

.messageUserBlock h3.userText {
    background-color: rgb(43, 43, 43);
    padding: 8px 6px;
    border-top: 1px solid rgb(224, 121, 0);
    border-bottom: 1px solid rgb(224, 121, 0);
    text-align: center;
}

.breadcrumb .crust:hover a.crumb {
    color: rgb(43, 43, 43);
    background-color: rgb(22, 22, 22);
}

.navTabs {
    font-size: 13px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    background-color: rgb(26, 26, 26);
    padding: 0 25px;
    border-top: 6px solid rgb(26, 26, 26);
    border-bottom: 2px solid rgb(224, 121, 0);
    height: 40px;
}

html .navTabs .navTab.selected .navLink {
    position: relative;
    font-weight: bold;
    color: rgb(26, 26, 26);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#E07900 100%);
}

html .navTabs .navTab.PopupOpen .navLink {
    color: rgb(224, 121, 0);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #1A1A1A 0%,#1A1A1A 100%);
}

.Menu {
    font-size: 12px;
    border: 0px solid rgb(26, 26, 26);
    border-top: 5px solid rgb(43, 43, 43);
    overflow: hidden;
    -webkit-box-shadow: 0px 5px 5px rgba(0,0,0, 0.5);
    -moz-box-shadow: 0px 5px 5px rgba(0,0,0, 0.5);
    -khtml-box-shadow: 0px 5px 5px rgba(0,0,0, 0.5);
    box-shadow: 0px 5px 5px rgba(0,0,0, 0.5);
    min-width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
}

.navTabs .navTab.PopupOpen .navLink:hover, .navTabs .navTab.PopupClosed .navLink:hover {
-webkit-border-top-left-radius: 3px;
-moz-border-radius-topleft: 3px;
-khtml-border-top-left-radius: 3px;
border-top-left-radius: 3px;
-webkit-border-top-right-radius: 3px;
-moz-border-radius-topright: 3px;
-khtml-border-top-right-radius: 3px;
border-top-right-radius: 3px;
background: linear-gradient(to bottom,rgba(224, 121, 201, 0) 0%,rgba(224, 121, 201, 0) 100%);
}

.nodeList .categoryStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(234, 229, 229);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#E07900 100%);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    min-height: 6px;
}


.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(224, 121, 0);
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgb(224, 121, 0);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url('http://i.imgur.com/E4HhkIY.png');
    background-repeat: no-repeat;
    background-position: -36px 0;
}

.navTabs .navLink, .navTabs .SplitCtrl {
    color: rgb(241, 232, 0);
    display: block;
    float: left;
    text-align: center;
    outline: 0 none;
    height: 40px;
    line-height: 40px;
}

.subForumsPopup.Popup .PopupControl.PopupOpen {
    background-image: none;
    color: rgb(243, 234, 0) !important;
}

.XenBase .subForumsPopup .PopupOpen .dt {
    color: rgb(243, 234, 0);
}

.Popup .PopupOpen .arrowWidget:before {
    content: "\f077";
    color: rgb(221, 195, 2);
}

.Popup .arrowWidget:before {
    color: rgb(241, 232, 0);
    content: "\f078";
    display: block;
    font-family: FontAwesome;
    line-height: 13px;
    margin-left: 2px;
}

.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: rgb(224, 121, 0);
}

a.callToAction {
    padding: 0px;
    border: 1px solid rgb(224, 121, 0);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#E07900 100%);
    display: inline-block;
    line-height: 28px;
    -webkit-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    -moz-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    -khtml-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    outline: 0 none;
    height: 28px;
}

.XenBase .amsArticleViewItem .main .featured, .XenBase .sidebar .featuredNotice {
    background: #E07900;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#E07900 100%);
    color: #131313;
    border: 1px solid rgb(66,98,14);
    font-size: 12px;
    line-height: 1.4;
}

a.callToAction:hover span {
    background-color: rgb(224, 121, 0);
}

.xenOverlay .formOverlay .heading {
    font-weight: bold;
    font-size: 14px;
    color: rgb(247, 247, 247);
    background-color: rgb(104, 159, 16);
    padding: 7px 10px;
    margin-bottom: 10px;
    border: 1px solid rgb(31, 31, 31);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#D37200 100%);
}

.PageNav a.currentPage {
    color: rgb(19, 19, 19);
    background-color: rgb(224, 121, 0);
    border-color: rgb(224, 121, 0);
    position: relative;
}

.PageNav a:hover, .PageNav a:focus {
    color: rgb(26, 26, 26);
    text-decoration: none;
    background-color: rgb(211, 114, 0);
    border-color: rgb(224, 121, 0);
}

.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    color: rgb(215, 116, 0);
    text-decoration: none;
    background: url(http://www.se7ensins.com/rgba.php?r=0&g=0&b=0&a=0);
    background: rgba(0, 0, 0, 0);
    border-width: 0px;
    outline: 0;
}

.subHeading {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#E07900 100%);
}

.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    color: rgb(255, 255, 255);
    text-decoration: none;
    background-color: rgb(224, 121, 0);
}

.mast .subHeading, .navigationSideBar .subHeading, .navigationSideBar .heading {
    background: rgb(26, 26, 26);
    border-bottom: 3px solid rgb(224, 121, 0);
    -webkit-border-radius: 3px 3px 0px 0px;
    -moz-border-radius: 3px 3px 0px 0px;
    -khtml-border-radius: 3px 3px 0px 0px;
    border-radius: 3px 3px 0px 0px;
}

.XenBase .profilePage .textWithCount.subHeading .count {
    background-color: rgb(241, 232, 0);
    color: #131313;
    border-width: 0px;
    font-size: 12px;
    font-weight: bold;
    margin-top: -1px;
}

.textCtrl:focus, .textCtrl.Focus {
    color: rgb(224, 121, 0);
    border-color: rgb(46,46,46);
}

.statusEditorCounter {
    color: #E07900;
}

.tabs li:hover a, .tabs.noLinks li:hover {
    color: rgb(224, 121, 0);
    text-decoration: none;
    background-color: rgb(43, 43, 43);
}

.discussionList .sectionHeaders {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 1px 0px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07900 0%,#E07900 100%);

}

#logoBlock {
    background: url("http://i.imgur.com/L3HoW3j.png") no-repeat scroll 0% 0% rgb(26, 26, 26) !important;
}

  
.dark_postrating_positive {
    color: #E07900;
}

.node .tinyIcon {
    font-size: 17px;
    color: rgb(247, 248, 0);
    background-color: transparent;
    margin: 1px 4px;
    display: block;
    white-space: nowrap;
    opacity: 0.25;
    width: 12px;
    height: 16px;
}

.thread_view .threadAlerts dt {
    font-weight: bold;
    color: rgb(241, 232, 0);
    display: inline;
}

.message .newIndicator {
    font-weight: bold;
    font-size: 12px;
    color: rgb(19, 19, 19);
    padding: 1px 5px;
    margin: -5px -5px 5px 5px;
    border: 1px solid rgb(82, 125, 57);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    -webkit-border-top-right-radius: 0px;
    -moz-border-radius-topright: 0px;
    -khtml-border-top-right-radius: 0px;
    border-top-right-radius: 0px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #F1E800 0%,#D6E102 100%);
    display: block;
    float: right;
    position: relative;
    -webkit-box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    -moz-box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    -khtml-box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    margin-right: -22px !important;
    text-transform: uppercase;
}

html .message .publicControls .MultiQuoteControl.active:hover {
    background-color: rgb(224, 121, 0);
}

.message .messageMeta .control:hover {
    color: rgb(234, 234, 234);
    text-decoration: none;
    background-color: rgb(224, 121, 0);
}
}
