css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("giessener-allgemeine.de") {
   
  #container {
    margin:0 auto;
    float:unset;
    width:1200px;
  }
  #header {
    width:1200px;
    height:auto;
  }
  #logo { 
    clear: left;
    padding:0 0 10px 50px;
    float:none;
  }
  #menue {
    width:1010px;
  }
  #submenue {
    width:1184px;
    height:auto;
    float:none;
    clear: both;
    padding-top:5px;
    display: inline-block 
  }
  #submenue a {
    padding-top:5px
  }
  .ssh {
    width:auto;
    padding:10px 0 0px 13px
  }
  #left {
    width:825px;
    margin-right:10px;
    border:1px solid #ccc;
    border-radius: 10px;
    padding-bottom: 10px;
    box-shadow: 0px 0px 5px #ccc;
  }
  #container::after {
    content: ".";
    display: block;
    height: 0;
    clear: both;
    visibility: hidden;
  }
 
  .artikel {
    border-top:none;
    width:auto;
  }

  .artikel2 {
    width:auto;
  }
  .line_region {
    width:auto
  }

  .medienleiste {
    background-image: none;
    border:1px solid #ccc;
    border-radius: 5px;
    padding:5px 25px
  }
  .medienleiste > div:first-child,
  .medienleiste > div:last-child {
    display: none
  }
  .medienleiste .medienleiste_oneline_content,
  .jcarousel-container jcarousel-container-horizontal,
  .jcarousel-skin-leiste .jcarousel-clip-horizontal {
    width:710px;
  }
  #pagenavi {
    float:none;
    width:auto;
    font-size:0.9em
  }
  #right {
    width:auto;
    padding:10px 20px;
    border:1px solid #ccc;
    border-radius: 10px;
    width:auto;
    box-shadow: 0px 0px 5px #ccc;
  }
  #right .servicebox {
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 10px;
  }
  #right .serviceboxhead {
    font-size:1.2em;
    height:auto;
    padding:0 10px;
    width:270px;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
  }
  #right .serviceboxcontent {
    background-image:none;
    padding-bottom:10px
  }
  #right .serviceboxfoot2 {
   display:none
  }
  #right .serviceboxhead > img,
  #right .serviceboxfoot2 > img {
    display:none
  }
  .LinkArrow2 {
    font-size:0.8em;
    background-position: 3px center;
  }
  .servicebox_zweispaltig_right {
    font-size:1.1em;
  }
  .servicebox_werbung2 iframe {
    width:288px !important;;
  }
  .serviceboxcontent .news_text a,
  .serviceboxcontent .news_time {
    font-size:1.1em
  }
  
  #footermenue,
  #copyright {
    width:1190px;
    margin:0 auto;
  }
  /* artilce pages  */
  .galpicturefulltop_white,
  .galpicturefullbottom_white {
    display:none
  }
  .galpicturefullcontent_white {
    border:1px solid #ccc;
    border-radius: 10px;
    width:830px
  }
  .galpicturefullcontent_white .comment_grey {
    width:810px
  }
  .commentborder {
    width:810px
  }
  
  /* font-sizes */
  .line_region {
    font-size:0.7em;
  }
  #submenue a,
  #submenue a.aktiv {
    font-size: 1em;
    line-height: 1.1em
  }
  .ssh {
     font-size:0.9em
  }
  h2 { font-size:1.2em; }
  h3 { font-size:1em; }
  .artikel span { font-size:1em }
  .artikel2 span { font-size:1em }
  .medienleiste {
    font-family: Verdana,Helvetica,Arial,sans-serif;
  }
  .medienleiste a { font-size:1.1em }
  .box_links_artikel a { font-size:0.8em; line-height: 1.3em }
  .fettvorspann { font-size: 0.8em; }
  .fettvorspann > p { font-size: 1.1em; line-height:1.2em }
  .galpicturefullcontent_white .comment_grey {
    font-size:0.9em;
    line-height: 1.2em
  }

}
