css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
popupset{
-moz-appearance: none !important;
}
popupset menupopup{
-moz-appearance: none !important;
background-color:#FFF !important;
border:1px solid #D4D4D5 !important;
padding-top:4px;
padding-bottom:4px;
box-shadow:none;
}
popupset menupopup menu[_moz-menuactive="true"],
popupset menupopup menuitem[_moz-menuactive="true"]{
background-color:#ECECED !important;
-moz-appearance: none !important;
}
popupset menupopup menu,
popupset menupopup menuitem{

border:1px solid #FFF !important;
background-color:#FFF !important;
-moz-appearance: none !important;
margin-left:0px;
margin-right:0px;
}
popupset menupopup menuitem .menu-text{
margin-left:0px !important;
padding-left:12px !important;
-moz-appearance: none !important;
}
popupset menupopup menuseparator{
-moz-appearance: none !important;
border-top:1px solid #D4D4D5 !important;
padding:0px;
margin-top:2px;
margin-bottom:2px;
}
