css:
@-moz-document url-prefix(http://www.youtube)
,url-prefix(http://il.youtube)
,url-prefix(https://www.youtube)
{

div[id="placeholder-player"]{background-color:#000000!important;color:#000000!important}
div[class^="yt-consent yt-consent-banner"]{display:none!important}

span[aria-label="Subscribe"]{color:#FFFFFF!important;font-weight:bold}
span[aria-label="Unsubscribe"]{color:#FFFFFF!important;font-weight:bold}
a[id="upload-btn"]{padding-left:24px!important}

button[href*="accounts.google.com/ServiceLogin?"]{color:white!important}

span[class="yt-uix-button-content"]{color:#9E9E9E}

div[style*="background-color: rgb(244, 244, 244)"]{display:none!important}

*[class^="watch-title"]{color:#D02022!important}

a[href^="/channel"]{font-size:14px!important}

a[href*="action_handle_signin"]{background-color:#CFCFCF!important;border:none!important;width:80px!important}


span[aria-describedby^="description"]{font-size:16px!important}

span[class="content-region"]{BACKGROUND:url(http://kmext.sourceforge.net/media/ytplus3-hov.png)no-repeat!important;display:none!important}

span[class="footer-logo-icon yt-sprite"]{width:136px!important;height:40px!important;BACKGROUND:url(http://kmext.sourceforge.net/media/ytplus3-hov.png)no-repeat!important}

span[class="logo masthead-logo-renderer-logo yt-sprite"]{width:136px!important;height:40px!important;BACKGROUND:url(http://kmext.sourceforge.net/media/ytplus3.png)no-repeat!important;margin-top:-6px!important}

span[class="logo masthead-logo-renderer-logo yt-sprite"]:hover{width:136px!important;height:40px!important;BACKGROUND:url(http://kmext.sourceforge.net/media/ytplus3-hov.png)no-repeat!important}

*[classput^="yt-masthead-logo-"]{width:136px!important;height:40px!important;BACKGROUND:url(http://kmext.sourceforge.net/media/ytplus3.png)no-repeat!important;margin-top:2px!important}

*[classput^="yt-masthead-logo-"]:hover{width:136px!important;height:40px!important;BACKGROUND:url(http://kmext.sourceforge.net/media/ytplus3-hov.png)no-repeat!important}

img[class="logo doodle"]{visibility:hidden!important}


div[id="yt-masthead-container"]{border:none!important;background-color:#EFEFEF!important}
div[class="alerts-wrapper"]{display:none!important}
div[id="player-api"]{margin-top:0px!important}

div[id="yt-dialog-bg"]{display:none!important}
div[class="yt-dialog-bg"]{display:none!important}

button[href^="https://accounts.google.com/ServiceLogin"]{border-color:#D3D3D3!important;color:#FFFFFF!important;background-color:#F8F8F8!important}

a[id="upload-btn"]{width:90px!important}
button[href^="https://accounts.google.com/ServiceLogin"]{width:90px!important}






div[class*="yt-alert"]{display:none!important}

li,span,p,lu,h1,a:hover{text-decoration:none!important}
span[class="stat alt"]{background-color:#CE0F14!important;color:#FFFFFF!important;font-size:9px!important}
*[class="yt-badge-std"]{background-color:#CE0F14!important;color:#FFFFFF!important;font-size:9px!important;font-weight:bold!important;border:none!important}
*[class="yt-badge-std"]:hover{background-color:#A4202D!important}
*[id="default-language-top"]{display:none!important}
div[id^="default-language"]{display:none!important}
div[class="yt-uix-tooltip-tip-content"], div[id="yt-uix-tooltip1-content"]{background-color:#4A4A4A!important}
div[style^="margin: 0px 0px 5px"]{display:none!important}
img[src*="http://deturl.com/"]{display:none!important}
html>body>div>div>div>form>div>p{border:none!important}
html>body>div>div>div>div>form>div>p{border:none!important}
html>body>div>div>div>div>div>div>div>div>div>div[style^="padding"]{display:none!important}
div[id="msgFeaturePopup"]{display:none!important}

div[id="ticker-inner"]{margin-top:18px!important}
#msgHelpPopup{display:none!important}
span[id^="video-thumb"]{border:2px!important}

#topToolBarDiv a:link{color:#FFFFFF!important;text-decoration:none!important}
#topToolBarDiv a:hover{color:#CECECE!important;text-decoration:none!important}

div[onclick="hideTopToolBarDiv()"]{display:none!important}

[id^="ratingstars"]{background-color:#F6F6F6!important;color:#B3B3B3 !important}
html>body>div>div>div>div>div>div>div>div>div>ul>li>a>div[id^="ratingstars"]{display:none!important}

div[style^="padding: 10px"]{display:none!important}
a[href="http://deturl.com/tos.asp"]{display:none!important}
div[id="msgStarsFeaturePopup"]{display:none!important}
a[onclick="showHelpPopup(true)"]{display:none!important}
*[class^="google-checkout"]{display:none!important}
div[class="premium-yva-opened"]{display:none!important}
div[class^="masthead-ad-control"]{display:none!important}

div[id^="ratingstars-"]{display:none!important}

div[id="topToolBarDiv"]{position:fixed!important;background-color:#000000!important;text-decoration:none!important;float:right!important;font-size:9px!important;font-weight:bold!important;margin-top:-6px!important;margin-left:-48px!important;height:11px!important;border:none!important;height:6px!important;width:103.5%!important}
}



@-moz-document url-prefix(http://www.youtube.com/watch)
,url-prefix(http://www.youtube.com/user)
,url-prefix(http://www.youtube.com/channel)
,url-prefix(http://www.youtube.com/comment)
,url-prefix(https://www.youtube.com/watch)
,url-prefix(https://www.youtube.com/user)
,url-prefix(https://www.youtube.com/channel)
,url-prefix(https://www.youtube.com/comment)
{

div[id="placeholder-player"]{background-color:#000000!important;color:#000000!important}

div[style*="z-index: 2000000000"]{height:10px!important;background-color:white!important}
a[href*="deturl.com"]{display:none!important}
div[style*="rgb(192, 0, 192)"]{float:right!important;font-size:10px!important;font-color:#000000!important;border:none!important;color:#000000!important}
select[style*="font-size:16px"]{font-size:8px!important;height:16px!important;width:100px!important;float:right!important;padding:none!important}
}

@-moz-document url(http://www.youtube.com),url(http://www.youtube.com/)
{
/*div[class="feed-header no-metadata before-feed-content"]{margin-top:-160px!important}*/
div[class="premium-yva-unexpanded"],div[id="premium-yva"]{display:none!important}
iframe[id="yva-iframe"]{display:none!important}
a[href*="ads_preferences"]{display:none!important}
div[class="yva-360-bg"]{display:none!important}
div[id="homepage-sidebar-ads"]{display:none!important}
}
