css:
@-moz-document url-prefix("http://tagpro-"), url-prefix("http://maptest") {
html.background {
    background: rgb(31, 31, 31);
}
h1 {
    margin-top: 100px;
    padding-bottom: 20px;
    width: 650px;
    height: 137px;
    background: url(http://i.imgur.com/O4uUPLD.png) 0% 0% no-repeat transparent;
}
article {
    width: 1000px;
    display: block;
}
.section.bottomPadding.tiny {
    display: none;
}
a.button,
button.button {
    border: 3px solid rgb(255, 255, 255);
    background-color: rgb(239, 66, 62);
}
.buttons.smaller > a.button {
    min-width: 14%;
}
.section.smaller {
    padding-top: 25px!important;
}
a {
    color: white;
}
.hideIfExternal.section {
    display: none;
}
.section.lineup {
    display: none;
}
}

@-moz-document regexp(".*\\/groups\\/.{8,}") {
h1 {
    width: 166px;
    height: 35px;
    margin: 0;
    padding: 0;
    background: url(http://i.imgur.com/ehbQ2xv.png) 0% 0% no-repeat transparent;
}
a {
    color: white!important
}
div.teams > div {
    box-sizing: border-box;
}
div.teams > div.red {
    background-color: rgba(255, 0, 0, 0.20);
    color: #FFCFCF;
    border: 1px solid rgba(255, 255, 255, 0.2);
    background-color: rgba(239, 66, 62, 0.498039);
}
div.teams > div.blue {
    border: 1px solid rgba(255, 255, 255, 0.2);
    background-color: rgba(79, 175, 255, 0.498039);
}
div.teams > div.spectators {
    border: 1px solid rgba(255, 255, 255, 0.2);
}
div#waiting {
    border: 1px solid rgba(255, 255, 255, 0.2);
}
div.teams > div.gray {
    border: 1px solid rgba(255, 255, 255, 0.2);
}
div.teams ul > li {
    font-weight: bold;
}
form > div > .value {
    border: 1px solid rgba(255, 255, 255, 1);
    font-family: monospace;
}
form > div > .value:disabled {
    border: none;
}
button,
a.button {
    background-color: #EF423E;
    border: solid 2px white;
}
}

@-moz-document regexp(".*newcompte.fr:.*"), regexp(".*koalabeast.*:.*") {
html.background {
    background: rgb(31, 31, 31);
}
div#mapInfo {
    float: none;
    margin-left: auto;
    margin-right: auto;
    width: 100%;
    text-align: center;
    font-size: 150%;
    font-weight: bold;
    padding-bottom: 55px;
}
div#optionsLinks {
    display: none;
}
div#optionsName {
    position: absolute;
    top: 28px;
    width: 96.8%;
    text-align: center;
}
input#name {
    margin-left: 5px;
}
a#switchButton {
    position: absolute;
    right: 18px;
    top: 18px;
}
div#options {
    width: 1000px;
    box-shadow: 10px 10px 20px 0px rgba(0, 0, 0, 0.35);
    border: none;
    background-color: rgba(50, 50, 50, 0.75);
}
tbody.stats {
    font-family: monospace;
}
table#stats {
    font-size: 16px!important;
}
}

@-moz-document url-prefix("http://tagpro.gg/"), url-prefix("http://capturetheflag.us/"), url-prefix("http://tagpro.koalabeast.com/") {
html.background {
    background: rgb(31, 31, 31);
}
h1 {
    margin-top: 100px;
    padding-bottom: 20px;
    width: 650px;
    height: 137px;
    background: url(http://i.imgur.com/O4uUPLD.png) 0% 0% no-repeat transparent;
}
.right {
    display: none;
}
a.button.server {
    display: inline-block;
    margin-right: 5px;
    min-height: 0px;
}
a.button,
button.button {
    border: 3px solid rgb(255, 255, 255);
    background-color: rgb(239, 66, 62);
}
}
