css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://streamsquid.com/") {

   #main
   {
      visibility: hidden;
      width:0%;
   }
   
   .track-item-play-dummy
   {
      display: none !important;
      width:10px;
   }
   
   #now-playing
   {
      width:87%;
      height:100%;
   }
   
   #app-player
   {
      width:100%;
      height:100%;
   }

   #player
   {
      width:100%;
      height:75%;
   }
   
   #player-controls-wrapper
   {
      padding: 0px;
      align-content: center;
      align-items: center;
      align-self: center;
      width:100%;
      height:25%;
   }
   
   #player-track-name-wrapper, #player-track-artist-wrapper
   {
      height:10% !important;
      font-size: smaller;
   }
   
   #player-iframe
   {
      width:100% !important;
      height:100% !important;
   }
   
   #player-current-location, #play-history
   {
      display:none;
   }
   
   #player-controls
   {
      align-content: center;
      align-items: center;
      align-self: center;
   }
}

@-moz-document url("http://player.streamingmelodyforall.net/player.html") {
   body, html
   {
      width:100%;
      height:100%;
   }
   
   #player-container
   {
      width:100% !important;
      height:100% !important;
   }
   
   #player
   {
      width:100% !important;
      height:100% !important;
   }
}
