css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@-moz-document url("chrome://browser/content/browser.xul") {
  
  #tt,
  #tt-feedback,
  #tt-new-tab-button-container,
  #tt-treechildren {
    background-color: #222;
  }

  #tt {
    border: 0px;
    -moz-appearance: none;
  }

  #tt-splitter {
    -moz-appearance: none;
    border: 1px solid #222;
  }

  #tt-toolbar {
    background-color: #222;
    border-bottom: 1px solid #222;
    -moz-appearance: none;
  }
  
  #tt-toolbar toolbarbutton {
    -moz-appearance: none;
    padding: 5px;
  }
  
  #tt-toolbar toolbarbutton:hover {
    border: 1px solid #666 !important;
    background-color: #444 !important;
  }
 
  toolbarbutton[checked="true"] {
    background-color: highlight;
  }
  
  #tt-treechildren::-moz-tree-row {
    background-color: #222;
    margin: 3px;
  }

  #tt-treechildren::-moz-tree-row(selected) {
    background-color: highlight;
    margin: 0px;
  }

  #tt-treechildren::-moz-tree-cell-text {
    color: #FFF;
  }

  #tt-new-tab-button-container {
    border-top: 1px solid #222;
  }
  
  #tt-new-tab-button-container:hover {
    filter: opacity(100%) !important;
  }

  .tt-new-tab-button {
    filter: opacity(80%) !important;
    background-color: #666 !important;
  }
  
  .tt-new-tab-button:hover {
    filter: opacity(100%) !important;
    background-color: #777 !important;
    border: 1px solid #666 !important;
  }

  #tt-quicksearchbox {
    color: #DDD;
    background: #333 repeat scroll 0% 0%;
    -moz-appearance: none;
    border-width: 0px;
    height: 25px;
  }
  
  #tt-quicksearchbox:hover {
    background: #4C4C4C repeat scroll 0% 0%;
    border: 1px solid #333 !important;
  }
  
  #tt-treechildren::-moz-tree-image(tt-close),
  #tt-treechildren-feedback::-moz-tree-image(tt-close) {
    list-style-image: url(https://i.imgur.com/P4HAy9r.png);
  }

  #tt-treechildren::-moz-tree-image(tt-close, selected) {
    list-style-image: url(https://i.imgur.com/6ocI516.png);
  }

  #tt-treechildren::-moz-tree-image(tt-close, hover) {
    list-style-image: url(https://i.imgur.com/6ocI516.png);
  }

}
