css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(.*)/(mailman|pipermail)/(.*)") {
  * {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 18px;
    line-height: 1.42857;
    color: #333;
  }
  
  body{
    margin: .7em;
    
  }
  body > table:nth-child(1) > tbody > tr:nth-child(1) > td > center > h2{
    font-size:26px;
    text-align:left;
  }
  table{
    border-collapse: collapse;
  }
  table, td {
    border-spacing: 0;
    border: 1px solid #E5E5E5;
    font-size: 100%;
    empty-cells: show;
    border-top:none;
  }
  td {
    padding:0.2em;
  }
  h1, #content > p > table > tbody > tr:nth-child(1) > td,
  body > table:nth-child(1) > tbody > tr:nth-child(1) > td,
  body > p:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1),
  td[bgcolor="#99CCFF"]{
    background-color:#F7F7F7;
    padding:1.2em .7em .8em 1em;
    margin-left:-1em;
    margin-right:-1em;
    border-bottom: 1px solid #E5E5E5;
    border-top: 1px solid #F3F3F3;
    box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.15);
    text-align: left;
  }
  
  tr:nth-child(1) > td{
    font-weight: bold;
  }
  
  tr:nth-child(1) > td:last-child{
    border-right:1px solid white;
  }
  
  tr:nth-child(1) > td:nth-child(1){
    border-left: 1px solid white;
  }
  form:nth-child(1) td{
    border:0;
    border-top: 1px solid #EEE;
  }
  
  font[size="+2"],font[size="+1"]{
    font-size: 22px;
    display:inline-block;
    margin-top:8px;
  }
  
  td[bgcolor="#FFF0D0"]{
    background-color:#EEE;
  }
  i{
    color: #999;
    line-height: 1;
    padding-left:.5em;
  }
  ul{
    padding-left: 1.3em;
  }
  hr{
    border-color: #E5E5E5 #EEE #EEE;
    border-style: solid;
    border-width: 1px 0px 0px 0px;
    height:1px;
    box-shadow: 0px 3px 6px rgba(0, 0, 0, 0.15);
  }
  h1{
    font-size: 1.4em;
  }
  h2{
    font-size: 1.2em;
  }
  em{
    font-size: 1em;
  }
  body > h1:nth-child(2){
    margin: -.2em;
  }
  /* title */
  body > p:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > b:nth-child(1) > font:nth-child(1){
    font-size:1.4em;
  }
  
  /* glowing input fields */
  input[type=submit],input[type=reset]{
    background:green;
    color:#FFF;
    border:darkgreen 1px outset;
    border-radius:9px;
    font-weight:900;
    cursor: pointer;
  }
  input[type=reset]{
    background:grey;
    color:#000;
    border:darkgrey 1px outset;
  }
  input[type=text], textarea {
    -webkit-transition: all 0.30s ease-in-out;
    -moz-transition: all 0.30s ease-in-out;
    -ms-transition: all 0.30s ease-in-out;
    -o-transition: all 0.30s ease-in-out;
    outline: none;
    padding: 3px 0px 3px 3px;
    margin: 5px 5px 3px 0px;
    border: 1px solid #DDDDDD;
  }

  input[type=text]:focus, textarea:focus {
    box-shadow: 0 0 5px rgba(81, 203, 238, 1);
    padding: 3px 0px 3px 3px;
    margin: 5px 5px 3px 0px;
    border: 1px solid rgba(81, 203, 238, 1);
  }
  
  /* shades of #009ee0 */
  b a,b a:link{
    color: #009ee0;
  }
  a,a:link{
    color: #007eb3;
    text-decoration: none;
  }
  a:visited{color:#DC0067;}
  a:hover { color: #9a0048; }
  b a:hover{ color: #b00052;}
  a:focus { color: #DC0067; outline: none; }
  a:focus, a:hover {
    text-decoration: underline;
  }
  a:focus {
    outline: thin dotted;
    outline-offset: -2px;
  }
}
/* create site specific logo */
@-moz-document regexp("(.*)freifunk(.*)/(mailman|pipermail)/(.*)") {
  h1, #content > p > table > tbody > tr:nth-child(1) > td,
  body > table:nth-child(1) > tbody > tr:nth-child(1) > td,
  body > p:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1),
  td[bgcolor="#99CCFF"]{
    padding-left:120px;
    background: url("http://wiki.freifunk.net/images/0/0f/Logo_Freifunk_Oberhausen.png") no-repeat;
    background-size:6%;
    background-position: 10px;
  }
}
