css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
.pm-window h3 {
    margin: 0;
    padding: 2px 5px;
    font-size: 11pt;
    border: 1px solid #000;
    cursor: pointer;
    width: 325px;
    position: relative;
    right: 55px;
}
.pm-window h3.pm-minimized {
    position: relative;
    right: 55px;
}
.pmbox {
    width: 325px;
    margin: 0 auto;
    min-height: 2px;
}
.pm-log {
    min-height: 100px;
    max-height: 300px;
    word-wrap: break-word;
    border: 1px solid #000;
    border-top: 0;
    border-bottom: 0;
    width: 335px;
    overflow: auto;
    position: relative;
    right: 55px;
}
.pm-log-add {
    border: 1px solid #000;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    width: 335px;
    position: relative;
    right: 55px;
}
.pm-window.collapsed .pm-log,
.pm-window.collapsed .pm-log-add {
    display: none;
}
.pm-window.focused h3,
.pm-window.focused .pm-log,
.pm-window.focused .pm-log-add {
    border-color: #000;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {
    cursor: default;
    position: relative;
}
.battleform {
    width: 250px;
    text-align: left;
    margin: 0 auto;
}
.challenge {
    margin-top: -1px;
    background: #fcd2b3;
    border: 1px solid #f57b21;
    color: #682f05;
    padding: 1px 5px;
    width: 325px;
    position: relative;
    right: 55px;
}
.menugroup {
    background: rgba(0, 0, 0, 0)
}
}
