css:
@-moz-document domain("cinemaniahdd.com") {
/*Home*/

html {
    background-color: transparent;
}
body {
    background: url(http://images5.fanpop.com/image/photos/29400000/Massive-B-Horror-Collage-Wallpaper-horror-movies-29491579-2560-1600.jpg) fixed no-repeat!important;
}
.total-post {
    background-color: rgba(0, 0, 0, 0.7);
    color: #FFFFFF;
    border-right: 1px solid rgba(255, 255, 255, 07);
    border-left: 1px solid rgba(255, 255, 255, 07);
}
.total-post span {
    color: #FFF;
}
.uk-navbar {
    background: rgba(0, 0, 0, 0.69);
    color: #FFFFFF;
    border: 1px solid rgb(255, 255, 255);
}
.uk-navbar-nav > li > a {
    color: white;
    text-shadow: none;
}
.uk-navbar-nav > li:hover > a {
    color: #D8304A;
    text-shadow: none;
}
.uk-navbar-nav > li > a:active {
    background: none!important;
    background-color: transparent!important;
    box-shadow: none!important;
    text-shadow: none!important;
    color: #9E2236!important;
}
.slider-container {
    background-color: rgba(0, 0, 0, 0.7);
    border-right: 1px solid white;
    border-left: 1px solid white;
}
.top-container {
    background-color: rgba(0, 0, 0, 0.7);
    /* border-bottom: 1px solid rgba(90, 90, 90, 0.34); */
    
    border: 1px solid white;
}
.form-header form {
    border: 1px solid rgba(255, 255, 255, 0.7);
    background-color: rgba(0, 0, 0, 0.7);
}
.main-container {
    background-color: rgba(0, 0, 0, 0.7);
    border: 1px solid rgba(255, 255, 255, 0.7);
}
.uk-button-group .uk-button {
    border-radius: 0 !important;
    border: 1px solid rgba(255, 255, 255, 0.7);
}
.uk-panel-box .uk-panel-title {
    color: #FFFFFF;
}
.uk-panel-box {
    background: rgba(0, 0, 0, 0.7);
    color: #FFFFFF;
    border-right: 1px solid rgba(255, 255, 255, 0.7);
    border-radius: 0px;
    border-left: 1px solid rgba(255, 255, 255, 0.7);
    border-bottom: 1px solid rgba(255, 255, 255, 0.7);
    border-top: 1px solid rgba(255, 255, 255, 0.7);
}
.post {
    background-color: rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(255, 255, 255, 0.7);
    border-radius: 0px;
}
.post .title-post {
    color: #FFFFFF;
    text-shadow: none;
}
.post .count-view-post {
    color: white;
    text-shadow: none;
}
.sidebar-container .uk-panel-title {
    color: #FFFFFF;
    background-color: rgba(0, 0, 0, 0.7)!important;
    text-shadow: none;
    background-image: none;
    border-radius: 0px!important;
    border-bottom: 1px solid rgba(255, 255, 255, 0.7);
}
.footer-container {
    background-color: rgba(17, 17, 17, 0.7);
    border: 1px solid rgba(255, 255, 255, 0.7);
}
.footer-content {
    color: #FFFFFF;
}
.dev-commentario {
    color: black;
    background: #FAFAFA;
    border: 1px solid #3F3F3F;
    border-radius: 4px;
}
.uk-h3 {
    color: #939393;
}
}
