css:
@-moz-document domain("turtleseed.com") {
.container {
    background-color: #4477BD;
    background-image: none;
    border-bottom: 1px solid rgba(0, 0, 0, 0.15);
    border-top: none;
    border-left: none;
    border-right: none;
    box-shadow: none;
}
#logo {
    border: none;
    box-shadow: none;
    transition: all 0.4s;
    left: 82px;
    width: 39px;
    -webkit-filter: brightness(20) grayscale(1);
}
#logo h1 a {
    background-size: 37%;
    margin-top: -1px;
}
#logo:hover {
    background: none;
}
#searchid {
    border: 1px solid #e1e8ed;
    box-shadow: none;
    border-radius: 2px;
    background-color: #f5f8fa;
    transition: all 0.2s;
}
#searchid:hover {
    border: 1px solid #e1e8ed;
    box-shadow: none;
    border-radius: 2px;
}
#searchid:focus {
    border: 1px solid #e1e8ed;
    box-shadow: none;
    border-radius: 2px;
    background-color: white;
}
.bigger_status_boxes {
    border-radius: 3px;
    box-shadow: none;
    background-color: white;
    border: 1px solid #e1e8ed;
}
.bigger_status_boxes:hover {
    background-color: #f5f8fa;
    transition: all 0.1s;
}
.status_boxes {
    border-radius: 3px;
    box-shadow: none;
    background-color: white;
    border: 1px solid #e1e8ed;
}
.status_boxes:hover {
    background-color: #f5f8fa;
    transition: all 0.1s;
    border: 1px solid #e1e8ed;
}
.huge_status_boxes {
    border-radius: 3px;
    box-shadow: none;
    background-color: white;
    border: 1px solid #e1e8ed;
}
.huge_status_boxes:hover {
    background-color: #f5f8fa;
    transition: all 0.1s;
}
.giant_status_boxes {
    border-radius: 3px;
    box-shadow: none;
    background-color: white;
    border: 1px solid #e1e8ed;
}
.giant_status_boxes:hover {
    background-color: #f5f8fa;
    transition: all 0.1s;
}
.yoursubmit {
    -webkit-filter: brightness(2) grayscale(1);
}
#profilepage h2 a {
    -webkit-filter: brightness(20) grayscale(1);
    background-size: 80%;
    margin-top: -1px;
}
#messagepage h2 a {
    -webkit-filter: brightness(20) grayscale(1);
    background-size: 80%;
    margin-top: -1px;
}
#contentpage h2 a {
    -webkit-filter: brightness(20) grayscale(1);
    background-size: 80%;
    margin-top: -1px;
}
#notespage h2 a {
    -webkit-filter: brightness(20) grayscale(1);
    background-size: 80%;
    margin-top: -1px;
}
.data p {
    font-size: 14px;
    padding: 10px;
    color: #303030;
}
#statustext {
    border: 1px solid #e1e8ed;
    box-shadow: none;
    border-radius: 2px;
    background-color: #f5f8fa;
    transition: all 0.2s;
    margin-left: 1px;
}
#statustext:hover {
    border: 1px solid #e1e8ed;
    box-shadow: none;
    border-radius: 2px;
}
#statustext:focus {
    border: 1px solid #e1e8ed;
    box-shadow: none;
    border-radius: 2px;
    background-color: white;
}
#statusBtn {
    top: 195px;
}
#remainingCharacters {
    font-family: sans-serif;
    top: 204px;
    left: 145px;
}
#thiscoverpic {
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}
#notesBox h2 {
    border-radius: 3px;
}
.stream li {
    border-radius: 3px;
}
#shorepercent {
    display: none;
}
#followsyou {
    background: #f5f5f5;
    border-color: #f5f5f5;
}
#transbox {
    background: #f5f5f5;
    border-color: #f5f5f5;
}
#profile_pic_box {
    display: none;
}
#proseedurelink {
    top: 94%;
}

#changenotify {
    display: none;
    margin-bottom: 200px;
    opacity: 0;
}
#newnewsforyou {
    margin: 10px;
    margin-top: 4px;
}
}
