css:
@-moz-document domain("www.goldenboys.fr") {

body {
 	color: #333;   
    background: #f1f1f1;
    height: auto;
}

h1#logo
{
 	top: 35px;   
}

#headerMenu-container .f-left,
#headerMenu-container .f-right
{
    display: none;
}

div#wrapper {
    width: 80%;
    min-width: 1024px;
}

div#header-container,
div#main-container,
div#header-top
{
    width: 100%;
}

div#header-container
{
 	background: #333;
    color: white;
}
div#header-infocompte div.f-left ul li a
{
 	color: white;   
}

div#header-top
{
    background: #333333;
}

#navmenu #header-menu{
  	width:100%;
    left: 0;
    border: none;
    border-left: none;
    border-right: none;
    background: #222222;
}

#navmenu #header-menu li,
#navmenu #header-menu li a
{
    border: none;
    margin-left: 0 !important;
}
#navmenu #header-menu li a:hover
{
 	background: #333333;   
}

div#header-news
{
    width: 100%;
    background: white;
    border: none;
    color: #333;
}

ul#langues-villes {
 	height: 30px;
    line-height: 30px;
    width: auto;
    padding: 0 10px;
    background: #222;
}

#menu_equipe {
 	height: 30px;
    line-height: 30px;
    width: 100%;
    padding: 0 10px;
    box-sizing: border-box;
    background: #222;
    display: block;
    color: white !important;
}
#menu_equipe a:link {
 	color: white;
    float: none;
    display: inline-block;
}
#menu_equipe ul li.actif a:link {
 	border: none;
    line-height: normal;
    color: #ffab00;
    background: none;
}
#menu_equipe ul li.actif {
    background: none;
}

div#header-infocompte {
 	border: none;   
}
div#header-infocompte div.f-left {
 	width: auto;
    border: none;
}
div#header-infocompte div.f-left p {
	margin: 0; padding: 0;
    text-align: right;
    margin-right: 2px;
}
div#header-infocompte div.f-left ul {
	margin: 0; padding: 0;
    text-align: right;
}
div#header-infocompte p.f-left {
	display: none;
}

div.industrie
{
 	background: #222;
    height: 35px;
    line-height: 35px;
    margin: 0;
    padding: 0 15px;
}

div#main-container{
	margin-top: 0;    
}

div#main-wrapper
{
 	border: none;
    width: 80%;
}
div#rigt-container
{
    width: 20%;
    background: #333;
}
div#rigt-container div.bloc
{
    width: 100%;
    border: none;
    background: #fafafa;
    margin-bottom: 0;
}
div.pdm span.header {
 	background: #333;
    color: white;
    margin-top: 7px;
}

div.bloc span.footer {
 	width: 100%;
    top: 0; left: 0;
    background: #333;
    line-height: normal;
    height: auto;
    padding: 7px;
    box-sizing: border-box;
    text-indent: 0;
    min-height: 26px;
}
div.bloc span.footer b a {
 	margin-left: 20px;
}
div.bloc span.footer a img {
    left: 8px;
    top: 7px;
}

#main-content #formrecherche
{
 	background: #fafafa;
    height: 30px;
    line-height: 30px;
}

#marches li.actif a:link
#marches li:hover a
{
 	border: none;
    line-height: normal;
}

div#footer-container {
 	width: 80%;
    background: #333;
}

ul.footer-infos {
 	min-width: 200px;
    width: 33%;
    box-sizing: border-box;
    padding: 15px;
    min-height: 0;
    margin: 0;
    border: none;
}
ul.footer-infos li.footerInfos-titre {
 	text-transform: uppercase;
    border-bottom: 1px solid white;
    margin-bottom: 5px;
    display: block;
    width: 100%;
}
ul.footer-infos li {
 	display: inline-block;
    width: 49%;
    box-sizing: border-box;
    padding: 3px 0;
    font-size: 12px;
}

td {
 	padding: 7px 10px;   
}

div.miniban {
 	display: none;   
}

.clear {
 	height: 0;   
}

input.bouton {
 	-webkit-border-radius: 7;
    -moz-border-radius: 7;
    border-radius: 7px;
    font-family: Arial;
    color: #ffffff;
    font-size: 1em;
    background: #ffab00;
    padding: 10px 20px 10px 20px;
    text-decoration: none;
    border: none;
}
input.bouton:hover {
 	background: #eb9d00;
  	text-decoration: none;   
}

textarea,
input[type=text],
input[type=password],
input[type=email]
{
    border: 1px solid #c3c3c3;
 	padding: 10px;
    border-radius: 7px;
    outline: none;
    font-family: Calibri, Arial, sans-serif;
    font-size: 14px;
    background: white;
}
input[type=text],
input[type=password],
input[type=email]
{
 	padding: 5px 10px;   
}

}
