css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("investing.com") {

  body {
    background-color: #0F0F0F;
    color: #898989;
    border-bottom: 1px solid #000;}
A, .AR_3.ob-strip-layout .ob-rec-text:hover {
    text-decoration: none;
    color: #0084FF;
    outline: medium none;}

  .eCalNew .ecoCalTbl.genTbl TD.theDay {
    background-color: #000;
    color: #DADADA;
    border-top: none;
    line-height: 19px;
}
  
  
H2 A, .popupBigTitle A, H2 A:hover, .popupBigTitle A:hover, .h2LikeTitle A, .linkTitle A {
    color: #DADADA;
}  
  
  
  .subMenuWrapper {
    background-color: #000;
    border-bottom: 1px solid #000;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.2);
}
  
  
  .chartWrap {
    border-width: 0px 0px 0px;
    border-style: none solid solid;
    border-color: -moz-use-text-color #C7C7C7 #C7C7C7;}
  
  .chartBarWrap {
    border-width: 1px;
    border-style: solid;
    border-color: #000 #000 #000;
    border-image: none;
    padding: 4px 0px;
    background-color: #000;
    background-image: -moz-linear-gradient(center top , #000, #000);
}
  
  
  .doubleLineSeperator {
    border-top: 3px solid #000;
    height: 0px;
    width: auto;
    margin: 27px 0px 26px;
}
  
  
  .instrumentFloaterInner {
    position: absolute;
    background-color: #0F0F0F;
    width: 650px;
    padding: 11px 0px 10px;
    border-bottom: 1px solid #fff;
    box-shadow: 0px 6px 6px -6px #C4C4C4;
}
  
  
  .closedTbl {
    border-width: 1px 1px 2px;
    border-style: solid;
    border-color: #000;}
    
    
    .commentReply {
    position: relative;
    border-top: 1px solid #444;
    padding-top: 15px;
    margin: 10px 0px 10px 45px;
}
  
  .comment {
    border-top: 2px solid #008AFF;
}
  
  
  .showMoreComments:hover, .showMoreReplies:hover {
    background-color: #000;
    border-bottom: 1px solid #000;
}
  
  .showMoreComments, .showMoreReplies {
    background-color: #000;
    border-bottom: 1px solid #000;}
  
  
  .tabsBoxBottom {
    border-width: 1px 1px 2px;
    border-style: solid;
    border-color: #000;}
    
    
    .tabsForBox LI.selected A {
    background-color: #000;
    border-width: 2px 1px medium;
    border-style: solid solid none;
    color: #A6A6A6;
    border-color: #000 #000 -moz-use-text-color;}
      
      
.tabsForBox LI.selected:first-child A {
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}
  
  
.curExp .boxWrapper {
    border-width: 1px 1px 2px;
    border-style: solid;
    border-color: #000;}
  
  
  #newFilterBox, #filterBoxTable, #directoryFilter  {
    background-color: #000;
    border-top: 1px solid #000;
  }
  
  
  
  .techSummaryTool .dataBox.timeframe {
    border-top: 1px solid #000;
    background-color: #000;  }
    
    
    
    .sideColumnBox {
    border-color: #000;  }
  
  
  
  .sideColumnBoxTitle {
    background-color: #000; }
  
  
  FOOTER .top {
    background-color: #000;}
  
.disclaimerBox {
    border: 1px solid #000;}
  
  
  
  .overviewDataTable DIV, .newBigTabs, .articleItem, FOOTER, .articleFooter, .articleHeader + .contentSectionDetails, .genTbl TD, .halfSizeArticle.twoColumn .articleItem, .largeTitle.halfSizeArticle.twoColumn .articleItem:last-child {
    border-bottom: 1px solid #444;
    border-top: 1px solid #444;
  }
  
  
  .innerHeaderSeperatorBottom, .overViewBox HR {
    background-color: #444;}
  
.quotesBarWrapper {
    border-top: 1px solid #444;
    background: rgb(0, 0, 0) none repeat scroll 0% 0%;
}  
  
  
  .genTbl TH {
    color: #848484; } 
  
  
  
  .WYSIWYG .aqlink {
    background-color: rgb(0, 0, 0);  } 
  
  
  .articleTextFont, .WYSIWYG, .articlesPageShort P, .mainArticleTop P, .mainSmallArticleItem P, .innerWebinarText, #_300x250textads LI DIV {
    color: #8C8C8C;
}
  
  
  .round-items LI.selected A {
    border: 1px solid #0078FF;
    background-color: #E0E0E0;
    box-shadow: 0px 2px 2px 0px #5C5C5C inset;
}
.round-items LI A {
    color: #0078FF;}
  
  
  .techSummaryTool .dataBox {
    border-color: -moz-use-text-color #444 #444;
}
  
  .eCalNew.homepageEC .ecoCalTbl.genTbl TD.theDay {
    background-color: #000;}
  
  .hugeTitle .textDiv {
 background-color: #000;}
  
  
  H2.opinionsHp, .opinionsHp.popupBigTitle {
    border-top: 1px solid #444;}
  
    
    
    .newFilterBox .mainFilter A {
    border-top: 1px solid #23303F;}
  
  
  .curExp .newFilterBox .mainFilter A:first-child {
    border-top: 1px solid #000;}
  
  .blackFont {
    color: #FFF;}
  
  .techSummaryTool .genTbl TBODY TR.selected, .recentQuotesSideBlockTbl tbody tr:hover td, .quotesSideBlockTbl tbody tr.Selected, .quotesSideBlockTbl tbody tr.Selected .sideColumn {
    background-color: rgb(0, 0, 0);
    border-bottom: 1px solid rgb(0, 0, 0);
    border-top: 1px solid rgb(0, 0, 0);
}
  
  
  .quotesSideBlockTbl tbody tr.Selected .sideColumn {
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
}
  
  
  
  .floatingHeader {
    background-color: #000;
    border-bottom: 1px solid #005A8C;
    border-top: 1px solid #000;
}
  
  
  .genTbl .timeSeparator TD {
    border-top: 2px solid #0058A4;
}
  
  .notificationImportant {
    background-color: #000;
          -moz-box-shadow: 0px 0px 10px #0084FF;
      -webkit-box-shadow: 0px 0px 10px #0084FF;
      box-shadow: 0px 0px 10px #0084FF;}
    
    
    .eCalMainNew TD.event A {
    color: #267FD7;}
  
  

}
