css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

@-moz-document url("chrome://browser/content/browser.xul")
{
	/*
	 * Background
	 */
	#tt,
	#tt-feedback,
	#tt-new-tab-button-container,
	#tt-treechildren
	{
		background-color: #fff;
	}
	#tt
	{
		-moz-appearance: none;
		border: 0px;
	}

	/*
	 * Left splitter
	 */
	#tt-splitter
	{
		-moz-appearance: none;
		border: none;
		background-color: #fff;
		border-left: 1px solid #aaa;
		border-right: 1px solid #aaa;
	}

	/*
	 * Pinned tabs
	 */
	#tt-toolbar
	{
		-moz-appearance: none;
		background-color: #fff;
	}
	#tt-toolbar toolbarbutton
	{
		-moz-appearance: none;
		border: 1px solid transparent;
		padding: 3px;
		margin: 2px;
	}
	#tt-toolbar toolbarbutton:hover
	{
		border: 1px solid transparent;
		background-color: #e5f3ff;
	}
	#tt-toolbar toolbarbutton[checked="true"]
	{
		background-color: #ddd;
		border: 1px solid #aaa;
	}

	/*
	 * Tab rows
	 */
	#tt-treechildren::-moz-tree-row
	{
		margin: 0px;
		margin-top: 1px;
		margin-bottom: 1px;
		border: none;
		border-top: 1px solid transparent;
		border-bottom: 1px solid transparent;
	}
	#tt-treechildren::-moz-tree-row(selected),
	#tt-treechildren-feedback::-moz-tree-row(selected)
	{
		border: none;
		border-top: 1px solid #aaa;
		border-bottom: 1px solid #aaa;
	}
	#tt-treechildren::-moz-tree-cell
	{
		background-color: transparent;
	}
	#tt-treechildren::-moz-tree-cell(selected)
	{
		background-color: #ddd;
	}
	#tt-treechildren::-moz-tree-cell-text(selected)
	{
		color: #000;
		font-weight: bold;
	}
	#tt-treechildren::-moz-tree-line(selected),
	#tt-treechildren-feedback::-moz-tree-line(selected)
	{
		border-top: 2px solid #aaa;
		border-bottom: 2px solid #aaa;
	}

	/*
	 * Tabs drag and drop
	 */
	#tt-treechildren::-moz-tree-row(dropOn),
	#tt-treechildren::-moz-tree-row(dropBefore),
	#tt-treechildren::-moz-tree-row(dropAfter)
	{
		background-color: #eee;
	}
	#tt-treechildren::-moz-tree-row(dropOn)
	{
		background-color: #888;
	}
	#tt-treechildren::-moz-tree-row(dropBefore)
	{
		border: none;
	}
	#tt-treechildren::-moz-tree-row(dropAfter)
	{
		border: none;
	}

	/*
	 * Tab search & search box
	 */
	#tt-treechildren::-moz-tree-row(quickSearch)
	{
		border: none;
		border-right: 3px solid #44a;
	}
	#tt-quicksearchbox
	{
		-moz-appearance: none;
		color: #888;
		border-width: 0px;
		height: 25px;
	}
	#tt-quicksearchbox:hover
	{
		background: #fafafa repeat scroll 0% 0%;
		color: #aaa;
	}

	/*
	 * New tab button
	 */
	#tt-new-tab-button-container
	{
		border-top: 1px solid #888;
		cursor: hand;
	}
	.tt-new-tab-button
	{
		height: 22px;
		cursor: pointer;
	}
	.tt-new-tab-button:hover
	{
		border: none;
		background-color: #e5f3ff;
	}
}
