css:
@-moz-document domain("noonpacific.com") {
html, body {
  background: #222;
}


.sidebar,
.mixtape-container {
  background: #333;
}

.modal-overlay-subscribe,
.modal-frame-subscribe,
ul.nav,
h3,
ul.song-social,
ul.accordion__panel,
ul.song-social li,
ul.accordion__panel li,
ul.song-social li a,
ul.accordion__panel li a,
#vibes,
.subscribe-container,
.subscription_form,
.mobile-app,
.tracklist:after {
  display: none;
  height: 0 !important;
  width: 0 !important;
}

nav.navbar {
	background: #333;
}

.control-buttons i,
div.audio-player {
  background: #444;
  color: #ccc;
}

a.accordion__button,
span.artist {
  color: #666;
}

div.tracklist ol li.active-borders {
	border-top: 1px solid red;
	border-bottom: 1px solid red;
}

div.tracklist ol li:hover {
	border-top: 1px solid red;
	border-bottom: 1px solid red;
}
}
