css:
@-moz-document url-prefix("http://runouw.com") {
/* Reset backgrounds */

span.corners-top,
span.corners-bottom,
span.corners-top span,
span.corners-bottom span,
.forabg,
.forumbg,
.bg1,
.bg2,
ul.forums,
li.row:hover,
#tabs a {
    background: none !important;
}
body {
    background: #1F1F1F !important;
}
/* Background colors */

.forabg,
.forumbg {
    background: #111 !important;
}
.headerbar {
    background: #333 !important;
}
#navigation a {
    background: #555 !important;
}
#navigation a:hover,
#navigation #active-subsection a {
    background: linear-gradient(to right, #333 0%, #444 100%) !important;
}
html,
body,
select,
input.search,
.inputbox,
.pagination span a,
.pagination span a:link,
.pagination span a:visited,
dl.codebox {
    background-color: #333 !important;
}
.navbar,
.bg1,
ul.forums,
.panel,
#tabs a {
    background-color: #333 !important;
}
.bg2 {
    background: #445145 !important;
}
li.row:hover {
    background-color: #1F1F1F !important;
}
blockquote {
    background-color: #666 !important;
}
.pagination span a:hover,
.pagination span strong {
    background-color: #888 !important;
}
.pagination span strong {
    background-color: #a96 !important;
}
.postlink:hover {
    background-color: #ddd !important;
}
#tabs a span {
    background: inherit !important;
}
/* Text colors */

html,
body,
.headerbar,
h2,
h3,
.postbody,
.postbody .content,
#tabs .activetab a span,
ul.topiclist li,
.panel li.header dd,
.panel li.header dt {
    color: #ddd !important;
}
.inputbox {
    color: #bbb !important;
}
a:hover {
    color: #00CC00 !important;
}
.postprofile,
dl.details dd {
    color: #aaa !important;
}
li.row strong,
.postprofile strong,
label,
.panel,
#tabs a span,
dl.details dt {
    color: #999 !important;
}
select,
input.search,
.inputbox,
.pagination span a,
.pagination span a:link,
.pagination span a:visited {
    color: #888 !important;
}
.postlink:hover,
.pagination span a:hover,
.pagination span strong {
    color: #333 !important;
}
dl.codebox code {
    color: #6a6 !important;
}
/* Border colors */

hr {
    border-color: #222 #333 #333 !important;
}
blockquote {
    border-color: #333 !important;
}
dl.codebox {
    border-color: #363 !important;
}
select,
input.search,
.inputbox,
.pagination span a,
.pagination span a:link,
.pagination span a:visited,
.pagination span a:hover,
.pagination span strong {
    border-color: #444 !important;
}
h2,
h3 {
    border-bottom-color: #222 !important;
}
ul.navlinks,
#cp-main h3 {
    border-bottom-color: #333 !important;
}
dl.codebox dt {
    border-bottom-color: #363 !important;
}
#tabs .activetab a {
    border-bottom-color: #444 !important;
}
.postlink {
    border-bottom-color: #a96;
}
li.row {
    border-top-color: #333 !important;
    border-bottom-color: #222 !important;
}
.signature,
ul.cplist {
    border-top-color: #333 !important;
}
ul.topiclist dd,
.postprofile {
    border-left-color: #333 !important;
}
/* Other */

.forabg,
.forumbg {
    border-radius: 4px;
}
/* Images */
}
