css:
@-moz-document domain(cgvisual.com){
  html,body{background:#020!important;color:#ccc!important;}
  /*global highlight*/::-moz-selection{color:#0a0!important;background:#000!important;}
  /*global*/*{text-shadow:none!important;text-decoration:none!important;border:none!important;box-shadow:none!important;font-weight:100!important;}
  /*global link*/a:link:not([style*="color:"]){color:#c60!important;}
  /*global link*/a:hover                      {color:#f60!important;}
  /*global link*/a:visited                    {color:#963!important;}
}
@-moz-document regexp("http(s)?://www\.cgvisual\.com/.*"){html,body{background:#020!important;}}
@-moz-document regexp("http(s)?://www\.cgvisual\.com/forum/.*"){
  /*resize*/body#phpbb{margin:0px auto!important;}
  /*resize*/body>#wrap{width:900px!important;margin:auto!important;padding:0px!important;}
}
@-moz-document regexp("http(s)?://www\.cgvisual\.com/forum/viewforum.*"){
  .forumbg{background:transparent!important;padding:10px 10px 10px 10px!important;}
  .forumbg>div>span{display:none!important;}
  
  /*thread*/.topiclist li>dl{background:none!important;padding:0px!important;}
  /*thread*/.topiclist li>dl>dt{width:64%!important;padding:0px!important;margin:0px!important;}
  /*thread*/.topiclist li>dl>dd:nth-of-type(1){width:8%!important;padding:0px!important;margin:0px!important;color:#0af!important;}
  /*thread*/.topiclist li>dl>dd:nth-of-type(2){width:8%!important;padding:0px!important;margin:0px!important;color:#0a0!important;}
  /*thread*/.topiclist li>dl>dd:nth-of-type(3){width:20%!important;padding:0px!important;margin:0px!important;}
  
  /*thread header*/.topiclist:not(.topics){background:#000!important;padding:5px 0px!important;border-radius:10px 10px 0px 0px!important;}
  /*thread header*/.topiclist:not(.topics) .header{background:transparent!important;}
  /*thread header*/.topiclist:not(.topics) .icon>*{text-align:center!important;font-size:20px!important;line-height:100%!important;}
  
  /*thread row*/.topiclist.topics>li{padding:3px 10px 3px 10px!important;}
  /*thread row*/.topiclist.topics>li:nth-child(2n){background:#333!important;}
  /*thread row*/.topiclist.topics>li:hover{background:#000!important;}
  
  /*thread title*/.topiclist.topics .icon>dt{margin:0px 0px 0px 0px!important;}
  /*thread title*/.topiclist.topics .icon .topictitle{font-size:24px!important;line-height:100%!important;}
  /*thread title*/.topiclist.topics .icon .topictitle~.pagination{background:none!important;padding:0px!important;}
  /*thread title*/.topiclist.topics .icon .topictitle~.pagination span>span{font-size:12px!important;line-height:100%!important;margin:0px -5px!important;}
  /*thread title*/.topiclist.topics .icon .topictitle~.pagination span>a{font-size:20px!important;line-height:100%!important;background:transparent!important;padding:2px 5px!important;margin:0px!important;}
  /*thread title*/.topiclist.topics .icon .topictitle~.pagination span>a:hover{background:#063!important;border-radius:10px!important;}
  
  /*thread info*/.topiclist.topics .icon .posts{font-size:20px!important;line-height:35px!important;}
  /*thread info*/.topiclist.topics .icon .views{font-size:16px!important;line-height:35px!important;}
  /*thread info*/.topiclist.topics .icon .lastpost{font-size:12px!important;line-height:120%!important;text-align:right!important;}
  /*thread info*/.topiclist.topics .icon .lastpost>span{padding-top:5px!important;}
}
@-moz-document regexp("http(s)?://www\.cgvisual\.com/forum/viewtopic.*"){
  #page-body{margin:0px!important;}
  #page-body>h2{background:#000!important;text-align:center!important;padding:5px 0px!important;margin:0px!important;}
  #page-body>h2>a{color:#a11!important;font-size:30px!important;}
  #page-body>.panel{display:none!important;}
  
  
  
  /*post*/.post{padding:0px!important;border-radius:10px!important;}
  /*post*/.post~.post{margin:20px 0px !important;}
  /*post*/.post>.inner{background:transparent!important;}
  /*post separator*/.post>.inner>span{display:none!important;}
  /*post footer*/   .post>.inner>.back2top{height:0px!important;}
  /*post footer*/   .post>.inner>.back2top>a{display:none!important;}
  /*post footer*/   .post>.inner .signature{color:#888!important;font-size:14px!important;line-height:100%!important;text-align:right!important;width:initial!important;padding:0px 10px 10px 200px!important;margin:0px!important;}
  
  /*author*/     .postprofile{margin:0px!important;min-height:initial!important;width:20%!important;overflow-y:hidden!important;}
  /*author*/     .postprofile>*{margin:0px!important;padding:0px!important;line-height:100%!important;}
  /*author*/     .postprofile>*:nth-child(-n+4){text-align:center!important;}
  /*author*/     .postprofile>*:nth-child(n+5){padding-left:10px!important;line-height:120%!important;font-size:12px!important;margin-top:3px!important;}
  /*author name*/.postprofile>dt>br{display:none!important;}
  /*author name*/.postprofile>dt>a{font-size:24px!important;line-height:120%!important;}
  /*author name*/.postprofile>dt>a:nth-of-type(1){width:100%!important;}
  /*author name*/.postprofile>dt>a:nth-of-type(1) img{width:100%!important;height:100%!important;display:block!important;border-radius:10px 0px 10px 0px !important;}
  /*author name*/.postprofile>dt>a:nth-of-type(2){width:100%!important;}
  /*author info*/.postprofile>dd:nth-of-type(2){display:none!important;}
  /*author info*/.postprofile>dd:nth-of-type(-n+4)>strong{display:none!important;}
  /*author info*/.postprofile>dd:nth-of-type(1){color:#ddd!important;font-size:20px!important;}
  /*author info*/.postprofile>dd:nth-of-type(3){color:#0af!important;font-size:20px!important;}
  /*author info*/.postprofile>dd:nth-of-type(4){color:#0a0!important;}
  
  /*content info*/.postbody{width:80%!important;}
  /*content info*/.postbody>h3{display:none!important;}
  /*content info*/.postbody>.author{margin:0px!important;padding:0px 0px 0px 5px!important;}
  /*content info*/.postbody>.author{font-size:20px!important;color:#444!important;}
  /*content info*/.postbody>.author img{height:100%!important;width:20px!important;top:2px!important;position:relative!important;opacity:0.5!important;}
  /*content info*/.postbody>.author img:hover{opacity:1!important;}
  /*content info*/.postbody>.author strong{display:none!important;}
  
  /*content main*/.postbody .content{color:#bbb!important;font-size:24px!important;line-height:120%!important;min-height:initial!important;padding:10px 5px 10px 10px!important;}
  
  /*quote*/.content blockquote{margin:0px!important;padding:0px 0px 0px 40px!important;background:none!important;color:#888!important;}
  /*quote*/.content blockquote>div{opacity:.8!important;}
  /*quote*/.content blockquote:hover>div{opacity:1!important;color:#ccc!important;}
  /*quote*/.content blockquote cite{display:none!important;}
  /*quote*/.content blockquote+br,.content blockquote+br+br,.content blockquote+br+br+br,
  /*quote*/.content blockquote br{display:block!important;margin:0px 0!important;line-height:0px!important;content:""!important;}
  
  
  /*content misc - last edit*/ .postbody .notice{margin:0px!important;padding:0px 0px 5px 5px!important;}
  /*content misc - attach img*/.postbody .content .thumbnail{margin:0px!important;}
  /*content misc - attach img*/.postbody .thumbnail img{background:transparent!important;}
  /*content misc - attach img*/.postbody .thumbnail img:hover{background:#080!important;}
  /*content misc - attach img*/.postbody .thumbnail a:hover{background:transparent!important;}
  /*content misc - attach img*/.postbody .attachbox{background:#000!important;border:none!important;margin:0px 0px 0px 10px!important;}
  
  /*content misc - fix img*/.postbody{overflow-y:hidden!important;}
  /*content misc - fix img*/.postbody img[alt="圖檔"]{width:100%!important;}
  /*content misc - fix img*/.postbody .attach-image{max-height:initial!important;}
  /*content misc - fix img*/.postbody .attach-image img{width:100%!important;}
}
@-moz-document regexp("http(s)?://www\.cgvisual\.com/forum/(viewforum|viewtopic).*"){
  /*pages*/.topic-actions{margin:0px!important;height:initial!important;}
  /*pages*/.topic-actions>*{margin:0px!important;}
  /*pages*/.topic-actions>.search-box{display:none!important;}
  /*pages*/.topic-actions>.pagination{font-size:20px!important;line-height:30px!important;height:initial!important;}
  /*pages*/.topic-actions>.pagination span>*{font-size:30px!important;line-height:100%!important;background:transparent!important;margin:0px 2px!important;padding:5px 8px!important;}
  /*pages*/.topic-actions>.pagination span>strong{background:#053!important;border-radius:30%!important;}
  /*pages*/.topic-actions>.pagination span>*:hover{background:#073!important;border-radius:30%!important;}
}
@-moz-document regexp("http(s)?://www\.cgvisual\.com/(desktop|cgnews|headlines|tuto|Shop|film|links|events).*"){
  /*separator*/body>table[width="865"]+p,
  /*separator*/body>table[width="865"]+p+br{
    display:none!important;}
  /*BG main*/html,body,
  /*thread box*/td[bgcolor="#F0F0F0"],.contentTxt_chi.style6,
  /*footer*/td[valign="bottom"][bgcolor="#EEEEEE"],
  /*date box*/td[background="design/new_BG.jpg"],
  /*links_title box*/td[bgcolor="#CCCCCC"]{
    background:#020!important;}
  /*BG sub*/td[bgcolor="#FFFFFF"]{
    background:#030!important;}
  /*content font*/font[size="2"][color="#666666"]  {color:#a11!important;font-size:20px!important;}
  /*content font*/font[size="2"][color="#000000"]  {color:#ccc!important;font-size:22px!important;line-height:105%!important;}
  /*content font*/font[size="2"][color="#444444"]>a{color:#c60!important;}
  /*resize*/body{width:877px!important;margin:auto!important;}
  /*more news*/#Layer1{position:unset!important;top:0px!important;left:0px!important;padding:20px!important;background:#333!important;}
}
