  /*page*/body>table[height="44"]>tbody>tr>td:nth-child(2) table[width="700"] span.txt4_eng a:visited{color:#888!important;}
  /*page*/body>table[height="44"]>tbody>tr>td:nth-child(2) table[width="700"] span.txt4_eng a>u{color:#0a0!important;}
  
  /*news box list*/
