  /**/       table[width="701"] td[width="349"]:nth-child(2){padding-left:5px!important;}
  /**/       table[width="701"] table[width="480"]{}
  /**/       table[width="480"]{}
  /**/       table[width="480"]>*>tr{background:#040!important;}
  /**/       table[width="480"]>*>tr>td{background:transparent!important;}
  /*title*/  table[width="480"]>*>tr:nth-child(1){}
  /*title*/  table[width="480"]>*>tr:nth-child(1)>td{background:#000!important;}
  /*title*/  table[width="480"]>*>tr:nth-child(1)>td font{color:#0a0!important;font-size:20px!important;}
  /*content*/table[width="480"]>*>tr:nth-child(2){}
  /*content*/table[width="480"]>*>tr:nth-child(2)>td{padding:0px 10px!important;}
  /*content*/table[width="480"]>*>tr:nth-child(2)>td>font[color="#666666"]{text-align:center!important;display:block!important;box-sizing:border-box!important;position:relative!important;left:-10px!important;}
  /*content*/table[width="480"]>*>tr:nth-child(2)>td>font[color="#666666"]{font-size:26px!important;line-height:100%!important;background:#000!important;width:104%!important;}
  /*content*/table[width="480"]>*>tr:nth-child(2)>td>font[color="#000000"]{font-size:20px!important;line-height:120%!important;}
  /*content*/table[width="480"]>*>tr:nth-child(2)>td>font[color="#000000"] bb{font-size:20px!important;line-height:100%!important;padding:5px 0px!important;background:#000!important;text-align:center!important;width:100%!important;display:block!important;}
  /*content*/table[width="480"]>*>tr:nth-child(2)>td>font[color="#000000"] b{color:#0a0!important;}
  /*content*/table[width="480"]>*>tr:nth-child(2) iframe{display:block!important;margin:0 auto!important;margin-top:10px!important;}
  /*img*/    table[width="480"]>*>tr:nth-child(3) img{display:block!important;margin:0 auto!important;}
  /*img*/    table[width="480"]>*>tr:nth-child(3) strong{padding:3px 0px!important;line-height:100%!important;text-align:center!important;width:100%!important;display:block!important;}
  /*img*/    table[width="480"]>*>tr:nth-child(3) strong font{color:#0af!important;font-size:20px!important;line-height:100%!important;}
  /*info*/   table[width="480"]>*>tr:nth-child(4){background:#222!important;}
