  /*info*/   table[width="480"]>*>tr:nth-child(4) table{background:transparent!important;}
  /*info*/   table[width="480"]>*>tr:nth-child(4) table td[width="20"]{display:none!important;}
  /*info*/   table[width="480"]>*>tr:nth-child(4) table td[width="187"] a{font-size:20px!important;line-height:100%!important;text-align:center!important;width:100%!important;display:block!important;}
  /*info*/   table[width="480"]>*>tr:nth-child(4) table td[width="187"] a:hover{color:#f60!important;background:#053!important;}
  
  
  /*post link collapse*/td[width="207"][height="24"]{height:0px!important;}
  /*BG sub sub*/td[height="27"]>table{
    background:#050!important;}
  /*link*/a:link{color:#c60!important;}
  /*search font*/.txt3_chi{color:#ccc!important;}
  /*select*/select{background:#000!important;color:#ccc!important;}
}
