css:
@-moz-document url("about:newtab") {
   
   /* Страница визуальных хакладок */
   
   #newtab-vertical-margin{background:url('http://i59.tinypic.com/2rm7b61.jpg')left repeat-y,url('http://i60.tinypic.com/10dw6mo.jpg') right repeat-y,url('http://i62.tinypic.com/2ptqjrc.jpg')top repeat-x,url('http://i58.tinypic.com/xmoa3m.jpg')bottom repeat-x, url('http://i61.tinypic.com/30kspdc.jpg')!important; background-size: 1%,1%,11%,11%, 20%!important;}
   
   
   #newtab-margin-bottom {display: none}
   
   #newtab-grid {margin-bottom: 120px; }
  
  
  /* Строка поиска */
  
   #newtab-search-text {border-radius: 50px; box-shadow: inset 0 3px 2px rgba(0,0,0,.7),0 -2px 1px #4C291A,0 2px 1px #A86C37, 0 3px 0 #A66A50; border: 0; height: 25px}
   
   #newtab-search-text:focus {box-shadow: 0 0 3px 3px #7DA6CD; background: white}
  
  
  /* Кнопка "Поиск" */

   #newtab-search-submit {border-radius: 20px; color: #EDB578; text-shadow: 0 1px 1px black,0px -1px 2px black, 1px 0 0 #B9A091;font-family: lucida grande!important;; height:35px;  box-shadow: inset 0 1px 3px -1px white,0 0px 0 1px #794226, 0 2px 1px #A86C37;-moz-margin-start: 5px; background: url('http://i68.tinypic.com/2jagvh2.jpg') no-repeat center; background-size: 50%!important;} 
   
  
  #newtab-search-submit:hover {background: url('http://i68.tinypic.com/2jagvh2.jpg') no-repeat center!important;; background-size: 50%!important;border: 0; box-shadow: inset 0 1px 3px -1px white,0 0px 0 1px #794226, 0 2px 1px #A86C37,inset 0 0 5px 2px #B77F5C !important;;-moz-margin-start: 5px; color: #FFEBE3; }
  
  
  #newtab-search-text:focus + #newtab-search-submit, #newtab-search-text + #newtab-search-submit:hover, #newtab-search-text[autofocus] + #newtab-search-submit {border: 0!important; background: -moz-linear-gradient(top, rgba(255,224,175,1) 0%, rgba(255,205,146,1) 12%, rgba(255,185,114,1) 23%, rgba(251,161,85,1) 40%, rgba(239,148,67,1) 61%, rgba(209,119,66,1) 100%); box-shadow: 0 0 0 2px #783403, 0 1px 0 2px #D4855E, 0 -2px 0 1px #E8C6AB; text-shadow: 0 1px 1px black,0px -1px 2px black, 1px 0 0 #B9A091, 0 -1px 0 #3A0E03; color: #FFF7F1 }
  
  /* Кнопка с лупой */
  
  #newtab-search-logo.magnifier {background-size: 70%;background-image: url("http://i57.tinypic.com/33398wn.jpg") !important;box-shadow: 0 0px 0 1px #794226,  0 2px 1px #A86C37;border-radius: 90px;}
  
  #newtab-search-logo.magnifier:hover {background-color: transparent; border: 0; border-radius: 90px; box-shadow: 0 0px 0 1px #794226, 0 2px 1px #A86C37,inset 0 0 5px 2px #B77F5C }
  
  #newtab-search-logo[active] {background-color: transparent!important; box-shadow: inset 0 5px 5px black,0 0px 0 1px #794226, 0 2px 1px #A86C37;border: 0; -moz-animation-duration: 0.2s;-moz-animation-name: push; -moz-animation-iteration-count:  1; -moz-animation-direction:  alternate; }
  

   
  @-moz-keyframes push {
  0% {
   
    box-shadow: inset 0 1px 5px black,0 0px 0 1px #794226, 0 2px 1px #A86C37;
  }
    
     30% {
   
   box-shadow: inset 0 2px 5px black,0 0px 0 1px #794226, 0 2px 1px #A86C37
  }
    
  50% {
   
    box-shadow: inset 0 3px 5px black,0 0px 0 1px #794226, 0 2px 1px #A86C37
  }
    
      80% {
   
    box-shadow: inset 0 4px 5px black,0 0px 0 1px #794226, 0 2px 1px #A86C37
  }
    
   100% {
   
      box-shadow: inset 0 5px 5px black,0 0px 0 1px #794226, 0 2px 1px #A86C37
   }}
  
#newtab-search-form {
border-radius: 90px; height: 20px!important;;  background: url('http://i61.tinypic.com/wm1oa0.jpg') center; background-size: 100%!important; padding: 7px; box-shadow: 0 -1px 1px #4C291A,0 2px 1px #A86C37, 0 3px 1px black, 0 7px 4px -2px black, 0 -1px 0 #421D0A; border-right: 1px solid #84442B; border-left: 1px solid #661F09; border-top: 1px solid #9E6F5B;}
  
    /* Закрепленные визуальные вкладки */
  
  
  .newtab-site[pinned] .newtab-title { font-family: Segoe Script;
  
    background: #F6FF84;display: -moz-box;
 
    color: black;}
  
  .newtab-thumbnail {border-radius: 0}
  
   .newtab-link {border-radius: 0}
  
  .newtab-cell,.newtab-cell:hover {background:transparent!important}

  .newtab-site {box-shadow: 0 7px 4px -2px }

.newtab-control-pin:hover, .newtab-site[pinned] .newtab-control-pin:hover,.newtab-control-pin:hover:active, .newtab-site[pinned] .newtab-control-pin {
  background: url("http://i59.tinypic.com/169ntyt.jpg") no-repeat; background-size: 100%; width: 50px; opacity: 1; margin: -25px 120px; height: 100px;}
  


  
  
   .newtab-site[dragged]{box-shadow: none!important;}
  
  /* Пустые ячейки для вкладок */
  
.newtab-cell:empty,.newtab-cell:empty:hover {outline: 0; box-shadow: none!important;}
  

  

  
  /* Кнопка "Настройка страницы "Новая влкадка" */

  #newtab-customize-button, .newtab-customize {background-color: white!important; border-radius: 50px; background: url('http://i58.tinypic.com/2vxpqwm.jpg') center no-repeat; background-size: 70%;box-shadow: inset 0 -4px 4px rgba(0,0,0,.4), 0 0 0 4px white, 0 -1px 1px 4px #c4c4c4, 0 1px 1px 4px #EAEAEA; margin-top: 30px; margin-right: 30px;transition: 0.8s;transform: scale(1.5);  border: 0!important;}
  
   #newtab-customize-button:hover, .newtab-customize:hover{background-color: white!important; border-radius: 50px; background: url('http://i58.tinypic.com/2vxpqwm.jpg') center no-repeat; background-size: 70%;box-shadow: inset 0 -4px 4px rgba(0,0,0,.4), 0 0 3px 4px white, 0 0 6px  6px white;transform: scale(1.5); border: 0!important;}
  
   #newtab-customize-button:active, .newtab-customize:active{background-color: white!important; border-radius: 50px; background: url('http://i58.tinypic.com/2vxpqwm.jpg') center no-repeat; background-size: 70%;box-shadow: inset 0 4px 4px rgba(0,0,0,.7), 0 0 0 4px white, 0 0 6px  6px white;margin-top: 30px!important;; margin-right: 30px!important;transform: scale(1.5)!important; }
  
  #newtab-customize-button:-moz-any( :active, [active]){background-color: white!important; border-radius: 50px; background: url('http://i58.tinypic.com/2vxpqwm.jpg') center no-repeat; background-size: 70%;transform: scale(2.5);
-moz-animation-duration: 3s;-moz-animation-name: shine; -moz-animation-iteration-count:  1; -moz-animation-direction:  alternate; box-shadow: inset 0 4px 4px rgba(0,0,0,.7), 0 0 3px 4px white, 0 0 6px  6px white; margin-top: 7%; margin-right: 42%;border: 0!important;outline: 0!important;; }
  

   
  @-moz-keyframes shine {
  0% {
   
    box-shadow: inset 0 4px 4px rgba(0,0,0,.7), 0 0 2px 2px white, 0 0 6px  3px white;
  }
    
     30% {
   
    box-shadow: inset 0 4px 4px rgba(0,0,0,.7), 0 0 5px 2px white, 0 0 10px  7px white;
  }
    
  50% {
   
    box-shadow: inset 0 4px 4px rgba(0,0,0,.7), 0 0 7px 9px white, 0 0 16px  9px white;
  }
    
      80% {
   
    box-shadow: inset 0 4px 4px rgba(0,0,0,.7), 0 0 5px 2px white, 0 0 10px  7px white;
  }
    
   100% {
   
      box-shadow: inset 0 4px 4px rgba(0,0,0,.7), 0 0 3px 4px white, 0 0 6px  6px white;
   }}
  
  
  /* Окно с настройками "Новая вкладка" */
  
  #newtab-customize-panel-inner-wrapper {background-color: transparent!important;}
  
  #newtab-customize-panel[open="true"] {margin-right: 432.5px;margin-top: 320px;transition: all 1s ease 0s;transform: translate(-230px, 160px) scale(1) translate(230px, -200px);
}

#newtab-customize-panel {transition: all 0.7s ease 0s;}
  
  #newtab-customize-overlay {box-shadow: inset 0 0 220px 252px; background: transparent}
  
  
  #newtab-customize-panel-anchor {display: none}
  
  .newtab-customize-panel-superitem {line-height: 55px;}
  
  #newtab-customize-panel {margin: 15% 30%;;width: 450px;height: 370px; box-shadow: 0 0 8px; background: url('http://i61.tinypic.com/125kuow.jpg'); 
    
    box-shadow: inset 0 34px 54px -25px rgba(255,255,255,.4), inset 0 22px 24px -12px rgba(255,216,0,.2), 0 4px 20px; 
    
    border-style: solid; 
    
    border-width: 9px 7px 7px 9px;
    
    border-image: url('http://i61.tinypic.com/21c2vq.jpg') 18 13 17 15 stretch; 
    
    border-radius: 5px}
  
  .newtab-cell:hover {box-shadow: 0 0 20px 1px #82D9FF, 0 0 20px 2px #9EFBFF, 0 0 20px 3px white!important;}
  
 .newtab-customize-panel-item, .newtab-customize-complex-option, #newtab-customize-title {background: transparent}
  
  #newtab-customize-title > label {text-align: center!important; margin-left: 19.3%;}
  
  .newtab-customize-panel-subitem > .checkbox {border: 0; background-color: transparent}
  
  .newtab-customize-complex-option:hover > .selectable:not([selected]) + .newtab-customize-panel-subitem {background-color: transparent}
  
  .checkbox {background: url('http://i59.tinypic.com/k1oxmt.jpg')no-repeat; background-size: 100%!important;; padding: 20px!important;}
  
  .newtab-customize-panel-subitem[selected] > .checkbox {background: url('http://i57.tinypic.com/28a52yu.jpg') no-repeat;background-size: 100%;}
  
 .newtab-customize-panel-subitem > label, .newtab-customize-panel-item > label, .newtab-search-panel-engine > label, .newtab-customize-complex-option, .newtab-customize-panel-subitem > label {font-family: candara; color: #686B69; font-weight: normal!important;font-size: 12pt; font-family: lucida grande;
   
    text-shadow:0 2px 0 white, -1px 0 1px #303030, 2px 0 3px #303030}
  

#newtab-customize-classic  {margin-left: 60px; padding: 15px; background: transparent url("http://i61.tinypic.com/1z4elc3.jpg") no-repeat 17.5px 24.7px / 36px 36px;} 
  
  #newtab-customize-classic > label {margin-bottom: -120px; margin-left: 10px}
  
  #newtab-customize-classic:not([selected]) {background: transparent url("http://i62.tinypic.com/vhuce1.jpg") no-repeat 17.5px 24.7px / 36px 36px!important; opacity: 1!important;}
  
  #newtab-customize-enhanced {margin-left: 60px; margin-top: 20px; padding: 15px; max-width: 2000px; opacity: 1}

  .newtab-customize-panel-item[selected]{background: transparent url("http://i61.tinypic.com/1z4elc3.jpg") no-repeat 17.5px 13.4px / 36px 36px;}
 
 .newtab-customize-complex-option > .selectable:not([selected]){background: transparent url("http://i62.tinypic.com/vhuce1.jpg") no-repeat 15.5px 7.6px / 36px 36px!important;; opacity: 1!important;}
  
   .newtab-search-panel-engine[selected] {background: transparent url("http://i61.tinypic.com/1z4elc3.jpg") no-repeat 15px 15px / 36px 36px;}
  

.newtab-customize-panel-superitem {-moz-padding-start: 55px!important;;}
  
  
    .newtab-customize-panel-item > label{margin-left: 50px;}
  
  #newtab-customize-blank {margin-left: 62.5px; margin-top: 20px; }
  
   #newtab-customize-blank:not([selected]) {background: transparent url("http://i62.tinypic.com/vhuce1.jpg") no-repeat 15.5px 13.4px / 36px 36px!important; opacity: 1!important;}
  
  #newtab-customize-blank > label {margin-left: 22px!important;}

  .newtab-customize-panel-subitem:not([selected]) {background: transparent url("http://i62.tinypic.com/vhuce1.jpg") no-repeat 15.5px 13.4px / 36px 36px!important; opacity: 1!important;}
  
   #newtab-customize-blank[selected] {background: transparent url("http://i61.tinypic.com/1z4elc3.jpg") no-repeat 15.5px 13.4px / 36px 36px;}
  
  #newtab-customize-title {max-width: 2000px!important;display: block;margin-left: 13.5px !important;}
  
  #newtab-customize-learn {margin-left: 62.8px;max-width: 300px!important; width: 350px;background: transparent url("http://i58.tinypic.com/b3pes2.jpg") no-repeat 15px 13.4px / 36px 36px; opacity: 1!important;}
  
  #newtab-customize-learn:hover {background-color: transparent; }
  
  #newtab-customize-learn > label {width: 1500px!important; margin-left: 22px;}
  
      .newtab-control-block:-moz-locale-dir(ltr), .newtab-control-pin:-moz-locale-dir(rtl) {background: url('http://i61.tinypic.com/28m2s1y.jpg') no-repeat center; background-size: 80%!important; height: 40px; width: 40px; background-color:  white; border-radius: 50px; box-shadow: 0 0 15px white;transition: .2s}
  
 .newtab-control-block:hover{background: url('http://i58.tinypic.com/2py946w.jpg') no-repeat -2px 10px!important; background-size: 120%!important;background-color:  white!important;; border-radius: 50px; box-shadow: 0 0 15px #fff;height: 60px; width: 60px; transition: .3s}
  

  
  
  
  
  
  
  
  #newtab-search-manage > label {font-family: Comic Sans MS; font-style: italic; }
  
  

  

 }
