css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("emergentor.org") {
  
  
  @font-face {
  font-family: 'Exo 2';
  font-style: normal;
  font-weight: 300;
  src: local('Exo 2 Light'), local('Exo2-Light'), url(https://fonts.gstatic.com/s/exo2/v3/ZvqiGg27-1PW7JHPGOnJEHYhjbSpvc47ee6xR_80Hnw.woff2) format('woff2');
}
@font-face {
  font-family: 'Exo 2';
  font-style: normal;
  font-weight: 400;
  src: local('Exo 2'), local('Exo2-Regular'), url(https://fonts.gstatic.com/s/exo2/v3/67PFCF-E5tJ4OT0xf2g4_6CWcynf_cDxXwCLxiixG1c.woff2) format('woff2');
}
@font-face {
  font-family: 'Exo 2';
  font-style: normal;
  font-weight: 600;
  src: local('Exo 2 Semi Bold'), local('Exo2-SemiBold'), url(https://fonts.gstatic.com/s/exo2/v3/LFe3mB-q6jNXyAcQ2iPxAXYhjbSpvc47ee6xR_80Hnw.woff2) format('woff2');
}

  
#footer {background-color: #292B2E; line-height: normal;}
#copyleft > a {color:#292B2E; font-weight: 200;}
  #copyleft {width: 200px; margin: 0 auto; background-color: #555}

div.pagenav_c {
  width: intrinsic;
  width: -moz-max-content;
  width: -webkit-max-content;
  padding:0 15px;
}
/* div.post {padding:0; margin:15px 15px;}*/
  
div.replycontainer > div.post {padding:0;margin:0;}
  
div.posttext_gradient {display: none !important;} 
html, body    {
font-family: "Exo 2", Helvetica, sans-serif; -webkit-font-smoothing: antialiased; font-weight: 300;
} 
  
div.posttext  {
font-family: "Exo 2", Helvetica, sans-serif; -webkit-font-smoothing: antialiased; 
  font-size: 13pt; color:#4d4d4d;  line-height: normal;
  margin: 10px 0;
  background-color: #ebebeb;
  padding: 15px 20px;
} 
  
#main_content_inner > div:nth-of-type(2) > div.post_r {
margin: 0 auto;
  width: 700px;
}
 
  div.userbar {height: 30px; background-color: #323437; line-height: normal; color: #323437}
  div#header {padding: 0;margin-bottom: 10px;}
  div.pagenav {margin-bottom: 10px; margin-top: 10px;}
  div.pagenav_c {background-color: #323437;}

div.post_r, div.replycontainer {
background-color: #323437;
padding: 0;
}  
  
div.postinfo {
  line-height: normal; 
  padding: 10px 20px 0 20px;
  width: 395px;
}
  
  div.postbottom {line-height: normal; padding-left: 20px; padding-right: 20px; padding-bottom: 10px; padding-top: 0px; width: 395px;
/*     padding-top:22px; */
    color: #323437;
  }
  
  a.postinfo, a.postreply {
    text-shadow: 0px 20px 30px rgba(255, 255, 255, 1),0px 0px 15px rgba(255, 255, 255, 0.1); 
  }
  div.commentscount > a.postinfo {text-shadow: none;}
  a.profile_act {margin-right: 5px;}
  a.postinfo { text-align:left; color: #AAA;}
   
  div.file {text-align: right;}
 
  
  div.innerblock {
    margin: 10px 0;
  background-color: #4d4d4d;
  padding: 15px 20px;
    color:#ebebeb;
  }
  
  div.replycontainer {width: 435px;} 
  div.replycontainer > div.post_t {padding: 0;}
  div.replycontainer > div.post_t > div.post_r {margin: 0 auto; width: 100%;}
  
  div.post_r {width: 435px; }
 
  
  
  a{
	color:#777;
	text-decoration:none;
}

a:visited{
/* 	color:#957b8d; */
/* 	color:#777; */
}

a:hover{
/* 	color:#555; */
	text-decoration:underline
}
  
/*   
  div.posttext > p > a {text-align: underline;} */
  

}
