css:
@-moz-document domain("hipchat.com") {
#hipchat .hc-tab .aui-nav-item,
body .hc-tab .aui-nav-item {
	padding: 3px 30px 3px 10px;
	/*    xpadding: 9px 30px 10px 10px; */
	font-size: 13px;
}
#hipchat .hc-tab-close,
body .hc-tab-close {
	padding: 3px 5px 3px 5px;
	/*     padding: 10px 5px 3px 5px; */
}

#hipchat .hc-right-sidebar-col .hc-roster-item .aui-nav-item, body .hc-right-sidebar-col .hc-roster-item .aui-nav-item {
    padding: 2px 3px;
}

.hc-chat-msg .msg-line {
    margin: 0 0 0px 0;
}

.hc-chat-row div {
    font-size: 13px;
    line-height: 16px;
}

.hc-chat-row {
    padding: 2px 0px;
}

.hc-classic-neue .hc-chat-from {
    padding: 1px 10px;
}
}
