/* Main Elements */
html {
    color: #aaa;
    background-position: center -60px;
    background-size: 100% auto;
    font-family: "Nunito", "Helvetica Neue", "Helvetica", "Arial", sans-serif;
    font-weight: 300;
    background-blend-mode: hard-light;
    background-image: linear-gradient(to bottom, rgba(0,0,0,0.9) 60px, rgba(31,59,8,0.7) 180px, rgba(0,0,0,1) 430px), url(https://s.yimg.com/dh/ap/fantasy/img/background-football@2x.jpg);
}
body {
    font-size: 15px;    
}
.Page {
    display: block;
}
/* Ads, go away! */
#masterNav, #yspmh, #uhWrapper, #yspcontentuh, #fantasyhero, .df-ad, section.Ad, #gamepromo, .RailSkyAd, .CAN_ad {
    display: none !important;
}

/* "Header" */
#league-info.Bg-transparent {
    height: 160px;
    width: 700px;
    padding: 0 0 0 16px;
    display: flex;
    align-items: center;
}
#league-info .Ta-end.Mbot-xs {
    text-align: left;
    text-shadow: 0px 1px 3px rgba(0,0,0,.5);
}
#league-info .Ta-end.Mend-sm, #league-info .Ta-end.Mend-med {
    font-family: 'Alegreya Sans SC';
    font-weight: 700;
    font-size: 48px;
    line-height: 42px;
    color: rgba(255,255,255,.95);
    text-shadow: 0 0 9px rgba(20,255,20,0.5) , 0 4px 0 rgba(40,40,40,0.83)
}

/* Dark backgrounds */
.template-html5 .Subnav-main-wrap:before, .template-html5 .Subnav-main-wrap:after,
.template-html5 .Page-bd:before,
.template-html5 .Page-bd:after,
.template-html5 .Page-ft:before,
.template-html5 .Page-ft:after {
    background-color: #000;
}
.template-html5 .Subnav-main-wrap, .template-html5 .Page-bd, .template-html5 .Page-ft,
.Page-maincontent, .Page-footercontent, .Page-ft,
.Mod {
    background: #222;
}
.Subnav-main {
    background: #555;
}
.Table td.Selected, .Table-interactive tr:hover > td, .Table-interactive tr:hover > td.Selected, .Table th.Selected,
#Stencil .Linkable:hover,
.Table tr.Selected td,
.Nav-flyout .Navtarget:hover {
    background: #292929;    
}
.Desktop .ysf-rosterswapper:not(.swapping) tbody tr:hover, .Desktop .ysf-rosterswapper:not(.swapping) tbody tr:focus {
    background: #373737;
}
.Table thead > tr:not(.First), .Table thead > tr.First.Last {
    background: #444;   
}
.Nav-h:not(.Nav-main) {
    border-bottom-color: #111;
    border-top-color: #111;
}
.Subnav-main,
#Stencil .Bg-shade2 {
    background: rgba(0,0,0,.8);
}
.Nav-bot-pointers-north > .Hovered > .Navpointer {
    border-bottom: 5px solid rgba(0,0,0,.8);
}
#Stencil .Bdr, #Stencil .Bdrx, #Stencil .Bdry, #Stencil .Bdrtop, #Stencil .Bdrbot, #Stencil .Bdrstart, #Stencil .Bdrend,
.Table td,
.List-rich > .Listitem {
    border-color: #333;
}
#Stencil .F-shade {
    color: #ddd;
}
#Stencil .Bg-shade {
    background: #444;
}
.Thm-toyota .Thm-inherit .Bd, .Thm-toyota.Thm-override .Bd,
#Stencil .Bg-overlay,
.Nav-flyout > .Nav-v {
    background-color: #111;
}
.Thm-toyota .Thm-inherit .Hd, .Thm-toyota.Thm-override .Hd,
.Thm-toyota .Thm-inherit, .Thm-toyota.Thm-override {
    color: #aaa;
}
#Stencil .F-reset, a {
    color: #ddd;
}
.Input, select,
#slogan-content.slogan-default, #slogan-content.editing, #slogan-content.is-editable:hover, #slogan-content.is-editable:focus, #notetoself-content.editing, #notetoself-content.is-editable:hover, #notetoself-content.is-editable:focus {
    background-color: #393939;
    border-color: #444;
    color: #ddd;
}
.Nav-h:not(.Nav-main) > .Navitem.Selected > .Navtarget {
    color: #aaa;
}
.F-page-header, .Hd {
    border-bottom: 1px solid #555;
    box-shadow: 0px 1px 0px rgba(0,0,0,0.2);
}

/* Link colors */
p a, td a,
#Stencil .F-link {
    color: #ADD4FF;
}
.Btn, .Btn-secondary {
    background-color: #393939;
    border: 1px solid #444;
    color: #ADD4FF;
}

/* table widths and alignments */
.Table, .Table-plain {
    font-size: 14px;
}
.Table th.Selected {
    color: #ddd;
}
#Stencil .ta-End, .Page-noexpand .RailMain {
    width: auto !important;
}
.ysf-rosterswap-manager .Ta-end {
    width: auto;
}
.First th[title="Return"] div,
.First th[title="Miscellaneous"] div,
.First th[title="Point After TD"] div,
.First th[title="Touchdowns"] div {
    text-align: right;
}
.Table th, .Table th a {
    color: #ccc;
}

/* Avatars */
.Avatar-xs, .Avatar-sm, .Avatar-med, .Avatar-lg {
    border-radius: 10%;
    background-image: url('http://i.imgur.com/QVn2xyL.gif');
    background-size: 100%;
    background-blend-mode: luminosity;
    background-color: #7C9683;
}
.Avatar-xs {
    width: 32px;
}
.Avatar-sm {
    width: 48px;
}
.Avatar-med {
    width: 72px;
}
.Avatar-lg {
    width: 128px;
}
.Grid-u-1-3 .Avatar-lg {
    max-width: 95%
}

/* Player notes and video icons */
@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
  .yfa-icon.playernote-new, .yfa-icon.playernote-recent, .yfa-icon.playernote-old, .yfa-icon.video-new, .yfa-icon.video-recent, .yfa-icon.video-old {
    background-image: url('http://i.imgur.com/lkZgu8T.png');
    background-size: auto 84px;
  }
}
.yfa-icon.playernote-new, .yfa-icon.playernote-recent, .yfa-icon.playernote-old, .yfa-icon.video-new, .yfa-icon.video-recent, .yfa-icon.video-old {
    background-image: url('http://i.imgur.com/lkZgu8T.png');
    background-size: auto 84px;
}

/* Smooth Hoperators easter egg */
@keyframes fadein {
    0%   { opacity: 0; }
    100% { opacity: 1; }
}
@keyframes fadeout {
    0%   { opacity: 1; }
    100% { opacity: 0; }
}
#matchupweek li.Linkable[data-target^="/f1/187582"] .Grid-bind-end > .Grid-u.Phone-grid-u-2-3 {
    overflow: visible;
}
#matchupweek a.Grid-u[href="/f1/187582/1"] {
    position: relative;
}
#matchupweek a.Grid-u[href="/f1/187582/1"]:after {
    color: #555;
    display: block;
    position: absolute;
    width: 135px;
    padding: 8px;
    background: rgba(255,255,255,.8);
    font-size: small;
    border-radius: 10px;
    content: '\"You know what your team needs?\"';
    text-align: center;
    left: 80px;
    top: -6px;
    white-space: normal;
    opacity: 0;
}
#matchupweek a.Grid-u[href="/f1/187582/1"]:before {
    position: absolute;
    left: 68px;
    top: 6px;
    content: '\a';
    height: 0;
    width: 0;
    border-top: 6px solid transparent;
    border-right: 12px solid rgba(255,255,255,.8);
    border-bottom: 6px solid transparent;
    opacity: 0;
}
#matchupweek a.Grid-u[href="/f1/187582/1"]:after,
#matchupweek a.Grid-u[href="/f1/187582/1"]:before {
	animation: fadein 1s 6s forwards, fadeout 1s 18s forwards;
}

/* Player Popups */
.yui3-ysplayernote .playernoteview {
    width: 100%;
}
.Ta-c.Bdrbot.Py-lg, .yui3-widget-ft {
    display: none;
}
.yui3-ysplayernote .dynamicnote-bd, .yui3-ysplayernote .teamsched-bd  {
    background-color: #333;
}
.yui3-ysplayernote .teamsched-ft p, .yui3-ysplayernote .playernotes-ft p {
    background-color: #222;
}
.teamsched-bd .even {
    background-color: #393939;
}
.teamsched-bd .teamtable td, .yui3-ysplayernote .teamsched,
.yui3-ysplayernote .teamsched-ft, .yui3-ysplayernote .playernotes-ft,
.yui3-ysplayernote .playerdata {
    border-color: #444;
}
.yui3-ysplayernote .teamsched-ft p, .yui3-ysplayernote .playernotes-ft p {
    border-color: #222;    
}
.yui3-ysplayernote .teamsched {
    background-color: #444;
}
.yui3-ysplayernote .teamsched .teamtable th div {
    border-left-color: #333;
}
.yui3-ysplayernote .teamsched-bd h5 {
    background-color: #292929;
}
.yui3-ysplayernote .playerdata, .yui3-ysplayernote .playernotes, .yui3-ysplayernote .playernoteview-notes {
    background-color: #333;
}
.yui3-ysplayernote p.rating-value,
.yui3-ysplayernote p.rating-comment,
.yui3-ysplayernote #playernotes-pointsagainst {
    color: #ddd;
}
.yui3-ysplayernote .playernoteview-notes {
    height: 400px;
}
.yui3-ysplayernote .playernotes-bd {
    height: 380px;
}
.yui3-ysplayernote .teamsched {
    height: 360px;
}

/* Twitter Feed */
html, .SandboxRoot, .p-author .profile .p-name, .custom-timeline-owner-profile .p-name, .cards-base p, .cards-base p a, .timeline-header .custom-timeline-summary, .timeline-header .custom-timeline-summary a:link, .timeline-header .custom-timeline-summary a:visited, .timeline-header .summary, .timeline-header .summary a:link, .timeline-header .summary a:visited, .p-author a.profile:hover .p-name, .p-author a.profile:focus .p-name {
    color: #aaa;
}
.customisable-border {
    border-color: #444;
}
.tweet-box-button {
    background: #292929;
    border: 1px #444 solid;
}
}
