  .navbar {
    position: relative;
    height: 53px;
    background: none repeat scroll 0% 0% #c2c3c4;
    margin: 10px 0px 0px;
    color: #FFF;
    border: 0px solid #FFF;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
  }
  
  .navtabs {
    background: #E2E3E4;
    padding: 0px 0px 0px 20px;
    height: 26px;
    border: none;
}
  
.navtabs ul {
    border-top: none;
    border-radius: 0px;
  }
  
  .navtabs.navtabs li a.navtab {
    border: none;
    display: block;
    background: #d2d3d4;
    min-width: 60px;
    width: auto !important;
    text-align: center;
    color: #000;
    padding: 0px 10px;   
}
  .navtabs li.selected {
    height: 26px;
    color: #000;
    background-color: #F1353C;
    border: none;
}
  
.navtabs li {
    float: left;
    border-right: none;
}
  
.navtabs li:first-child {
    border-left: none;
}
  
.navtabs li.selected a.navtab {
    color: #fff;
    background: #467784;
    position: relative;
    top: -4px;
    padding-top: 4px;
    z-index: 10;
    border: none;
}

.globalsearch {
    position: absolute;
    width: 180px;
    right: 20px;
    top: 0px;
    border: none;
    float: right;
    clear: left;
    text-align: right;
    line-height: 1em;
    display: block;
}
  
.globalsearch .textboxcontainer {
    height: 26px;
    border: none !important;
    display: inline-block;
    float: left;
    text-align: right;
}
  
