css:
@-moz-document domain("play.pokemonshowdown.com"), domain("replay.pokemonshowdown.com") {

/*header CSS :]*/

.header {
    background: black;
}
.button.cur,
.button.cur:hover {
    background: black;
    color: white;
}
.maintabbarbottom {
    background: black;
}
/*header CSS :] end */
/*Popup CSS*/
.select, .team {
    background:black;
    color:white;
}
.select:hover, .team.team-hover {
    background:white;
    color:black;
}

.setdetails .detailcell:hover{
    color:black;
}

.setdetails, .setstats:hover{
    color:white;
}

.popupmenu button:hover{
    background:white;
    color:black;
}

.setdetails, .setstats:hover{
    background:white;
    color:black;
}

.select, .team{
    font-family:"comic sans ms";
}
.setchart .setstats label:hover{
    color:black;
}
                                            
.pm-log {
    background: black;
    color: white;
}
.pm-window h3 {
    background: black;
    color: white;
}
.pm-window.focused h3 {
    background: black;
    color: white;
}

.ladder th{
    color:black;
}
.pm-log-add {
    background: black;
}

.select.teamselect {
    background:black;
    color:white;
}
.ps-popup {
    background: black;
    color: white;
}
.popupmenu button {
    color: white;
    font-family:"Comic Sans MS";
}
.challenge {
    background: black;
    color: white;
    border: 1px black
}
/*popup css done*/
/*Button CSS and the userlist CSS*/

button {
    border: 2px solid white;
    background-color: black;
    color: white;
}
.closebutton,
.minimizebutton {
    color: red;
}
.menugroup .button{
    font-family:"comic Sans MS";
}
.userlist,
.userlist li {
    background: black;
    color: white;
}
.menugroup .button {
    background: black;
    color: white;
}
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    background: black;
    color: white;
}
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    background: white;
    color: black;
}
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button {
    background: black;
    color: white;
}
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover {
    background: white;
    color: black;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button {
    background: black;
    color: white;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover {
    background: white;
    color: black;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button {
    background: black;
    color: white;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover {
    background: white;
    color: black;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button {
    background: black;
    color: white;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover {
    background: white;
    color: black;
}
.mainmenu .menugroup:nth-child(1) .button {
    background: black;
    color: white;
}
.mainmenu .menugroup:nth-child(1) .button:hover {
    background: white;
    color: black;
}
.setmenu button {
    background: white;
    color: black;
    font-family:"Comic Sans MS";
}
.menugroup{
    background:black;
}
.roomlist a.ilink {
    background: black;
    color: white;
}
/*Button And Userlist CSS end*/
/*Room CSS*/

body {
    background: url("http://i.ytimg.com/vi/KsHUrwV0Kek/maxresdefault.jpg");
                                          
    font-family: "Comic Sans MS", Times, serif;
}
.chat-log {
    background: black;
    color: white;
    size: cover;
}
.chat-log-add {
    background: black;
}
/*Room CSS End*/
/*Battle CSS*/

.ps-room.ps-room-opaque {
    background: black;
    color: white;
}
.ps-room .battle-log{
    color:white;
}
.ps-room .battle-controls {
    background: black;
}

.battle-log {
   color:white;
    font-family:"Comic Sans MS";
}
.rightbar,
.leftbar {
    background: black;
    color: white;
}
.battle-log {
    background: black;
    color: white;
}
.battle-log .chat em {
    color: black;
}
.battle-log h2 {
    background: white;
    color: black;
}
.tournament-status,
.tournament-toggle {
    background: black;
    color: white;
}

.tournament-bracket-tree-node-match-team-draw, .tournament-bracket-tree-node-match-team-loss{
    stroke:white;
}
.backdrop:before {
    display: block;
    content: "";
    background: url(http://images.forwallpaper.com/files/thumbs/preview/30/303653__dark-starry-night-sky_p.jpg);
    width: 753px;
    height: 500px;
    background-size: cover;
}
.ps-room .battle-log {
    color: white;
}
    .battle-log .chat em{
 color:white;                                           
}
/*Battle css done*/
/*Teambuilder CSS*/

.teamlist {
    background: black;
}
.ps-room.ps-room-light {
    background: black;
    color: white;
}
.teamlist .team {
    background: black;
    color: white;
}
.utilichart .itemdesccol {
    color: white;
}
.teambuilder-results .firstresult a,
.teambuilder-results .firstresult a:hover {
    background: black;
}
.teambuilder-results h3,
.teambuilder-results .utilichart h3 {
    background: white;
    Color: black;
}
.utilichart .itemdesccol,
.utilichart .abilitydesccol,
.utilichart .movedesccol {
    color: white;
}
.utilichart .col {
    color: white;
}
.setchart input, .setchart-nickname input, .statform input.inputform{
    font-family:"comic sans ms"
}
.setcell-details {
    background: black;
    color: white;
}
.teamchart,
.teamchart li {
    background: black;
    color: white;
}
.utilichart .col:hover{
    color:black;
}
.setcol-icon {
    background: black none no-repeat scroll center top;
}
.setchart label,
.setchart-nickname label {
    background: black;
    color: white;
}
.setdetails,
.setstats {
    background: black;
    color: white;
}
.setstats .statrow-head em {
    color: white;
}
.setstats .statrow-head em {
    color: white;
}
.jslider .jslider-label {
    color: white;
}
.setcol-moves .setcell {
    background: black;
}
.setchart {
    background: black;
}
.pokemonedit {
    background: black;
    color: white;
}
.pokemonedit:hover {
    background: white;
    color: black;
}
/*end of teambuilder css*/
/*Tournament CSS*/

.ps-room .battle-log-add {
    background: black;
}
.tournament-bracket {
    background: black;
    color: white;
}
.tournament-bracket-tree-node-loss>rect {
    color: white;
    background: red;
}
}
