css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("basilmarket.com") {
  
  /* Colors */

  body {
    background-color: /*[[bgreg]]*/ !important;
  }
  
  a {
    color: /*[[textreg]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  a:hover {
    color: /*[[textlt]]*/ !important;
  }
  
  /* removes all text and box shadows */
  * {
    text-shadow: none !important;
    box-shadow: none !important;
  }
  
  /* Navigation Bar */
  
  header {
    background-color: /*[[navbar]]*/ !important;
  }
  
  nav > ul > li > a:hover, nav > ul > li:hover > a {
    background-color: /*[[bglt]]*/ !important;
  }
  
  nav > ul > li ul {
    padding: 0 !important;
    background-color: /*[[bglt]]*/ !important;
  }
  
  nav > ul > li > ul li {
    padding: 0 20px !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  nav > ul > li > ul li:hover {
    background-color: /*[[bglter]]*/ !important;
  }
  
  /* Notice Message */
  
  .notice-message {
    background-color: /*[[yellow]]*/ !important;
    color: /*[[textreg]]*/ !important;
  }
  
  .notice-message a {
    border-color: /*[[link]]*/ !important;
  }
  
  .notice-message a:hover {
    color: /*[[link]]*/ !important;
  }
  
  /* Title Bar Colors */
  
  .topic-bar {
    background-color: /*[[blue]]*/ !important;
    color: /*[[textreg]]*/ !important;
  }
  
  .color-1, .color-2 {
    background-color: /*[[green]]*/ !important;
  }
  
  .color-4 {
    background-color: /*[[orange]]*/ !important;
  }
    
  .color-5 {
    background-color: /*[[bgdk]]*/ !important;
    color: /*[[textdk]]*/ !important;
  }
  
  .color-6 {
    background-color: /*[[yellow]]*/ !important;
  }
    
  .color-7 {
    background-color: /*[[red]]*/ !important;
  }
  
  /* List Items */
  
  .list-top, .list {
    background-color: transparent !important;
    color: /*[[textdk]]*/ !important;
    border: none !important;
  }
  
  .list-top:hover, .list:hover {
    background-color: /*[[bglt]]*/ !important;
  }
  
  .list.new {
    border-left: 3px solid /*[[bgdk]]*/ !important;
  }
  
  .list h3, .list > span {
    background-color: /*[[bglt]]*/ !important;
    color: /*[[textdk]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  .list:hover h3, .list:hover > span {
    background-color: /*[[bglter]]*/ !important;
  }
  
  /* Footer */
  
  footer, footer .title {
    background-color: transparent !important;
  }
  
  footer .title a {
    background-color: /*[[bgdk]]*/ !important;
  }
  
  footer .title a:hover {
    background-color: /*[[bglt]]*/ !important;
  }
  
  /* Input */
  
  input[type="text"]:not(.color-mp), input[type="password"], textarea {
    background-color: transparent !important;
    color: /*[[textdk]]*/ !important;
    border: none !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  input[type="text"]:not(.color-mp):hover, input[type="password"]:hover, textarea:hover, input[type="text"]:not(.color-mp):focus, input[type="password"]:focus, textarea:focus {
    background-color: /*[[bglt]]*/ !important;
    color: /*[[textlt]]*/ !important;
  }
  
  .form-field {
    background-color: transparent !important;
  }
  
  .title-button {
    background-color: /*[[bgdk]]*/ !important;
    color: /*[[textdk]]*/ !important;
  }
  
  .title-button:hover {
    background-color: /*[[bglt]]*/ !important;
  }
  
  .form-button {
    background-color: transparent !important;
  }
    
  #main-form {
    background-color: /*[[bgdk]]*/ !important;
    color: /*[[textreg]]*/ !important;
  }
  
  #main-form input[type="submit"], .form-button input[type="submit"] {
    background-color: /*[[green]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  /* Titles */
  
  .title h1, .title h2 {
    color: /*[[textreg]]*/ !important;
  }
  
  .title a {
    background-color: /*[[bgdk]]*/ !important;
  }
  
  .title a:hover {
    background-color: /*[[bglt]]*/ !important;
  }
  
  /* Threads */
  
  article {
    background-color: /*[[bglt]]*/ !important;
    color: /*[[textdk]]*/ !important;
    border-color: /*[[bgdk]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  article:hover {
    background-color: /*[[bglter]]*/ !important;
    color: /*[[textreg]]*/ !important;
  }
  
  article .p a, article .p .link-other {
    border-bottom: 2px solid /*[[link]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  article .p a:hover, article .p .link-other:hover {
    color: /*[[link]]*/ !important;
  }
  
  article .details, article .tools, .list-tag {
    background-color: transparent !important;
  }
  
  article .author {
    color: /*[[textdk]]*/ !important;
    opacity: 1 !important;
  }
  
  article .job, article .author:hover, article .tools span {
    color: /*[[textreg]]*/ !important;
  }
  
  .post-likes {
    background-color: /*[[yellow]]*/ !important;
    color: /*[[bgreg]]*/ !important;
    border: none !important;
    margin: -10px !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
    
  article:hover .post-likes {
    opacity: 1 !important;
  }
    
  .pages {
    margin: 0 !important;
  }
    
  .pages strong {
    color: /*[[textlt]]*/ !important;
  }
    
  .pages span {
    color: /*[[textdk]]*/ !important;
  }
    
  .pages a, .pages strong {
    padding: 5px 10px !important;
    margin: 0 !important;
  }
    
  .pages a:hover {
    background-color: /*[[bglt]]*/ !important;
  }
    
  /* Option Buttons */
    
  .topic-options > div, .topic-options > a, .topic-options > span, .topic-options > input {
    background-color: /*[[bglter]]*/ !important;
    border-bottom: 2px solid /*[[bgdk]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
    
  span.color-actioned {
    background-color: /*[[green]]*/ !important;
    border: none !important;
  }
  
  .topic-options > span {
    color: /*[[textreg]]*/ !important;
  }
  
  .topic-options > input {
    cursor: auto !important;
    color: /*[[textreg]]*/ !important;
  }
  
  /* brighten text color on twitter and facebook buttons */
  .color-tw { color: #00ACED !important; }
  .color-fb { color: #5977B6 !important; }
  
  /* Wiki */
  
  .text {
    background-color: /*[[bgreg]]*/ !important;
    color: /*[[textreg]]*/ !important;
    border: none !important;
  }
  
  .wiki-level-1, .wiki-level-2 {
    border: none !important;
  }
  
  .text h2 {
    border-bottom: 3px solid /*[[bgdk]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
    
  .text h2:hover {
    border-color: /*[[bglt]]*/ !important;
  }
  
  .text h2:hover a {
    color: /*[[textlt]]*/ !important;
  }
  
  .wiki-edit {
    background-color: transparent !important;
    padding: 5px !important;
  }
  
  .wiki-edit:hover {
    background-color: /*[[bglt]]*/ !important;
  }
  
  .wiki-stubs a, .link-site {
    border-bottom: 3px solid /*[[link]]*/ !important;
  }
  
  .wiki-stubs a:hover, .link-site:hover {
    color: /*[[link]]*/ !important;
  }
    
  .text table th {
    background-color: /*[[bgdk]]*/ !important;
      color: /*[[textdk]]*/ !important;
  }
    
  .text table th, .text table td {
    border-color: /*[[bgdk]]*/ !important;
  }
    
  .text table tr {
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
    
  .text table tr:hover {
    background-color: /*[[bglt]]*/ !important;
    color: /*[[textlt]]*/ !important;
  }
  
  /* Confirmation */
  
  #confirm {
    background-color: rgba(50, 50, 50, 0.7);
  }
    
  #confirm-wrap {
    background-color: /*[[bgreg]]*/ !important;
  }
    
  #confirm-wrap p {
    color: /*[[textreg]]*/ !important;
  }
  
  #confirm-yes { background-color: #329664 !important; }
  #confirm-no { background-color: #B43232 !important; }
  
  /* Create Thread */
    
  .flex .box > h1 {
    background-color: /*[[bgdk]]*/ !important;
    color: /*[[textdk]]*/ !important;
  }
    
  .flex .box > span {
    background-color: /*[[bglt]]*/ !important;
    color: /*[[textreg]]*/ !important;
    -moz-transition: all .2s;
	-webkit-transition: all .2s;
  }
  
  .flex .box > span:hover, .flex .box .selected {
    background-color: /*[[bglter]]*/ !important;
    color: /*[[textlt]]*/ !important;
  }

  /* General */

  /* prevent navbar from following page scroll */
  header, #logo, #hamburgers {
    position: absolute !important;
  }
  
  /* move player details on thread posts to top*/
  article .avatar { margin-bottom: 0 !important; }
  article .p { margin-left: 0 !important; }
  
  
  /* removes various things */
  body > div:nth-of-type(2), #show-thread article.main.comment > div:nth-of-type(2), #show-front .column-3-1 > .topic-options, #show-thread #main > div:first-child > div:nth-of-type(4) {
    display: none !important;
  }
  
  body {
    margin-top: 60px !important;
    -moz-animation: fadein .7s;
    -webkit-animation: fadein .7s;
  }
  
  @keyframes fadein {
      from { opacity: 0; }
      to { opacity: 1; }
  }

  /* removes widescreen */
  #main {
    max-width: 1200px !important;
  }
  
  /* Title Bars */
  
  .topic-bar {
    padding: 6px !important;
    text-align: center !important;
  }
  
  /* Nav Bar */
  
  header {
    padding: 0 !important;
    height: 40px !important;
  }
  
  #logo, nav > ul > li > a {
    line-height: 40px !important;
  }
  
  /* Notice Message */
  .notice-message {
    font-size: 15px !important;
    padding: 10px !important;
  }
  
  /* Front Page */
  
  #show-front .column-3-2 {
    width: 69% !important;
    float: left !important;
    margin-left: 0 !important;
  }
  
  #show-front .column-3-1, #show-front .column-3-3 {
    width: 30% !important;
    margin-bottom: 10px !important;
    float: none !important;
  }
  
  #show-front .column-3-1 > a:nth-of-type(n+12), #show-front .column-3-3 > a:nth-of-type(n+11):nth-of-type(-n+20) {
    display: none !important;
  }
  
  /* hide screens and videos */
  #show-front .media-thumb {
    min-height: 0 !important;
    max-height: 0 !important;
    margin-bottom: 0 !important;
    visibility: hidden !important;
    opacity: 0 !important;
    -moz-transition: all .4s;
    -webkit-transition: all .4s;
  }
  #show-front .column-3-2 > div:nth-of-type(1):hover + .media-thumbs .media-thumb, #show-front .media-thumbs:hover .media-thumb {
    min-height: 133px !important;
    max-height: 999px !important;
    margin-bottom: 10px !important;
    visibility: visible !important;
    opacity: 1 !important;
  }
  
  /* Threads */
  
  article {
    margin-bottom: 15px !important;
  }
  
  /* shrink text */
  article .p {
    font-size: 1em !important;
    line-height: 1.5em !important;
  }
  
  article .post-likes {
    margin-top: -108px !important;
  }
  
  #show-thread #main > div:nth-of-type(1) {
    display: table !important;
    width: 100% !important;
  }
  
  #show-thread .column-2-1 {
    display: table-footer-group !important;
    width: 100% !important;
    float: none !important;
  }
  
  #show-thread .column-2-2 {
    width: 100% !important;
    margin-top: -10px !important;
    float: none !important;
  }
  
  #show-thread .topic-options {
    text-align: right !important;
  }
  
  #show-thread .column-2-2 .width-100 > span, #show-thread .column-2-2 .width-100 > a, #show-thread .column-2-2 .width-100 > input {
    display: inline-block !important;
    width: 10% !important;
    margin: 0 0 0 1% !important;
    text-align: center !important;
  }
  
  #show-thread .column-2-2 .width-100 > input {
    -moz-transition: all .5s !important;
    -webkit-transition: all .5s !important;
  }
  
  #show-thread .column-2-2 .width-100 > input:hover {
    width: 15% !important;
  }
  
  #show-thread .column-2-2 .width-100 {
    margin: auto !important;
  }
  
  .pages {
    padding: 0 !important;
  }
  
  #main-form input[type="submit"], .form-button input[type="submit"] {
    margin: 0 !important;
    padding: 7px 15px !important;
  }
}
