 font-size:14px;
 background:linear-gradient(to bottom,rgba(255,255,255,0.6) 0%,rgba(255,255,255,0) 75%,rgba(255,255,255,0.6) 100%);
 background-color:#002b36;
}
 #AVE-DAG-content {
 height:calc(100% - 20px);
 background:linear-gradient(to bottom,rgba(255,255,255,0.8) 0%,rgba(255,255,255,0) 75%,rgba(255,255,255,0.8) 100%);
 background-color: #eee8d5;
}
