css:
@-moz-document domain("forums.bunsenlabs.org") {
.pun {
    width: 90% ! important;
    margin: auto ! important;
    max-width: 100% ! important;
}
div#mp_bar_ext {
    border: 1px solid #000000;
}
div#mp_bar_int {
    background-color: #000000;
}
.logo {
    float: left;
    content: url("https://www.bunsenlabs.org/img/bunsenlabs-text-flame.svg");
    margin-left: -4px!Important;
    margin-top: -10px!Important;

}
#punindex #brdmain {
    padding-top: 0px;
    border: 0;
}
.pun .blocktable .tcr {
    width: 20%;
}

.icon-new {
    color:#000000 !Important;
}

#brdmenu {
    margin-left: -8px;
}

/* Quick and dirty rip of responsive breakpoints from old crunchbang forums */
/* Media Queries for responsive design -------------------------------------- */
#brdmenu a:link, #brdmenu a:visited {
	border-top-style: solid;
	border-width: 1px;
	display: block;
	padding: 10px 0px 10px 0px;
}

#brdmenu a:hover, #brmenu a:active, #brdmenu a:focus {
	text-decoration: none;
}


#brdmenu a#brdmenubutton {border:0;
	text-indent: -9999px;
	outline:none;
}
#brdwelcome .conr {float:left;}
.tc2,.tc3,.tcr,p.modlist{display: none;}
.pun .blockpost .postright {padding:4px 110px 7px 18px;}
.pun .blockpost .postleft,
.pun .blockpost .postfootleft {width:70px;margin-left:-90px;}
.pun .blockpost .postbody, .pun .blockpost .postfoot {margin-right:-90px;}
.pun .blockpost .postfootright {padding:7px 110px 7px 18px;}
.pun .blockpost .postleft dd {display:none;}
.pun .blockpost .postleft dd.postavatar {display:block;}
.community_info {display:none;}
.datetimeposted {float:right;}
@media only screen and (min-width: 480px) {
  /* Style adjustments for viewports 480px and over go here */
    #masthead, .pun {
        position:relative;
        width:470px;
    }
    .tcr{display: table-cell;}
    #fini p {
        display:block;
        float:left;
        width:70%;
    }
    #fini .credit {
        display:block;
        float:right;
        margin:0 0 0 6px;
    }
}

@media only screen and (min-width: 768px) {
  /* Style adjustments for viewports 768px and over go here */
    #masthead, .pun  {
        position:relative;
        width:760px;
    }

    #sitemenu {
        float:right;
        padding:0;
        margin:0;
        overflow: hidden;
        list-style: none;
    }

    #sitemenu li {
        display:block;
    }
    #sitemenu li {
        float: left;
        padding:0;
        margin:0;
    }

    #sitemenu a:link, #sitemenu a:visited {
        border:0;
        border-right-style: solid;
        border-width: 1px;
        display: block;
        min-width: 60px;
        padding: 12px 16px 7px 8px;
        white-space: nowrap;
        text-decoration: none;
    }

    #sitemenu a.siteabout {
        border-left-style: solid;
    }

    #sitemenu a.sitesearch,
    #sitemenu a.showmenu {
        border-left-style: solid;
        min-width: 24px;
        width:24px;
        height:24px;
        padding: 10px 16px 7px 8px;

    }
    #sitemenu a.sitesearch {
        border:none;
    }

    #sitemenu a.std {
        display: block;
    }
    #sitemenu a.showmenu {
        display: none;
    }
    #mobmenu {
        display: none;
    }

	#brddesc p {
		padding: 0;
	}

	#brdmenu a#brdmenubutton{display:none;}

	#brdmenu ul {
		padding: 0;display:block;
	}

	#brdmenu li {
		float: left;
	}

	#brdmenu a:link, #brdmenu a:visited {
		border-top: 0;
		border-right-style: solid;
		border-width: 1px;
		display: block;
		min-width: 60px;
		padding: 12px 16px 7px 8px;
		white-space: nowrap;
		text-align: left;
	}

	#brdmenu a:hover, #brmenu a:active, #brdmenu a:focus {
		text-decoration: none;
	}

    #brdwelcome .conr {float:right;}
    .tc2,.tc3 {display: table-cell;}
    .pun .blocktable .tcr {
		width: 20%;
	}
    /*p.modlist {display:inline;}*/
	.pun .blockpost .postright {padding:4px 230px 7px 18px;}
	.pun .blockpost .postleft,
	.pun .blockpost .postfootleft {width:194px;margin-left:-218px;}
	.pun .blockpost .postbody, .pun .blockpost .postfoot {margin-right:-218px;}
	.pun .blockpost .postfootright {padding:7px 270px 7px 18px;}
	.pun .blockpost .postleft dd {display:block;}
    .pun .blockpost .postleft dd.postavatar {display:block;}

    .pun .blockpost h2 .conr {
		display:block;
		float: right;
		text-align: right;
	}
	.datetimeposted {float:left;}
	.pun .crumbs li {
		white-space: nowrap;
		font-size: 90%;
	}

    .pun .foot {
    	margin-top:20px;
    	margin-bottom: 0px;
    }
    .pun .foot.additional{
    	margin-top:0px;
    	margin-bottom: 14px;
    	border-top: 1px solid #3B4245;
    }


}

@media only screen and (min-width: 920px) {
  /* Style adjustments for viewports 920px and over go here */
    #masthead, .pun  {
        position:relative;
        width:920px;
    }

}



  
  
  
}
