@-moz-document domain("strawpoll.me") {
body, header, button, footer, #bodyContainer #title, #footer {
	font-family : 'Roboto', 'Open Sans', 'Arial', sans-serif;
    background-color : #000;
    color : white;
}

.notification-container .notification {
    background-color : #222;
	font-family : 'Roboto', 'Open Sans', 'Arial', sans-serif;
}

::-webkit-input-placeholder {
   color : #BBB;
}

section {
    color : white;
    background-image : url(http://i.imgur.com/Otk7kXJ.png);
}

input {
    color : #FFF;
}

span.optionNumber.ng-binding {
    color : white;
}

textarea {
    color : white;
}

.pollBody {
    background: url(http://i.imgur.com/gyqPEYU.png);
}

#bodyContainer #title a, .pollBody .optionNumber {
  color : white;
}

#tos, #privacy-policy {
    background-color : #000;
}

a {
    color : #BBB;
}

