css:
@-moz-document domain("docs.unrealengine.com") {
#webThree,
body#webbgThree {
    background: #3E3E3E!important;
}


#navPanel,
#navPanel ul,
li,
nav.main-menu {
    background-color: #262626!important;
}

#header>nav.main-menu ul li a {
    color: #CECECE;
}

li.logo {
    background: -moz-linear-gradient(left, rgba(176, 176, 176, 0.65) 0%, rgba(38, 38, 38, 1) 100%);
    /* FF3.6+ */
    
    background: -webkit-gradient(linear, left top, right top, color-stop(0%, rgba(176, 176, 176, 0.65)), color-stop(100%, rgba(38, 38, 38, 1)));
}

#navPanel li>a:visited {
    color: #858585;
}

#navPanel li>a.selectedLink {
    background-color: #606060!important;
}
/*General text/link styling */

#navPanel li>a,
li>span.index_nolink,
div.features .inner .content .description,
div.features .inner .title,
.largetitle,
.largetitle p,
.topic .wrapper .largetitle,
.topic .wrapper .largetitle p {
    color: #CECECE;
}
/* Page Content */

#contentContainer,
#pagedefault,
#pagecontainer,
#maincol,
#footer #legal,
#footer #terms,
.content-wrapper,
.top-header .top,
#wrapper .topics-wrap > .topics .info,
.section {
    background: #3E3E3E;
    color: #CECECE;
}

#wrapper .topics-wrap > .topics .info
pre.prettyprint {
    background: #dadada!important;
}

#wrapper .topics-wrap > .topics:hover {
    background: #434242
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: inherit;
}

.navigation>.button a {
    background-color: #262626;
    color: inherit;
}

.navigation .button a:hover {
    background-color: #3E3E3E;
}

#maincol {
    border: none;
    background-color: #3E3E3E;
}

#maincol table td {
    background-color: #3E3E3E;
}

#maincol ol li,
#maincol ul li {
    background-color: #3E3E3E!important;
}

.bottom-row {
    background-color: #3E3E3E
}

a,
a:hover,
a:visited,
a:link,
.crumbs a,
.crumbs a:hover,
.crumbs a:visited,
.crumbs a:link,
#maincol ol li a,
#maincol ul li a,
#maincol table td a {
    color: #009EEF;
}

#maincol .linkcontainer ul li a {
    background-color: #262626;
    border: 1px solid #808386;
}

#maincol .linkcontainer ul li a:hover {
    background-color: #3E3E3E;
}

.searchterm_button {
    background: #262626;
    color: #CECECE;
}

.tip,
.note {
    background-color: #262626;
}

table.table thead, .content-wrapper table thead {
    background: #606060;
}

.content-wrapper table > thead > tr > th, td {
    border-color: #606060!important;
}
/* Search */

.cse .gsc-control-cse,
.gsc-control-cse {
    background: #262626;
}

.gs-result:hover {
    background-color: #3E3E3E!important;
}

div.gsc-orderby-label,
div.gsc-selected-option-container {
    color: #CECECE;
}

/* Left Sidebar */
.content-wrapper #sideNav li.sidebar_active_parent > a > span, .content-wrapper #sideNav li.sidebar_active_item > a > span {
    color: #CECECE!important;
}

.content-wrapper #sideNav li.sidebar_active_item > a {
    color: #CECECE!important;
    background: #606060;
}

@media (min-width: 1200px){
    .content-wrapper #sideNav li > ul > li ul,
    .content-wrapper #sideNav li {
        background: #262626!important;
    }
}
}
