css:
/* i really want this to be global */

.b-page {
    color: #C8C8C8;
    position: relative;
    width: 100%;
    height: auto;
    min-height: 100%;
    margin: 0;
    padding: 0;
    background-color: #363636;
    min-width: 820px;
    font: 1em Arial,Helvetica,sans-serif;
}

.b-link__highlighted {
    color: #FFFFFF;
}

.b-tabs__tab_active_yes {
    font-size: 1.38em;
    border-top: 3px solid #FFFFFF;
}

:link, .b-widget__title :visited, .link:visited, .link__search:visited {
    color: #FFFFFF;
    text-decoration: none;
}

.b-link:visited, .b-link__search:visited {
    color: #7E7E7E;
}
.b-link_black_novisit, .b-link_black_novisit:link, .b-link_black_novisit:visited {
    color: #C8C8C8;
    text-decoration: none;
}

.b-page .b-link_pseudo_yes {
    color: #FFFFFF;
}
.input__samples, .input__samples .b-link {
    color: #E1E1E1;
}

.b-page_noskin .input__samples .b-link_pseudo_yes .b-link__inner {
    color: #FFF;
}

.popup_theme_flat>.popup__content {
    background-color: #494949;
}
.header__action:before {
    position: absolute;
    top: 1px;
    right: 1px;
    bottom: 1px;
    left: 1px;
    content: '';
    border-radius: 2px;
    background: #555;
}

.serp-item {
    color: #FFF;
}

.header__background {
    position: absolute;
    z-index: -1;
    top: 0;
    bottom: 3px;
    width: 100%;
    background: #363636;
}

.header__under:after {

    display: none;
 
}

a.button_theme_pseudo, .button_theme_pseudo {
    font-family: Arial,sans-serif;
    color: #FFF;
}

.header2__main {
    position: relative;
    z-index: 10;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    height: 70px;
    border-bottom: 1px solid rgba(0,0,0,.06);
    background: #363636;
}

.page-content {
    font: 13px/18px Arial,Helvetica,sans-serif;
    color: #E3E0E0;
}
.widget {
    padding: 20px 12px;
    border: 1px solid #595959;
    border-radius: 3px;
    background-color: #595959;
}

.title .link {
    color: #FFF;
}

.story__text a {
    color: #FFF;
}
.link:hover, .link:visited:hover {
    color: #5BC3FF!important;
}

.b-news .b-link:hover {
    color: #5BC3FF!important;
}

a.b-link:hover .b-link__highlighted {
    color: #5BC3FF!important;
}

.m-svg .b-logo__image_bg {
    background: url("http://yastatic.net/www-skins/_/U/e/PSzFMQRthNBAuxbMkFXzcAlyA.svg");
}
