css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fisheye.co.il")
{
  
  /*******/
  /* All */
  /*******/
  
  /* background */ 
  html, #wrapper {
    background: #333030 !important;
  }
  
  /* Top - Fixed color */
  #header {
    background: #013571 !important;
    border-bottom: 5px solid #001F56 !important;
  }
  
  .featured {
    height: initial;
    width: 640px !important;
  }
  
  a, .published, span, .movielist-date {
    color: #EEE !important;
    text-decoration: none !important;
  }
  
  p, .ui-accordion-content > *, .ui-widget-content {
    color: #BBB !important;     
  }
  
  /* Older articles bottom button */
  .floatleft a {
    background: #212121 !important;
    font-size: 12px;
    border-radius: 1px;
    transition-duration: 1.5s;
  }
  .floatleft a:hover {
    background: #003571 !important;
  }
  
  /* Navigation bar */
  #nav {
    background: #111 !important;
  }
  
  /* Navigation bar - hover effect */
  #nav .sf-menu a {
    transition-duration: 0.75s !important;
    background: #111 !important;
  }
  #nav .sf-menu a:hover {
    background: #001F56 !important; 
  }
  
  /* Footer - Removed */
  #footer {
    display: none !important;
  }
  
  /* Transparent background - Removed */
  #main {
    background: none !important;
  }
  
   /* Small buttons */
  .buttonlink, .fisheye-link-but {
    background: #003571 !important;
    border-radius: 1px !important;
    transition-duration: 1s !important;
  }
  .buttonlink:hover, .fisheye-link-but:hover {
    background: #666 !important;
    margin-left: 0px !important;
  }
  
  /* Posts accordion */
  .ui-accordion-header {
    background: #424242 !important;
    color: #EEE !important;
    border-radius: 2px !important;
    transition-duration: 0.75s !important;
    box-shadow: 4px 4px 2px 0px rgba(0,0,0,0.15) !important;
  }
  .ui-accordion-header:hover, .ui-accordion-header-active {
    background: #003571 !important; 
  }
  
  .ui-accordion-content {
    background: #424242 !important;
    border-bottom-left-radius: 4px !important;
    border-bottom-right-radius: 4px !important;  
    box-shadow: 4px 4px 2px 0px rgba(0,0,0,0.15) !important;
  }
  
  /* Accordion search bar */
  #movie_page_search {
    border-radius: 0px;
    border-color: #383332;
    border-width: 2.5px !important;
    background: #EFEFEF;
    color: #212121;
  }
  
  .comment-count {
    color: #111 !important;
  }
  
  /*************/
  /* Main page */
  /*************/

  /* Movies bar */
  #new-at-movie-pages {
    border-radius: 1px !important;
    background: #212121 !important;     
  }
  
  /* Movies bar - Extended */
  #new-at-movie-pages-big {
    border-radius: 1px !important;
    background: #212121 !important;    
  }
  
  /* Posts */
  .entry {
    border-width: 0px 0px 24px 0px !important;
    background: #424242 !important;
    border-radius: 0px !important;
    transition-duration:.75s !important; 
    box-shadow: 4px 4px 2px 0px rgba(0,0,0,0.15) !important;
  }
  
  .entry:hover {
    -webkit-filter: grayscale(55%) !important;
    filter: grayscale(55%) !important;
  }

  .entry > .entry-thumbnails {
    transition-duration: 1s !important;
  } 
  .entry:hover > .entry-thumbnails {
    -webkit-filter: blur(1.4px);
    filter: blur(1.4px);
    z-index: 1;
  }
  
  /* Post - Categorys' colors and for Articles bottom border */  
  .category-reviews, .single-post .category-reviews {
    border-color: #1565c0 !important; /* Blue */
  }  
  .category-news, .single-post .category-news {
    border-color: #2e7d32 !important; /* Green */
  }     
  .category-thoughts, .single-post .category-thoughts {
    border-color: #ff8f00 !important; /* Yellow */
  }  
  .category-video, .single-post .category-video {
    border-color: #4527a0 !important; /* Purple */
  } 
  .category-polls, .single-post .category-polls {
    border-color: #ef6c00 !important; /* Orange */
  }
  
    /****************/
    /* Article page */
    /****************/
  
  /* Move the article up a bit */
  #content > .type-post {
    margin-top: -55px !important;
  }
  
  /* Article background */
  .single-post {
    background: none;
    border-bottom: 24px solid !important;
  }
  
  /* Polls */
  .wp-polls li {
    color: #BBB !important;
  }
  
  /* Polls - Results */
  .wp-polls-ul li {
    background: #212121 !important;
  }
  .pollbar {
    background: #013571 !important;  
  }
  
  /* Article tags */
  .tags a:link {
    background: #666 !important;
    border-radius: 2px;
    padding-right: 4px !important;
    transition-duration: 0.25s;
  }
  .tags a:link:hover {
    background: #212121 !important;
  }
  
  /* Comments */
  #commentlist, .module-title {
    background: none;
  }
  .comment-node, #commentlist .new_comment {
    background: #212121 !important;
    color: #EEE;
  }
  #commentlist .new_comment {
    border-left: 2px solid #919117 !important;
  }
  #commentlist .spoilered {
    border: none !important;
    border-left: 2px solid #710117 !important;
  }
  
  .comment h2, .module-title, .fn {
    color: #EEE;
  }
  
  /* Movie summery and movie page */
  .movie-factbox, .movie-postbox {
    border-radius: 2px;
    background: #212121;
    box-shadow: none;
  }
  
  .movie-factbox-bold {
    color: #eee;
  }
  
  /* Line on the right of the comment */
  li .comment {
    border-right: 12px solid #666 !important;
  }
  
  /* Comments anchor and replat buttom */
  .comment-reply-link {
    border-radius: 2px;
  }
  .comment-controls {
    border-bottom-left-radius: 2px;
    border-bottom-right-radius: 2px;
  }
  .comment-controls, .comment-reply-link, .reply-to-button {
    background: #003571 !important;
    transition-duration: 1s;
  }
  .comment-controls:hover, .comment-reply-link:hover {
    background: #666 !important;
  }
  
  /*  fishlikes and givers ? buttom */
  .fishlike-givers, .fishliked {
    background-color: #111 !important;
    color: #EEE;
  }
  
  /* Spoilers */
  .spoiler-control {
    background: #710117 !important;
  }
  .inline_spoiler {
    background: #EEE;
  }
  
  /* Submit comments */
  .other-comments, #reply-title {
    background: #212121 !important;
    color: #AAA;
  }
  .other-comments a {
    color: #EEE !important;
  }
  
  /* Advence Styling button */
  #comment_adv_toolbar, #submit {
    background: #003571 !important;
    border-radius: 2px !important;
    color: #EEE !important;
    transition-duration: 1s !important;
    box-shadow: none;
  }
  #comment_adv_toolbar:hover, #submit:hover {
    background: #212121 !important;
  }
  
  #commentform {
    background: #666;
  }
  
     /*******************/
     /* Movies schedule */
     /*******************/
  
  .losofi-info, .losofi-yeartabs, #losofi-selectors, .losofi-usa-all, .losofi-israel-all {
    background: none;
    color: #BBB;
  }
  
  .losofi-usa-all, .losofi-israel-all {
    padding-top: 0px !important;
    padding-bottom: 15px;
    border-bottom: 1px solid #666 !important;
  }
  
  .year_tab {
    padding-bottom: 9px !important;
  }
  
  .selected {
    color: #EEE !important;
  }
  
  .losofi-months {
    background: #005588;
  }
  
  .losofi-month-yes {
    background: #666;
  }
  .losofi-month-no {
   background: #212121; 
  }
  
  .losofi-month {
    text-align: center;
    background: #212121;
  }
  
  .losofi-usa-all:before {
    content: 'תאריך הפצה בארה"ב';
  }
  
  .losofi-israel-all:before {
    content: 'תאריך הפצה בישראל';
  }
  
  .losofi-record {
    transition-duration: .25s !important;
  }
  
  .losofi-record:hover {
    background: #666 !important;  
  }
  
     /**************/
     /* Movie page */
     /**************/
  
  .movie-minireview {
    background: none;
    color: #EEE;
    margin: 0px 50px 0px 0px;
    padding: 8px 0px 8px 0px;
    text-align: center;
    font-weight: bold;
    background: #666;
    border-radius: 2px;
  }
  .movie-minireview:before {
    content: 'ביקורת הדג: ';
  }
  
  .movie-review-ref {
    background: none;
  }
  
  .movie-minireviewbottom {
    display: none;
  }
  
  
     /**********/
     /* Others */
     /**********/
  
  /* Hide ALL borders */
  * {
    border-width: 0px !important;
  }
  
  /* All hyperlink are bold */
  a:link {
    color: #EEE !important;
    font-weight: bold !important;
  }
  
  /* Every paragraph text is in bright gray */
  p {
    color: #BBB !important;
  }
}




@-moz-document url(http://www.fisheye.co.il/)
{
    /**************************/
    /* Thumbnail in main page */
    /**************************/
  
  .category-reviews:before, .category-news:before, .category-thoughts:before, .category-video:before, .category-polls:before {
    letter-spacing: -2px;
    font-size: 36px;
    position: absolute;
    width: 200px;
    text-align: center;
    z-index: 2 !important;
    opacity: 0;
    transform: translate3d(0, -25%, 0);
    transition-duration: .6s;
  }
  .category-reviews:hover:before, .category-news:hover:before, .category-thoughts:hover:before, .category-video:hover:before, .category-polls:hover:before {
    transform: none;
    opacity: 1;
  }
  
  .category-reviews:before {
    content: "ביקורת";
    color: #2196f3 !important; /* Blue */
  } 
  .category-news:before {
    content: "חדשות";
    color: #4caf50 !important; /* Green */
  }   
  .category-thoughts:before {
    content: "הגיגים";
    color: #ffeb3b !important; /* Yellow */
  }   
  .category-video:before {
    content: "וידאו";
    color: #673ab7 !important; /* Purple */
  }   
  .category-polls:before {
    content: "סקר";
    color: #ff5722 !important; /* Orange */
  } 
}
