css:
@-moz-document domain("putlocker.is") {

/******************************
Header 
*******************************/

td.mainlogo {
	border: none !important;
	background-color: #22252A;
}
body table tbody tr td input {
	color: black !important;
	/*background-color: #34495e;*/
}
.form_info {
	color: #4CB59D !important;
}
tr td input {
	border-radius: 4px !important;
}
element.style {
	background-color: #4CB59D;
	border-color: #4CB59D;
	color: white !important;
}
tr td {
	max-width: 100%;
}
ul {
	background: none !important;
}
#year_to {
	border-radius: 4px !important;
}
#year_from {
	border-radius: 4px !important;
}

/******************************
Body 
*******************************/

body {
	background-color: #22252A !important;
	background-image: none;
}
body table tbody tr td div h2 {
	display: none;
}
tr td div.menu-box {
	display: none;
}

/******************************
Search Card 
*******************************/

table tbody tr td center div.content-box h1 strong font {
	color: #4CB59D !important;
	background-color: #22252A;
	text-transform: none;
}
h1 {
	float: left;
	color: #4CB59D !important;
	background-color: #22252A !important;
	border: none;
}
div {
	border-color: #22252A !important;
}
div form table tbody {
	background-color: #22252A !important;
	border-color: #22252A !important;
}
div form table tbody tr {
	background-color: #22252A !important;
	border-color: #22252A !important;
}
div form table tbody tr td.form_info {
	background-color: #22252A !important;
	border-color: #22252A !important;
}
table {
	color: #4CB59D;
	border-color: #22252A !important;
}
.table .table2 {
	color: #4CB59D;
	border: 1px solid rgb(34, 37, 42) !important;
}
.content-box {
	background-color: #22252A;
	border: 1px solid rgb(34, 37, 42) !important;
}
input.submit_form_button {
	border-radius: 4px;
	background-color: #4CB59D;
	border-color: #4CB59D;
	color: white !important;
}

/******************************
Search Boxes 
*******************************/

.submit_form_textfield {
	border-radius: 4px;
}

/******************************
Footer 
*******************************/

.footer-box {
	background-color: #22252A;
	color: white;
	border: none;
	font-size: 0.6em;
}
table.footer {
	display: none;
}
/******************************
Search Page
*******************************/
div {
	color:#4CB59D !important;
	text-decoration: none !important;
}

div a b {
	color:#4CB59D !important;
	text-decoration: none !important;
}
}
