    -webkit-transition: 200ms all ease-in-out;
    transition: 200ms all ease-in-out;
    opacity:0.6 !important;
}

.conversations_item:hover {
    padding: 0px 5px 3px 8px;
    border: 0px solid transparent;
    border-radius: 50px;
    -webkit-transition: 200ms all ease-in-out;
    transition: 200ms all ease-in-out;
    
    opacity:1 !important;
}

.conversations_item.new-message {
    padding: 0px 5px 3px 8px;
    border: 0px solid;
    border-radius: 50px;
    -webkit-transition: 200ms all ease-in-out;
    transition: 200ms all ease-in-out;
    
    opacity:1 !important;
}

.conversations_item.new-message:hover {
    padding: 0px 5px 3px 8px;
    border: 0px solid;
    border-radius: 50px;
    -webkit-transition: 200ms all ease-in-out;
    transition: 200ms all ease-in-out;

    
    opacity:0.9 !important;
}

